INSERT INTO `cookbook_introduce` VALUES ('2000', null, '', '[桂花莲子羹]是江苏省传统的特色名点，属于苏菜系。作早餐或作点心食用，可助眠益寿。古人认为经常服食，百病可祛，因它“享清芳之气，得稼穑之味，乃脾之果也”。中医认为，莲子有益心补肾、健脾止泻、固精安神的作用。桂花性温，味甘、辛，有化痰、散瘀之功用。\n\n', '', '0', '0', null, '2001', '16', '8', '14761388444');
