INSERT INTO `cookbook_marche` VALUES ('10', '1', 'https://s1.st.meishij.net/rs/126/153/5413376/n5413376_148820246085333.jpg', '先把豆芽焯熟一下，备用。', '4');
INSERT INTO `cookbook_marche` VALUES ('11', '2', 'https://s1.st.meishij.net/rs/126/153/5413376/n5413376_148820246358337.jpg', '花椒干锅炒一下，在磨成粉，用热油炝一下，就成了花椒油，备用。', '4');
INSERT INTO `cookbook_marche` VALUES ('12', '3', 'https://s1.st.meishij.net/rs/126/153/5413376/n5413376_148820246651699.jpg', '猪肉剁成肉馅，备用。', '4');
INSERT INTO `cookbook_marche` VALUES ('13', '4', 'https://s1.st.meishij.net/rs/126/153/5413376/n5413376_148820246834465.jpg', '大蒜子用杵臼捯成泥，备用。', '4');
INSERT INTO `cookbook_marche` VALUES ('14', '5', 'https://s1.st.meishij.net/rs/126/153/5413376/n5413376_148820247271908.jpg', '小米椒切段，小葱剁碎，备用。', '4');
INSERT INTO `cookbook_marche` VALUES ('15', '6', 'https://s1.st.meishij.net/rs/126/153/5413376/n5413376_148820247441283.jpg', '用辣椒末、辣椒粉、芝麻炝个油泼辣子，备用。', '4');
INSERT INTO `cookbook_marche` VALUES ('16', '7', 'https://s1.st.meishij.net/rs/126/153/5413376/n5413376_148820247761841.jpg', '锅里下油，烧热。', '4');
INSERT INTO `cookbook_marche` VALUES ('17', '8', 'https://s1.st.meishij.net/rs/126/153/5413376/n5413376_148820247998144.jpg', '把辣椒段、葱白部分下锅炒香。', '4');
INSERT INTO `cookbook_marche` VALUES ('34', '1', 'https://s1.st.meishij.net/rs/69/126/5719069/n5719069_146200979743900.jpg', '猪肉洗净切二指宽10米左右长条后再用凉水泡5分钟捞起滤干备用，猪肉可选五花或者后臀尖，没有具体要求，肥肉根据自己喜好选择。泡水目的是去除血水及肉质软嫩。', '9');
INSERT INTO `cookbook_marche` VALUES ('35', '2', 'https://s1.st.meishij.net/rs/69/126/5719069/n5719069_146200980188770.jpg', '辅料：淀粉、鸡蛋、盐、糖、鸡精、味精、花椒、姜末、酱油、五香粉，关于用量，根据个人可谓适量掌握，酥肉以椒麻鲜香为主，整体口感须外焦里嫩，从而突出它的酥，遇水而滑，所以叫酥肉滑肉。', '9');
INSERT INTO `cookbook_marche` VALUES ('36', '3', 'https://s1.st.meishij.net/rs/69/126/5719069/n5719069_146200980528235.jpg', '将备好的肉条入盆后逐次加入以上调料，再加入250克左右红薯淀粉，这里需要说明的是，红薯淀粉更加爽滑，调制过程中不用加水稀释，只用鸡蛋，3斤肉约5枚鸡蛋够了，淀粉和鸡蛋的比例自己掌握，整体以干糊状，让更多的淀粉糊裹在肉条上为目的。调配好后腌制20分钟以使肉入味。', '9');
INSERT INTO `cookbook_marche` VALUES ('37', '4', 'http://pgsnqecfs.bkt.clouddn.com/357fbc91-f70c-4e29-87c9-aa58fc06a079.jpg', '修改步骤', '9');
INSERT INTO `cookbook_marche` VALUES ('38', '1', 'https://s1.st.meishij.net/rs/173/81/3832923/n3832923_146196918358361.jpg', '用料：鸡蛋1个，水70克，酵母1克，面粉70克，盐少许，红糖7克，白糖10克，红豆沙适量', '10');
INSERT INTO `cookbook_marche` VALUES ('39', '2', 'https://s1.st.meishij.net/rs/173/81/3832923/n3832923_146196918578824.jpg', '将酵母用水溶化，', '10');
INSERT INTO `cookbook_marche` VALUES ('40', '3', 'https://s1.st.meishij.net/rs/173/81/3832923/n3832923_146196918680266.jpg', '打入鸡蛋，搅拌均匀。', '10');
INSERT INTO `cookbook_marche` VALUES ('41', '4', 'https://s1.st.meishij.net/rs/173/81/3832923/n3832923_146196918734787.jpg', '倒入，糖，盐，粉。', '10');
INSERT INTO `cookbook_marche` VALUES ('42', '5', 'https://s1.st.meishij.net/rs/173/81/3832923/n3832923_146196919087183.jpg', '搅拌成均匀面糊。盖上保鲜膜静置1-2小时。', '10');
INSERT INTO `cookbook_marche` VALUES ('43', '6', 'https://s1.st.meishij.net/rs/173/81/3832923/n3832923_146196919190992.jpg', '至面糊出现小气泡。', '10');
INSERT INTO `cookbook_marche` VALUES ('48', '1', 'http://pgsnqecfs.bkt.clouddn.com/f9983da1-6302-4a55-b68a-9899aa1fd91c.jpg', '食材洗净备用', '12');
INSERT INTO `cookbook_marche` VALUES ('49', '2', 'https://s1.st.meishij.net/rs/167/194/5111167/n5111167_146129909394469.jpg', '黄瓜切片备用，', '12');
INSERT INTO `cookbook_marche` VALUES ('50', '3', 'https://s1.st.meishij.net/rs/167/194/5111167/n5111167_146129909713483.jpg', '烧开水，把蔬菜焯水备用。（菇菇要煮熟哈！）', '12');
INSERT INTO `cookbook_marche` VALUES ('51', '4', 'https://s1.st.meishij.net/rs/167/194/5111167/n5111167_146129910121475.jpg', '摆盘备用。', '12');
INSERT INTO `cookbook_marche` VALUES ('52', '5', 'https://s1.st.meishij.net/rs/167/194/5111167/n5111167_146129910435549.jpg', '如图所示，切好备用，', '12');
INSERT INTO `cookbook_marche` VALUES ('53', '6', 'https://s1.st.meishij.net/rs/167/194/5111167/n5111167_146129910705676.jpg', '鱼，放入一个鸡蛋清，淀粉，盐，料酒，抓匀。备用。', '12');
INSERT INTO `cookbook_marche` VALUES ('54', '7', 'https://s1.st.meishij.net/rs/167/194/5111167/n5111167_146129911091202.jpg', '放适量油，加入姜丝，蒜末，干辣椒段，花椒粒，郫县豆瓣酱。炒香。（注意留一小部分：小米辣，蒜末最后撒面上。）', '12');
INSERT INTO `cookbook_marche` VALUES ('55', '8', 'https://s1.st.meishij.net/rs/167/194/5111167/n5111167_146129911306262.jpg', '加适量鸡精，盐，自制红油。', '12');
INSERT INTO `cookbook_marche` VALUES ('56', '1', 'http://pgsnqecfs.bkt.clouddn.com/undefined', '?¡ã?¨¨£¤???¡ã¨¨?¡À?¡ä??????¡ã????¡ã???¦Ì', '13');
INSERT INTO `cookbook_marche` VALUES ('57', '2', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146105446488071.jpg', '鸡丁中加入盐，料酒，酱油，淀粉腌制10分钟左右。', '13');
INSERT INTO `cookbook_marche` VALUES ('58', '3', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146105447432401.jpg', '大葱切丁', '13');
INSERT INTO `cookbook_marche` VALUES ('59', '4', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146105448388400.jpg', '姜，蒜，胡萝卜切丁，辣椒切段去籽。', '13');
INSERT INTO `cookbook_marche` VALUES ('60', '5', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146105449336327.jpg', '糖，醋，盐，酱油，清水，淀粉少许混合均匀成味汁', '13');
INSERT INTO `cookbook_marche` VALUES ('61', '6', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146105456484814.jpg', '准备好油炸花生米', '13');
INSERT INTO `cookbook_marche` VALUES ('62', '1', 'https://s1.st.meishij.net/rs/115/13/2253365/n2253365_146104590713658.jpg', '豌豆尖掐去比较老的部分，洗净备用；', '14');
INSERT INTO `cookbook_marche` VALUES ('63', '2', 'https://s1.st.meishij.net/rs/115/13/2253365/n2253365_146104592466197.jpg', '达州肉圆子解冻后，切片。达州肉圆子的具体做法在这：http://www.meishij.net/zuofa/luobowanzi_9.html；', '14');
INSERT INTO `cookbook_marche` VALUES ('64', '3', 'https://s1.st.meishij.net/rs/115/13/2253365/n2253365_146104594134581.jpg', '老姜洗净后，拍扁；\r\n\r\n', '14');
INSERT INTO `cookbook_marche` VALUES ('65', '4', 'https://s1.st.meishij.net/rs/115/13/2253365/n2253365_146104598975160.jpg', '煮锅里倒入适量清水，老姜煮开；', '14');
INSERT INTO `cookbook_marche` VALUES ('66', '1', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146096671919221.jpg', '牛肉切成薄的小片。', '15');
INSERT INTO `cookbook_marche` VALUES ('67', '2', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146096672895658.jpg', '在肉片中加入少许盐，料酒，糖，酱油和烤肉料，按摩均匀。烤肉料中本来就有盐和胡椒粉之类的，所以其他调料可以少加一些，我怕烤肉料多了辣，所以，烤肉料加得不多。', '15');
INSERT INTO `cookbook_marche` VALUES ('68', '3', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146096673972603.jpg', '加入淀粉，继续搅拌均匀，然后盖上盖子，冰箱冷藏腌制30分钟。', '15');
INSERT INTO `cookbook_marche` VALUES ('69', '4', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146096674953457.jpg', '牙签在水里浸泡一下。', '15');
INSERT INTO `cookbook_marche` VALUES ('70', '5', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146096676105626.jpg', '辣椒剪断去籽，姜，蒜，葱切末。', '15');
INSERT INTO `cookbook_marche` VALUES ('71', '1', 'https://s1.st.meishij.net/rs/193/12/2378193/n2378193_146079453270408.jpg', '小土豆洗乾淨', '16');
INSERT INTO `cookbook_marche` VALUES ('72', '2', 'https://s1.st.meishij.net/rs/193/12/2378193/n2378193_146079455710759.jpg', '鍋中加清水，放入土豆煮至7成熟，撈出去皮', '16');
INSERT INTO `cookbook_marche` VALUES ('73', '3', 'https://s1.st.meishij.net/rs/193/12/2378193/n2378193_146079458265911.jpg', '煎鍋中放適量菜籽油，放土豆小火煎', '16');
INSERT INTO `cookbook_marche` VALUES ('74', '1', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145879792311140.jpg', '1.	首先我们回顾一下材料，然后将五花肉原块放入冷藏，放入冷藏，放入冷藏，约1小时至2小时，视乎功率而定。直至五花肉形状已经冻硬，但依然可以切得动的状态。', '17');
INSERT INTO `cookbook_marche` VALUES ('75', '2', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145879797031563.jpg', '2.	经过冷藏，五花肉就会变得无比的好切，如果期间发现肉慢慢软化了，那就要重新放入冷藏。', '17');
INSERT INTO `cookbook_marche` VALUES ('76', '3', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145879806328024.jpg', '锅内放可以漫过五花肉的水，然后加入姜和八角。', '17');
INSERT INTO `cookbook_marche` VALUES ('77', '4', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145879813778670.jpg', '4.	待烧至温水（约50度）放入五花肉片，焯煮约7分钟。如果五花肉切得够薄，触碰温水的那刻就会略微卷起。', '17');
INSERT INTO `cookbook_marche` VALUES ('78', '5', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145879824626084.jpg', '5.	将肉片捞出，用厨房纸／布将肉片表面的水分尽量拭干！（如果不是后果很严重）。', '17');
INSERT INTO `cookbook_marche` VALUES ('79', '6', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145879832427757.jpg', '此时，让我们来欣赏一下灯影的效果吧 XD', '17');
INSERT INTO `cookbook_marche` VALUES ('80', '7', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145879840483104.jpg', '7.	用中火，将锅烧到5成热，然后放入2滴油（作为引子而已），然后将五花肉片平铺在锅内，煸出油分。', '17');
INSERT INTO `cookbook_marche` VALUES ('81', '1', 'https://s1.st.meishij.net/rs/115/13/2253365/n2253365_145750501803228.jpg', '烹饪的前一天晚上，将原料中的所有材料依次放入容器里；', '18');
INSERT INTO `cookbook_marche` VALUES ('82', '2', 'https://s1.st.meishij.net/rs/115/13/2253365/n2253365_145750504342467.jpg', '用打蛋器将面糊搅拌均匀；建议将红糖碾成粉末再放进去，结块的红糖不利于搅拌均匀；', '18');
INSERT INTO `cookbook_marche` VALUES ('83', '3', 'https://s1.st.meishij.net/rs/115/13/2253365/n2253365_145750506464086.jpg', '将容器覆盖上保鲜膜，放入冰箱冷藏一晚；放入冰箱是为了低温发酵，如果没时间等太久，那就在温度20度以上的空间里，发酵1-2个小时；\r\n', '18');
INSERT INTO `cookbook_marche` VALUES ('84', '4', 'https://s1.st.meishij.net/rs/115/13/2253365/n2253365_145750595437825.jpg', '取出，再次搅拌均匀；', '18');
INSERT INTO `cookbook_marche` VALUES ('85', '5', 'https://s1.st.meishij.net/rs/115/13/2253365/n2253365_145750626017699.jpg', '电饼铛开启下开关，不刷油，接通电源预热；', '18');
INSERT INTO `cookbook_marche` VALUES ('95', '1', 'https://s1.st.meishij.net/rs/198/115/5091448/n5091448_145715204024393.jpg', '二刀肉（坐墩肉）加姜片、料酒煮熟切片，蒜苗切段备用。', '21');
INSERT INTO `cookbook_marche` VALUES ('96', '2', 'https://s1.st.meishij.net/rs/198/115/5091448/n5091448_145715205467663.jpg', '猪肉下锅小火煸至出油变形起碗，切记不要煸的太干。', '21');
INSERT INTO `cookbook_marche` VALUES ('97', '3', 'https://s1.st.meishij.net/rs/198/115/5091448/n5091448_145715206197688.jpg', '加入姜丝快速翻炒。', '21');
INSERT INTO `cookbook_marche` VALUES ('98', '1', 'https://s1.st.meishij.net/rs/211/64/1641211/n1641211_145671385761159.jpg', '锅里放入调料；', '22');
INSERT INTO `cookbook_marche` VALUES ('99', '2', 'https://s1.st.meishij.net/rs/211/64/1641211/n1641211_145671386320146.jpg', '加入切块的豆腐，加酱油和水；', '22');
INSERT INTO `cookbook_marche` VALUES ('100', '1', 'https://s1.st.meishij.net/rs/41/212/5178041/n5178041_145655312536719.jpg', '豆芽洗净去皮去根，其它材料洗好切好装盘（豆腐、肉和油菜是做另一道菜用的，一起拍下来了，大家可以忽略不计）', '23');
INSERT INTO `cookbook_marche` VALUES ('101', '2', 'https://s1.st.meishij.net/rs/41/212/5178041/n5178041_145655312896061.jpg', '锅中加水烧开后将干海带卷放入煮至叶片舒展开捞出备用', '23');
INSERT INTO `cookbook_marche` VALUES ('102', '3', 'https://s1.st.meishij.net/rs/41/212/5178041/n5178041_145655313197080.jpg', '将豆芽倒入锅中开水中煮熟', '23');
INSERT INTO `cookbook_marche` VALUES ('103', '4', 'https://s1.st.meishij.net/rs/41/212/5178041/n5178041_145655313375855.jpg', '将熟后的豆芽捞出放入一个大盆中作为菜的铺底', '23');
INSERT INTO `cookbook_marche` VALUES ('104', '5', 'https://s1.st.meishij.net/rs/41/212/5178041/n5178041_145655313707370.jpg', '将金针菇放入热水中汆烫一分钟后捞出，倒在豆芽盆中', '23');
INSERT INTO `cookbook_marche` VALUES ('105', '6', 'https://s1.st.meishij.net/rs/41/212/5178041/n5178041_145655314093882.jpg', '锅中加2勺油烧热，将葱姜蒜末爆香后加入辣酱和甜面酱翻炒几下', '23');
INSERT INTO `cookbook_marche` VALUES ('106', '1', 'https://s1.st.meishij.net/rs/211/122/2280711/n2280711_145636746820396.jpg', '备好原材料：带皮五花肉，盐，老姜，陈皮，料酒，花椒，五香粉。', '24');
INSERT INTO `cookbook_marche` VALUES ('107', '2', 'https://s1.st.meishij.net/rs/211/122/2280711/n2280711_145636748437655.jpg', '老姜洗净，剁碎。陈皮洗净，切碎。\r\n\r\n', '24');
INSERT INTO `cookbook_marche` VALUES ('108', '3', 'https://s1.st.meishij.net/rs/211/122/2280711/n2280711_145636749873526.jpg', '猪肉同陈皮混合均匀，放入调入适量料酒。', '24');
INSERT INTO `cookbook_marche` VALUES ('109', '4', 'https://s1.st.meishij.net/rs/211/122/2280711/n2280711_145636751002519.jpg', '继续调入适量盐，花椒，五香粉，姜末，混合均匀后，放置两天。期间给它翻次面。', '24');
INSERT INTO `cookbook_marche` VALUES ('110', '5', 'https://s1.st.meishij.net/rs/211/122/2280711/n2280711_145636752679401.jpg', '腌制两天后，将腊肉悬挂，晾干盐水。', '24');
INSERT INTO `cookbook_marche` VALUES ('111', '1', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145624672624944.jpg', '原材料全家福（干辣椒可以不加）', '25');
INSERT INTO `cookbook_marche` VALUES ('112', '2', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145624672900765.jpg', '五花肉切方块，放入沸水中加料酒焯5分钟，捞出洗净后修整好形状', '25');
INSERT INTO `cookbook_marche` VALUES ('113', '1', 'https://s1.st.meishij.net/rs/117/208/5552117/n5552117_145629903293077.jpg', '豆腐切块在开水里焯烫，目的是排除豆腐里多余的水分。', '26');
INSERT INTO `cookbook_marche` VALUES ('114', '2', 'https://s1.st.meishij.net/rs/117/208/5552117/n5552117_145629907484598.jpg', '锅里热油，下姜蒜末爆香。', '26');
INSERT INTO `cookbook_marche` VALUES ('115', '3', 'https://s1.st.meishij.net/rs/117/208/5552117/n5552117_145629908640343.jpg', '加入豆瓣酱爆香。', '26');
INSERT INTO `cookbook_marche` VALUES ('116', '4', 'https://s1.st.meishij.net/rs/117/208/5552117/n5552117_145629910110107.jpg', '加入肉末翻炒均匀。', '26');
INSERT INTO `cookbook_marche` VALUES ('117', '1', 'https://s1.st.meishij.net/rs/211/122/2280711/n2280711_145629267439565.jpg', '备好原材料：五花肉，四川冬尖，老姜。', '27');
INSERT INTO `cookbook_marche` VALUES ('118', '2', 'https://s1.st.meishij.net/rs/211/122/2280711/n2280711_145629269070035.jpg', '五花肉洗净后，切成适当长短的肉块，放入冷水锅里煮熟。', '27');
INSERT INTO `cookbook_marche` VALUES ('119', '3', 'https://s1.st.meishij.net/rs/211/122/2280711/n2280711_145629270538275.jpg', '冬尖洗净挤干水分切碎，老姜洗净切碎。\r\n', '27');
INSERT INTO `cookbook_marche` VALUES ('120', '4', 'https://s1.st.meishij.net/rs/211/122/2280711/n2280711_145629271963329.jpg', '煮熟的五花肉捞出，备好酱油，酒酿。', '27');
INSERT INTO `cookbook_marche` VALUES ('121', '5', 'https://s1.st.meishij.net/rs/211/122/2280711/n2280711_145629273594568.jpg', '五花肉先用酒酿抹匀后晾干，再用酱油抹匀，再次晾干。', '27');
INSERT INTO `cookbook_marche` VALUES ('122', '6', 'https://s1.st.meishij.net/rs/211/122/2280711/n2280711_145629275383901.jpg', '炒锅里倒入适量植物油烧热，放入五花肉炸至焦黄，五花肉皮朝下。', '27');
INSERT INTO `cookbook_marche` VALUES ('126', '1', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145572483889961.jpg', '材料全家福', '29');
INSERT INTO `cookbook_marche` VALUES ('127', '2', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145572484521885.jpg', '肉鸡爪洗净入锅，加清水淹没鸡爪，加白醋、麦芽糖、米酒烧开，撇去浮沫，煮至断生捞出\r\n', '29');
INSERT INTO `cookbook_marche` VALUES ('128', '3', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145572485184026.jpg', '将鸡爪斩去爪尖，晾干水汽，热锅放油，油温7成热时放入鸡爪炸至金黄', '29');
INSERT INTO `cookbook_marche` VALUES ('129', '4', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145572486011485.jpg', '将炸好的鸡爪捞至热汤中浸泡，加盖焖20分钟，至鸡爪起皱皮时捞出', '29');
INSERT INTO `cookbook_marche` VALUES ('130', '5', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145572487369476.jpg', '生菜洗净沥干水分，将菜叶铺于圆盘四周，围成花边', '29');
INSERT INTO `cookbook_marche` VALUES ('131', '1', 'https://s1.st.meishij.net/rs/247/67/5579497/n5579497_145570576014580.jpg', '将芋儿剥皮，洗净，切成四块。', '30');
INSERT INTO `cookbook_marche` VALUES ('132', '2', 'https://s1.st.meishij.net/rs/247/67/5579497/n5579497_145570585361395.jpg', '撒上少许盐。', '30');
INSERT INTO `cookbook_marche` VALUES ('137', '1', 'https://s1.st.meishij.net/rs/179/132/4283179/n4283179_145192260207029.jpg', '猪里脊肉切丝，用少许盐、胡椒粉、料酒、生抽、水淀粉、花椒粉、鸡蛋清抓匀，静置。泡姜、泡椒、大蒜切碎，一大勺豆瓣酱放一起，香葱切末，小米辣切段备用。', '32');
INSERT INTO `cookbook_marche` VALUES ('138', '2', 'https://s1.st.meishij.net/rs/179/132/4283179/n4283179_145192259829009.jpg', '黑木耳泡发切丝、胡萝卜、青椒、冬笋切丝。', '32');
INSERT INTO `cookbook_marche` VALUES ('139', '3', 'https://s1.st.meishij.net/rs/179/132/4283179/n4283179_145192259671465.jpg', '糖、醋、料酒、水、淀粉（淀粉不要多了，少许即可，否则菜会粘稠不清爽）搅匀备用。', '32');
INSERT INTO `cookbook_marche` VALUES ('140', '4', 'https://s1.st.meishij.net/rs/179/132/4283179/n4283179_145192259349475.jpg', '中小火，油温六成热，下肉丝，滑炒，打散，防止粘连。', '32');
INSERT INTO `cookbook_marche` VALUES ('141', '5', 'https://s1.st.meishij.net/rs/179/132/4283179/n4283179_145192296403896.jpg', '肉丝全部变色后，扒拉到两边，中间下豆瓣酱、泡姜、泡椒、大蒜碎煸炒出香味，出红油，与肉丝炒匀。', '32');
INSERT INTO `cookbook_marche` VALUES ('142', '1', 'https://s1.st.meishij.net/rs/152/104/3713652/n3713652_145144853255262.jpg', '蘸汁调料：芝竹签先用开水烫煮10分钟、麻酱加点盐，清水，香油调开，辣椒酱，香菜，甜辣酱', '33');
INSERT INTO `cookbook_marche` VALUES ('143', '2', 'https://s1.st.meishij.net/rs/152/104/3713652/n3713652_145144853473968.jpg', '所有食材改刀用竹签串好，锅中倒入清水加花椒，大料，葱姜煮开，炖煮15分钟。', '33');
INSERT INTO `cookbook_marche` VALUES ('144', '3', 'https://s1.st.meishij.net/rs/152/104/3713652/n3713652_145144871988514.jpg', '然后把作料捞出，加入浓汤宝两块煮开。', '33');
INSERT INTO `cookbook_marche` VALUES ('145', '1', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145123425004884.jpg', '尽量选用新鲜兔肉，冷冻兔肉味道还是要差一些的', '34');
INSERT INTO `cookbook_marche` VALUES ('146', '2', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145123425421076.jpg', '最显眼的是红彤彤的干辣椒，干辣椒要剪成小段，辣椒籽尽量去除不要', '34');
INSERT INTO `cookbook_marche` VALUES ('147', '3', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145123425649162.jpg', '清水中加一半的姜片、大葱、花椒、八角、料酒，煮沸后放入整只兔肉汆水', '34');
INSERT INTO `cookbook_marche` VALUES ('148', '4', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145123426048231.jpg', '汆水后的兔肉去骨、切成1.5cm见方的丁', '34');
INSERT INTO `cookbook_marche` VALUES ('149', '5', 'https://s1.st.meishij.net/rs/86/218/4367086/n4367086_145123426359381.jpg', '锅中倒油，加入姜片、大葱、花椒、八角小火炸出香味后将香料捞出不要', '34');
INSERT INTO `cookbook_marche` VALUES ('150', '1', 'https://s1.st.meishij.net/rs/131/18/5254631/n5254631_145033171811197.jpg', '宽干粉条用水泡上一个小时。蒜压碎，葱切小段，大头菜切小颗粒，香菜切小段；', '35');
INSERT INTO `cookbook_marche` VALUES ('151', '1.5', 'https://s1.st.meishij.net/rs/131/18/5254631/n5254631_145033172374863.jpg', '调料碗里放上火锅底料，用微波炉打热一分钟，或者隔水蒸热，让其化开；', '35');
INSERT INTO `cookbook_marche` VALUES ('152', '1', 'https://s1.st.meishij.net/rs/173/41/5135423/n5135423_145032425211355.jpg', '准备材料。红薯粉泡软；\r\n', '36');
INSERT INTO `cookbook_marche` VALUES ('153', '1.5', 'https://s1.st.meishij.net/rs/173/41/5135423/n5135423_145032425555051.jpg', '红薯粉下锅煮熟，捞起过凉水，大头菜切碎粒；\r\n', '36');
INSERT INTO `cookbook_marche` VALUES ('154', '2.5', 'https://s1.st.meishij.net/rs/173/41/5135423/n5135423_145032426347250.jpg', '配调料：油辣椒、花椒末、味精、酱油、醋；\r\n', '36');
