INSERT INTO `cookbook_marche` VALUES ('210', '3', 'https://s1.st.meishij.net/rs/41/143/2723291/n2723291_143839065895036.jpg', '猪肉切丝，放入少许淀粉，干辣椒段、花椒粒', '45');
