INSERT INTO `cookbook_marche` VALUES ('221', '1', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143745802649548.jpg', '鱼片用淀粉和少许盐拌匀，油锅七成热，将鱼片逐个放入油锅中，小火炸至金黄色捞起备用', '49');
INSERT INTO `cookbook_marche` VALUES ('222', '2', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143745803013449.jpg', '油锅7成热，下豆瓣酱，快速翻炒出香味后，下姜蒜粒、料酒，作料炒香后，下炸好的鱼片', '49');
INSERT INTO `cookbook_marche` VALUES ('223', '3', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143745803245495.jpg', '翻炒2-3分钟，下葱段，继续炒', '49');
