INSERT INTO `cookbook_marche` VALUES ('322', '10', 'https://s1.st.meishij.net/rs/04/143/2598254/n2598254_87666.jpg', '炸红油哈~鸡丝凉面的精髓就在这里~~红油好看又香的秘诀是分次加入油', '68');
INSERT INTO `cookbook_marche` VALUES ('323', '11', 'https://s1.st.meishij.net/rs/04/143/2598254/n2598254_17387.jpg', '油烧热，分三次加入辣椒中，先加入1/3，用筷子搅拌均匀，再加入1/3，搅拌均匀', '68');
INSERT INTO `cookbook_marche` VALUES ('324', '12', 'https://s1.st.meishij.net/rs/04/143/2598254/n2598254_92523.jpg', '最后加入剩下的1/3油，此时愿意的话可以加点芝麻，芝麻随意，最后加入面中也行', '68');
INSERT INTO `cookbook_marche` VALUES ('325', '1', 'https://s1.st.meishij.net/rs/185/123/1655935/n1655935_66275.jpg', '五花肉洗净煮熟，熟五花肉冷却以后切薄片', '69');
INSERT INTO `cookbook_marche` VALUES ('326', '2', 'https://s1.st.meishij.net/rs/185/123/1655935/n1655935_82973.jpg', '青、红尖椒切块', '69');
INSERT INTO `cookbook_marche` VALUES ('327', '3', 'https://s1.st.meishij.net/rs/185/123/1655935/n1655935_81209.jpg', '大蒜切片，生姜切丝，大葱切马耳片', '69');
INSERT INTO `cookbook_marche` VALUES ('328', '4', 'https://s1.st.meishij.net/rs/185/123/1655935/n1655935_95463.jpg', '洋葱也切大片', '69');
INSERT INTO `cookbook_marche` VALUES ('329', '5', 'https://s1.st.meishij.net/rs/185/123/1655935/n1655935_07139.jpg', '锅里水烧开，放入肉片焯烫一下，先去除一部分油 ', '69');
INSERT INTO `cookbook_marche` VALUES ('330', '6', 'https://s1.st.meishij.net/rs/185/123/1655935/n1655935_73698.jpg', '肉片捞出沥干水分', '69');
INSERT INTO `cookbook_marche` VALUES ('331', '7', 'https://s1.st.meishij.net/rs/185/123/1655935/n1655935_87846.jpg', '煎锅开小火，放入肉片慢慢煸香', '69');
INSERT INTO `cookbook_marche` VALUES ('332', '8', 'https://s1.st.meishij.net/rs/185/123/1655935/n1655935_58444.jpg', '等到肉片发出噼里啪啦的声音，放入豆豉、葱、姜、蒜一起煸炒', '69');
INSERT INTO `cookbook_marche` VALUES ('333', '1', 'https://s1.st.meishij.net/rs/69/57/9889319/n9889319_150288582855059.jpg', '刷油', '285');
INSERT INTO `cookbook_marche` VALUES ('334', '2', 'https://s1.st.meishij.net/rs/69/57/9889319/n9889319_150288595222297.jpg', '倒入薄薄的一层', '285');
INSERT INTO `cookbook_marche` VALUES ('335', '3', 'https://s1.st.meishij.net/rs/69/57/9889319/n9889319_150288609763602.jpg', '先加入想加的肉或蔬菜', '285');
INSERT INTO `cookbook_marche` VALUES ('336', '4', 'https://s1.st.meishij.net/rs/69/57/9889319/n9889319_150288617300418.jpg', '这个我都加了', '285');
INSERT INTO `cookbook_marche` VALUES ('337', '1', 'https://s1.st.meishij.net/rs/30/75/4706280/n4706280_150285757582542.jpg', '把肠粉按照比例，一杯放到碗里', '286');
INSERT INTO `cookbook_marche` VALUES ('338', '2', 'https://s1.st.meishij.net/rs/30/75/4706280/n4706280_150285758128160.jpg', '加入一杯半的清水，用勺子搅拌', '286');
INSERT INTO `cookbook_marche` VALUES ('339', '3', 'https://s1.st.meishij.net/rs/30/75/4706280/n4706280_150285758743140.jpg', '把鲜虾剥去外壳，青菜切末', '286');
INSERT INTO `cookbook_marche` VALUES ('340', '4', 'https://s1.st.meishij.net/rs/30/75/4706280/n4706280_150285759273494.jpg', '把蒸盘洗净擦干，用刷子刷上植物油', '286');
INSERT INTO `cookbook_marche` VALUES ('341', '5', 'https://s1.st.meishij.net/rs/30/75/4706280/n4706280_150285759841279.jpg', '舀入搅拌好的肠粉', '286');
INSERT INTO `cookbook_marche` VALUES ('342', '6', 'https://s1.st.meishij.net/rs/30/75/4706280/n4706280_150285760594400.jpg', '加上虾仁和青菜末', '286');
INSERT INTO `cookbook_marche` VALUES ('343', '7', 'https://s1.st.meishij.net/rs/30/75/4706280/n4706280_150285761204794.jpg', '锅里烧开水，放到锅里蒸2分钟，这个粉皮鼓起，虾仁熟透', '286');
INSERT INTO `cookbook_marche` VALUES ('344', '1', 'https://s1.st.meishij.net/rs/44/110/4152544/n4152544_149768885449938.jpg', '各材料', '287');
INSERT INTO `cookbook_marche` VALUES ('345', '2', 'https://s1.st.meishij.net/rs/44/110/4152544/n4152544_149770556523059.jpg', '萝卜丝炒软去水份', '287');
INSERT INTO `cookbook_marche` VALUES ('346', '3', 'https://s1.st.meishij.net/rs/44/110/4152544/n4152544_149768888447384.jpg', '鱼茸加入调味料与麻油拌好待用', '287');
INSERT INTO `cookbook_marche` VALUES ('347', '4', 'https://s1.st.meishij.net/rs/44/110/4152544/n4152544_149768890694766.jpg', '在粘米粉内加水调开', '287');
INSERT INTO `cookbook_marche` VALUES ('348', '5', 'https://s1.st.meishij.net/rs/44/110/4152544/n4152544_149768892540705.jpg', '加入萝卜丝拌开', '287');
INSERT INTO `cookbook_marche` VALUES ('349', '6', 'https://s1.st.meishij.net/rs/44/110/4152544/n4152544_149768894545573.jpg', '加入鱼茸', '287');
INSERT INTO `cookbook_marche` VALUES ('350', '1', 'https://s1.st.meishij.net/rs/160/00/9625160/n9625160_149016030340052.jpg', '用我们家里平时用的米杯子来配粉浆，一杯粉加1.25杯水（体积比1:1.25/重量比1:2.5）', '288');
INSERT INTO `cookbook_marche` VALUES ('351', '2', 'https://s1.st.meishij.net/rs/160/00/9625160/n9625160_149016088513184.jpg', '把粉和水混合在一起，搅拌均匀；', '288');
INSERT INTO `cookbook_marche` VALUES ('352', '3', 'https://s1.st.meishij.net/rs/160/00/9625160/n9625160_149016158346250.jpg', '蒸盘中倒入薄薄的一层粉浆，之后可以加入您喜欢吃的馅料；', '288');
INSERT INTO `cookbook_marche` VALUES ('353', '1', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_149083582897304.jpg', '食材准备好，牛肉切丝备用', '289');
INSERT INTO `cookbook_marche` VALUES ('354', '2', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_149083583144963.jpg', '肠粉加水，混合均匀', '289');
INSERT INTO `cookbook_marche` VALUES ('355', '3', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_149083583344352.jpg', '肠粉专用盘子上刷油，将肠粉水倒进去，薄薄的一层即可', '289');
INSERT INTO `cookbook_marche` VALUES ('356', '4', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_149083583484663.jpg', '将盘子放在开水上', '289');
INSERT INTO `cookbook_marche` VALUES ('357', '5', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_149083583807043.jpg', '打入一个鸡蛋，滑散，撒上牛肉丝，再撒上青菜丝，盖上锅盖蒸至凝固即可', '289');
INSERT INTO `cookbook_marche` VALUES ('358', '1', 'https://s1.st.meishij.net/rs/52/228/9682052/n9682052_149060433751231.jpg', '食材图', '290');
INSERT INTO `cookbook_marche` VALUES ('359', '2', 'https://s1.st.meishij.net/rs/52/228/9682052/n9682052_149060435398112.jpg', '云吞的制作：肉糜加入盐、糖、胡椒粉、麻油，搅拌上劲', '290');
INSERT INTO `cookbook_marche` VALUES ('360', '3', 'https://s1.st.meishij.net/rs/52/228/9682052/n9682052_149060438198283.jpg', '适量肉馅挑到云吞皮上，对角对折云吞皮，用褶皱的方法收口', '290');
INSERT INTO `cookbook_marche` VALUES ('361', '1', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_149036318483478.jpg', '各种食材准备好', '291');
INSERT INTO `cookbook_marche` VALUES ('362', '2', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_149036318583425.jpg', '100克虾饺粉加200克开水烫熟，再加入150克虾饺粉混合，揉成光滑的面团', '291');
INSERT INTO `cookbook_marche` VALUES ('363', '3', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_149036318723877.jpg', '虾挑出虾线，去壳，用刀背碾成泥，胡萝卜丁和青豆准备好', '291');
INSERT INTO `cookbook_marche` VALUES ('364', '4', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_149036318832323.jpg', '虾泥和胡萝卜丁、青豆粒混合起来，加入盐，拌成虾饺馅', '291');
INSERT INTO `cookbook_marche` VALUES ('365', '5', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_149036318964715.jpg', '虾饺粉面团搓成长条', '291');
INSERT INTO `cookbook_marche` VALUES ('366', '6', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_149036319443060.jpg', '切成小剂子', '291');
INSERT INTO `cookbook_marche` VALUES ('367', '1', 'https://s1.st.meishij.net/rs/109/168/8354609/n8354609_148888599471117.jpg', '准备好所有原材料。猪肚冲洗干净之后，正反两面用面粉和盐揉搓之后继续冲洗干净，之后焯一下水。', '292');
INSERT INTO `cookbook_marche` VALUES ('368', '2', 'https://s1.st.meishij.net/rs/109/168/8354609/n8354609_148888603129785.jpg', '鸡洗干净之后斩块，同样焯一下水。', '292');
INSERT INTO `cookbook_marche` VALUES ('369', '3', 'https://s1.st.meishij.net/rs/109/168/8354609/n8354609_148888604483667.jpg', '将鸡块和白胡椒粒、姜片塞进猪肚。', '292');
INSERT INTO `cookbook_marche` VALUES ('370', '4', 'https://s1.st.meishij.net/rs/109/168/8354609/n8354609_148888606325964.jpg', '塞满整个猪肚。', '292');
INSERT INTO `cookbook_marche` VALUES ('371', '5', 'https://s1.st.meishij.net/rs/109/168/8354609/n8354609_148888607609625.jpg', '将猪肚放入砂锅中，加入水，大约没过猪肚，电陶炉开动最大档，大火烧开。', '292');
INSERT INTO `cookbook_marche` VALUES ('372', '1', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656017272446.jpg', '糯米要浸泡2小时以上，比较方便的就是隔夜浸泡。', '293');
INSERT INTO `cookbook_marche` VALUES ('373', '2', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656019822636.jpg', '香菇用温水泡发，接着洗净切丁；胡萝卜去皮切小丁，香肠两根切小块。', '293');
INSERT INTO `cookbook_marche` VALUES ('374', '3', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656026541666.jpg', '锅内放油，先小火慢慢炒香香肠，煸到透明，再放入胡萝卜丁。', '293');
INSERT INTO `cookbook_marche` VALUES ('375', '4', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656031140446.jpg', '放入香菇丁，炒匀后加入泡好并洗净的糯米，记得加小半碗水，不然容易糊锅。加入适量的老抽、生抽、糖和盐。', '293');
INSERT INTO `cookbook_marche` VALUES ('376', '5', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656034944079.jpg', '不用炒很久，糯米把水分吸干了就关火。\r\n接着放入盆中，入蒸锅，上汽后蒸20分钟。晾凉需要一段时间。所以咱们是先蒸好馅料再揉面团。', '293');
INSERT INTO `cookbook_marche` VALUES ('377', '6', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656038800871.jpg', '面粉和适量清水，白糖可以不加，混合2克酵母，揉成光滑的面团，发酵到两倍大。', '293');
INSERT INTO `cookbook_marche` VALUES ('378', '1', 'https://s1.st.meishij.net/rs/199/30/7632699/n7632699_148281369802909.jpg', '萝卜擦成丝，略控一下水分。', '294');
INSERT INTO `cookbook_marche` VALUES ('379', '2', 'https://s1.st.meishij.net/rs/199/30/7632699/n7632699_148281369904214.jpg', '上锅开水烫软，滤干待用。', '294');
INSERT INTO `cookbook_marche` VALUES ('380', '3', 'https://s1.st.meishij.net/rs/199/30/7632699/n7632699_148281370022325.jpg', '准备原料。金华火腿最后先蒸一下。原谅利用工余时间（晚上）做的，灯光不好', '294');
INSERT INTO `cookbook_marche` VALUES ('381', '4', 'https://s1.st.meishij.net/rs/199/30/7632699/n7632699_148281370690900.jpg', '虾米、金华火腿、腊肠、香菇切碎。中小火，用30毫升油，先下腊肠煸出油，再下金华火腿、再下虾米、再下香菇炒香关火。也可以加适量的洋葱粒一并炒，口感层次更好。', '294');
INSERT INTO `cookbook_marche` VALUES ('382', '5', 'https://s1.st.meishij.net/rs/199/30/7632699/n7632699_148281370711061.jpg', '倒入萝卜丝，放入粘米粉，加入胡椒粉、鸡精、糖、蚝油拌匀，如口味偏淡，可再加一点点盐。静置15分钟，让所有材料味道互相浸透。', '294');
INSERT INTO `cookbook_marche` VALUES ('383', '1', 'https://s1.st.meishij.net/rs/94/25/8631344/n8631344_148176898774553.jpg', '材料准备好。楼主把芝麻、蒜蓉和香葱忘记拍进出了', '295');
INSERT INTO `cookbook_marche` VALUES ('384', '2', 'https://s1.st.meishij.net/rs/94/25/8631344/n8631344_148176902898234.jpg', '先把一大勺花生油烧热然后倒入一半的蒜蓉煸香后倒入河粉一齐翻炒', '295');
INSERT INTO `cookbook_marche` VALUES ('385', '3', 'https://s1.st.meishij.net/rs/94/25/8631344/n8631344_148176903012223.jpg', '五香粉、糖、盐一起倒入锅中翻炒', '295');
INSERT INTO `cookbook_marche` VALUES ('386', '4', 'https://s1.st.meishij.net/rs/94/25/8631344/n8631344_148176903251660.jpg', '翻炒到河粉和调料均匀就可以上锅备用', '295');
INSERT INTO `cookbook_marche` VALUES ('387', '5', 'https://s1.st.meishij.net/rs/94/25/8631344/n8631344_148176903339850.jpg', '在锅里倒一大勺油把剩下的蒜蓉煸香后和绿豆芽一起炒到7成熟', '295');
INSERT INTO `cookbook_marche` VALUES ('388', '6', 'https://s1.st.meishij.net/rs/94/25/8631344/n8631344_148176903559002.jpg', '把刚才炒好的河粉也倒入锅里，一齐翻炒均匀直到绿豆芽熟了就可以了', '295');
INSERT INTO `cookbook_marche` VALUES ('389', '7', 'https://s1.st.meishij.net/rs/94/25/8631344/n8631344_148176903660973.jpg', '撒上葱花翻炒几下就可以出锅了', '295');
INSERT INTO `cookbook_marche` VALUES ('390', '1', 'https://s1.st.meishij.net/rs/242/246/5561742/n5561742_148164048599642.jpg', '称好材料', '296');
INSERT INTO `cookbook_marche` VALUES ('391', '2', 'https://s1.st.meishij.net/rs/242/246/5561742/n5561742_148164049276057.jpg', '芋头切小块', '296');
INSERT INTO `cookbook_marche` VALUES ('392', '3', 'https://s1.st.meishij.net/rs/242/246/5561742/n5561742_148164049627244.jpg', '爆炒下大蒜', '296');
INSERT INTO `cookbook_marche` VALUES ('393', '4', 'https://s1.st.meishij.net/rs/242/246/5561742/n5561742_148164050082322.jpg', '放虾米和腊肠炒下', '296');
INSERT INTO `cookbook_marche` VALUES ('394', '5', 'https://s1.st.meishij.net/rs/242/246/5561742/n5561742_148164050318323.jpg', '放下芋头一起炒，放少糖，生抽，盐等，合适自己口味就可以了', '296');
INSERT INTO `cookbook_marche` VALUES ('395', '1', 'https://s1.st.meishij.net/rs/199/30/7632699/n7632699_148101609588888.jpg', '芋头、蕃薯去皮再削成丝，花生仁洗净泡洗15分钟略切碎，放于容器中，加入盐、五香粉、味精（依据自己口味加）。然后加入适量的生粉，最后加入少许水，搅匀了（以感觉到比较拈手为好）。', '297');
INSERT INTO `cookbook_marche` VALUES ('396', '2', 'https://s1.st.meishij.net/rs/199/30/7632699/n7632699_148101609691214.jpg', '将半张腐膜对半切，在板上铺开，取适量和好的芋丝在腐膜上，从左往右捏成大概4cm宽的带状。注意不要把芋丝捏得太紧太厚（太紧了成品会太硬，太厚了成品不容易炊熟）。', '297');
INSERT INTO `cookbook_marche` VALUES ('397', '1', 'https://s1.st.meishij.net/rs/95/165/7728845/n7728845_147602336819945.jpg', '锅里加油加热，生姜拍扁切块放入锅里煎出味道。', '298');
INSERT INTO `cookbook_marche` VALUES ('398', '2', 'https://s1.st.meishij.net/rs/95/165/7728845/n7728845_147602340233124.jpg', '猪肉丸洗干净，大颗的可以切半，然后在丸子的一面切5-7个切口。放入锅里稍微煎至表面金黄色就可以了。', '298');
INSERT INTO `cookbook_marche` VALUES ('399', '3', 'https://s1.st.meishij.net/rs/95/165/7728845/n7728845_147602343340734.jpg', '黄叶菜提前洗干净切断，放入锅中翻炒均匀。', '298');
INSERT INTO `cookbook_marche` VALUES ('400', '4', 'https://s1.st.meishij.net/rs/95/165/7728845/n7728845_147602346690534.jpg', '加入一小匙盐，翻炒一下。', '298');
INSERT INTO `cookbook_marche` VALUES ('401', '1', 'https://s1.st.meishij.net/rs/105/80/5270105/n5270105_147541133617409.jpeg', '洗净猪肉和香葱', '299');
INSERT INTO `cookbook_marche` VALUES ('402', '2', 'https://s1.st.meishij.net/rs/105/80/5270105/n5270105_147541135424964.jpeg', '澄面、粘米粉和清水，开成无生粉颗粒的粉浆', '299');
INSERT INTO `cookbook_marche` VALUES ('403', '3', 'https://s1.st.meishij.net/rs/105/80/5270105/n5270105_147541137730614.jpeg', '猪肉剁成肉末', '299');
INSERT INTO `cookbook_marche` VALUES ('404', '4', 'https://s1.st.meishij.net/rs/105/80/5270105/n5270105_147541139376896.jpeg', '加入盐、蚝油，搅拌均匀', '299');
INSERT INTO `cookbook_marche` VALUES ('405', '5', 'https://s1.st.meishij.net/rs/105/80/5270105/n5270105_147541141061342.jpeg', '葱切葱花', '299');
INSERT INTO `cookbook_marche` VALUES ('406', '1', 'https://s1.st.meishij.net/rs/151/126/4031651/n4031651_147528227946857.jpg', '小的是茶勺，大的是汤匙。', '300');
INSERT INTO `cookbook_marche` VALUES ('407', '2', 'https://s1.st.meishij.net/rs/151/126/4031651/n4031651_147529844596124.jpg', '准备牡蛎肉，茼蒿或别的绿叶青菜，鸡蛋和红薯淀粉。', '300');
