INSERT INTO `cookbook_marche` VALUES ('409', '4', 'https://s1.st.meishij.net/rs/151/126/4031651/n4031651_147529845290559.jpg', '牡蛎肉加入食盐0.5茶勺，黑胡椒粉0.5茶勺，白酒或料酒1汤匙，抓匀腌制15分钟。', '300');
