INSERT INTO `cookbook_marche` VALUES ('424', '4', 'https://s1.st.meishij.net/rs/34/177/4169284/n4169284_146574419919880.jpg', '用力把粘汁全部挤出来', '303');
INSERT INTO `cookbook_marche` VALUES ('425', '1', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146468199764612.jpg', '糯米洗净浸泡3小时；', '304');
INSERT INTO `cookbook_marche` VALUES ('426', '2', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146468200149702.jpg', '捞出糯米，沥干水；', '304');
INSERT INTO `cookbook_marche` VALUES ('427', '3', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146468200511120.jpg', '广式腊肠切粒、香菇浸泡切粒；', '304');
INSERT INTO `cookbook_marche` VALUES ('428', '4', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146468200824109.jpg', '热锅，小火煎腊肠，油出来，改为中火放香菇粒，爆香即可；PS：爆香过腊肠、香菇，包裹的粽子比较香好吃。', '304');
INSERT INTO `cookbook_marche` VALUES ('429', '5', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146468201129025.jpg', '把香菇、腊肠、盐、油、酱油加入沥干水的糯米中拌均；', '304');
INSERT INTO `cookbook_marche` VALUES ('430', '6', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146468201476348.jpg', '粽叶浸泡，洗净，沥干水；PS：我用的是干粽叶所以要浸泡；', '304');
INSERT INTO `cookbook_marche` VALUES ('431', '1', 'https://s1.st.meishij.net/rs/153/42/1885653/n1885653_146404440267054.jpg', '准备水磨粘米粉和适量热水\r\n', '305');
INSERT INTO `cookbook_marche` VALUES ('432', '2', 'https://s1.st.meishij.net/rs/153/42/1885653/n1885653_146404440716003.jpg', '粘米粉过筛入容器\r\n', '305');
INSERT INTO `cookbook_marche` VALUES ('433', '3', 'https://s1.st.meishij.net/rs/153/42/1885653/n1885653_146404441195100.jpg', '慢慢将热水倒进粘米粉里\r\n', '305');
INSERT INTO `cookbook_marche` VALUES ('434', '4', 'https://s1.st.meishij.net/rs/153/42/1885653/n1885653_146404441584834.jpg', '调至成比较浓的米浆\r\n', '305');
INSERT INTO `cookbook_marche` VALUES ('435', '1', 'https://s1.st.meishij.net/rs/181/148/3849681/n3849681_146366656987797.jpg', '葱切成葱花。', '306');
INSERT INTO `cookbook_marche` VALUES ('436', '2', 'https://s1.st.meishij.net/rs/181/148/3849681/n3849681_146366657349568.jpg', '粉加适量清水调成流动的糊。', '306');
INSERT INTO `cookbook_marche` VALUES ('437', '3', 'https://s1.st.meishij.net/rs/181/148/3849681/n3849681_146366657552416.jpg', '披萨盘放在锅里蒸热，刷上油。', '306');
INSERT INTO `cookbook_marche` VALUES ('438', '4', 'https://s1.st.meishij.net/rs/181/148/3849681/n3849681_146366683658828.jpg', '倒入粉浆。', '306');
INSERT INTO `cookbook_marche` VALUES ('439', '1', 'https://s1.st.meishij.net/rs/112/161/977862/n977862_146254602526965.jpg', '把米粉放在温开水浸泡半小时', '307');
INSERT INTO `cookbook_marche` VALUES ('440', '2', 'https://s1.st.meishij.net/rs/112/161/977862/n977862_146254603010991.jpg', '牛肉切片，用盐、白胡椒粉、料酒、生抽和玉米淀粉腌制片刻，西葫芦和胡萝卜削皮切细条', '307');
INSERT INTO `cookbook_marche` VALUES ('441', '3', 'https://s1.st.meishij.net/rs/112/161/977862/n977862_146254603315337.jpg', '平底锅烧热油爆香姜片，放牛肉片炒熟，盛起待用', '307');
INSERT INTO `cookbook_marche` VALUES ('442', '4', 'https://s1.st.meishij.net/rs/112/161/977862/n977862_146254616204879.jpg', '平底锅爆香蒜蓉，放胡萝卜和西葫芦炒熟，盛起待用', '307');
INSERT INTO `cookbook_marche` VALUES ('443', '1', 'https://s1.st.meishij.net/rs/143/162/2728143/n2728143_146223802033742.jpg', '先把米淘一下，然后泡上约一两个小时', '308');
INSERT INTO `cookbook_marche` VALUES ('444', '2', 'https://s1.st.meishij.net/rs/143/162/2728143/n2728143_146223802247936.jpg', '准备其他食材', '308');
INSERT INTO `cookbook_marche` VALUES ('445', '3', 'https://s1.st.meishij.net/rs/143/162/2728143/n2728143_146223802461225.jpg', '腊肉和五花肉切片；香茹洗发洗净、对半切开；胡萝卜切丁。', '308');
INSERT INTO `cookbook_marche` VALUES ('446', '4', 'https://s1.st.meishij.net/rs/143/162/2728143/n2728143_146223802588178.jpg', '腊肉干煸出油', '308');
INSERT INTO `cookbook_marche` VALUES ('447', '5', 'https://s1.st.meishij.net/rs/143/162/2728143/n2728143_146223802783046.jpg', '放入五花肉煸炒，放料酒。', '308');
INSERT INTO `cookbook_marche` VALUES ('448', '1', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146211283210389.jpg', '把花蛤洗净，泡淡盐水让其吐净泥沙。我买的这个特别干净，卖海鲜的老板特别好，已经把花蛤收拾的干干净净', '309');
INSERT INTO `cookbook_marche` VALUES ('449', '2', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146211284013068.jpg', '准备新鲜的面条和1小把韭菜，把韭菜洗净，切成小段', '309');
INSERT INTO `cookbook_marche` VALUES ('450', '3', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146211285723401.jpg', '锅中坐水，放入花蛤，加2片姜（去腥及中和花蛤的寒性）和1勺黄酒。开大火煮开', '309');
INSERT INTO `cookbook_marche` VALUES ('451', '4', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146211286640556.jpg', '大火煮开后，再煮2分钟。把浮沫用勺子撇掉', '309');
INSERT INTO `cookbook_marche` VALUES ('452', '1', 'https://s1.st.meishij.net/rs/195/17/2004445/n2004445_146177627462434.jpg', '清洗粉肠外面，姜切片，大葱对半竖着切开，葱用厨房剪刀剪葱花状。\r\n', '310');
INSERT INTO `cookbook_marche` VALUES ('453', '2', 'https://s1.st.meishij.net/rs/195/17/2004445/n2004445_146177628050530.jpg', '冷水下锅，开启“白切葱油鸡”功能，将水煮开。\r\n', '310');
INSERT INTO `cookbook_marche` VALUES ('454', '3', 'https://s1.st.meishij.net/rs/195/17/2004445/n2004445_146177629349708.jpg', '加入姜片、大葱段，再放入完整的粉肠。\r\n', '310');
INSERT INTO `cookbook_marche` VALUES ('455', '4', 'https://s1.st.meishij.net/rs/195/17/2004445/n2004445_146177630461912.jpg', '盖盖子，重新开启“白切鸡葱油鸡”功能至自动烹饪完成。\r\n', '310');
INSERT INTO `cookbook_marche` VALUES ('456', '1', 'https://s1.st.meishij.net/rs/195/17/2004445/n2004445_146176710604932.jpg', '食材准备、称量。\r\n', '311');
INSERT INTO `cookbook_marche` VALUES ('457', '2', 'https://s1.st.meishij.net/rs/195/17/2004445/n2004445_146176710932339.jpg', '马蹄粉、清水100克混合，用蛋抽搅打融化。\r\n', '311');
INSERT INTO `cookbook_marche` VALUES ('458', '3', 'https://s1.st.meishij.net/rs/195/17/2004445/n2004445_146176711121445.jpg', '姜汁红糖、清水100克倒入小奶锅内。\r\n', '311');
INSERT INTO `cookbook_marche` VALUES ('459', '4', 'https://s1.st.meishij.net/rs/195/17/2004445/n2004445_146176711429431.jpg', '红糖水中小火一边加热，一边搅拌至糖融化。\r\n', '311');
INSERT INTO `cookbook_marche` VALUES ('460', '1', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146155773772140.jpg', '猪腿肉和姜均洗净，用刀剁成肉糜。姜我是同肉一起剁的，当然你也可以分开进行。不喜欢姜的朋友可以把姜切成丝，用温水浸泡十几分钟，取姜水就好，它是用来去腥提味的。肉丸想要好吃，就得自己剁肉，不要偷懒哦', '312');
INSERT INTO `cookbook_marche` VALUES ('461', '2', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146155774731322.jpg', '把猪肉糜放到一大碗中，加适量的盐（不要太多，肉丸有个底味就好，后面的汤里还要加盐，不然会过咸）、酱油（根据自己喜好，可加可不加）、黄酒（去腥）、胡椒粉（去腥提味）', '312');
INSERT INTO `cookbook_marche` VALUES ('462', '3', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146155775483434.jpg', '淀粉加清水调匀成水淀粉，加到肉馅儿里。这一步很关键，不要省略，它使肉丸保持紧致，下锅入水不会散开。这些淀粉水我是全放进去了的，不要怕多，肉会吸水，只要搅拌到位。也可以用蛋清代替，不过个人感觉用蛋清的话搅拌起来有些费力气', '312');
INSERT INTO `cookbook_marche` VALUES ('463', '4', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146155777381028.jpg', '用筷子顺着同一个方向搅拌上劲，也可以用摔打的方法，把肉馅儿摔打至发黏，黏性十足是最好的啦', '312');
INSERT INTO `cookbook_marche` VALUES ('464', '5', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146155778281715.jpg', '水晶菜洗净，切成6厘米左右的小段备用', '312');
INSERT INTO `cookbook_marche` VALUES ('465', '1', 'https://s1.st.meishij.net/rs/238/170/6105238/n6105238_146121970911402.jpg', '红豆加水煮軟，后加糖，倒出压烂。1/3糯米粉加温水揉（熟粉），1/3糯米粉兑入淀粉加些冷水揉（生粉），再把两种熟粉+生粉+些许油混合揉在一起，形成（生熟粉）。注意面粉口感做出来好吃，就是生熟粉的勾兑和要加油！粉包入红豆馅，用叶子包，（叶子上涂点油防粘），用牙签封口。       ', '313');
INSERT INTO `cookbook_marche` VALUES ('466', '2', 'https://s1.st.meishij.net/rs/238/170/6105238/n6105238_146121971501584.jpg', '锅煮沸水，上锅蒸煮20-25分钟', '313');
INSERT INTO `cookbook_marche` VALUES ('467', '1', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146096532351390.jpg', '鸡爪，洗干净，去指甲；', '314');
INSERT INTO `cookbook_marche` VALUES ('468', '2', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146096532640848.jpg', '清水煮沸，放入鸡爪煮熟；', '314');
INSERT INTO `cookbook_marche` VALUES ('469', '3', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146096532870421.jpg', '捞出鸡爪，过冷水；PS：过冷水防止粘。', '314');
INSERT INTO `cookbook_marche` VALUES ('470', '4', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146096533138767.jpg', '热锅，倒油，油温180度，放入鸡爪，炸；PS：炸鸡爪的时候鸡爪的时候记得盖盖子，油会溅上来，防止烫伤', '314');
INSERT INTO `cookbook_marche` VALUES ('471', '5', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146096533345195.jpg', '炸好的鸡爪迅速放入准备好的冰水中，浸泡；PS：用冰水浸泡的比冷水浸泡效果更佳', '314');
INSERT INTO `cookbook_marche` VALUES ('472', '6', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_146096533662893.jpg', '浸泡的鸡爪变大肥大，皱皱的；', '314');
INSERT INTO `cookbook_marche` VALUES ('473', '1', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146094988659530.jpg', '花生和芝麻分别用烤箱烤香后，将花生去皮，放入破壁机设置坚果键工作一分钟左右，将花生打至粗颗粒的状态', '315');
INSERT INTO `cookbook_marche` VALUES ('474', '2', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146094988810505.jpg', '将坚果颗粒倒出来，和白糖一起混合均匀', '315');
INSERT INTO `cookbook_marche` VALUES ('475', '3', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146094988988949.jpg', '将面粉放在面条机里，开动面条机后，将油倒入水杯', '315');
INSERT INTO `cookbook_marche` VALUES ('476', '4', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146094989078286.jpg', '水杯里继续倒入水、鸡蛋，一起混合均匀', '315');
INSERT INTO `cookbook_marche` VALUES ('477', '5', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146094989123925.jpg', '面条机自动工作，将面片压出', '315');
INSERT INTO `cookbook_marche` VALUES ('478', '6', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146094989348172.jpg', '用模具切出圆形面片', '315');
INSERT INTO `cookbook_marche` VALUES ('479', '1', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146094862823678.jpg', '芋头洗净，切丝', '316');
INSERT INTO `cookbook_marche` VALUES ('480', '2', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146094863900344.jpg', '加入调料拌匀后，慢慢加入糯米粉和红薯粉', '316');
INSERT INTO `cookbook_marche` VALUES ('481', '3', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146094864979049.jpg', '调匀', '316');
INSERT INTO `cookbook_marche` VALUES ('482', '4', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146094865929225.jpg', '蒸盘上抹少许色拉油', '316');
INSERT INTO `cookbook_marche` VALUES ('483', '5', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146094866308764.jpg', '把拌好的芋头丝糊倒进去抹平', '316');
INSERT INTO `cookbook_marche` VALUES ('484', '1', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_146086500418631.jpg', '备好所需食材', '317');
INSERT INTO `cookbook_marche` VALUES ('485', '2', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_146086501095606.jpg', '排骨洗净沥干水，斩成小块，调入盐、酱油、蚝油', '317');
INSERT INTO `cookbook_marche` VALUES ('486', '3', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_146086502111737.jpg', '再加上一汤匙蒜蓉面豉酱（没有蒜蓉面豉酱可以用蒜头，豆豉剁碎代替）', '317');
INSERT INTO `cookbook_marche` VALUES ('487', '4', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_146086502823805.jpg', '最后调入鸡粉、淀粉、白糖、少许香油搅拌均匀，腌制半小时', '317');
INSERT INTO `cookbook_marche` VALUES ('488', '5', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_146086503272762.jpg', '大米清洗一遍，再浸泡30分钟，腌制排骨同时进行', '317');
INSERT INTO `cookbook_marche` VALUES ('489', '6', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_146086504047161.jpg', '小唐菜浸泡片刻洗净', '317');
INSERT INTO `cookbook_marche` VALUES ('490', '7', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_146086504471127.jpg', '砂锅内涂抹一层食用油', '317');
INSERT INTO `cookbook_marche` VALUES ('491', '8', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_146086505109389.jpg', '将浸泡好的大米倒入锅中，水量高出大米一指厚度、1厘米左右就可以了', '317');
INSERT INTO `cookbook_marche` VALUES ('492', '1', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_145949075385744.jpg', '准备好所有食材，粘米粉、花生仁，模具（铜勺），PS：这种磨具在韶关各县市都有的卖，其他地方好像没有。', '318');
INSERT INTO `cookbook_marche` VALUES ('493', '2', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_145949078296846.jpg', '花生仁，浸泡洗干净，沥干水；', '318');
INSERT INTO `cookbook_marche` VALUES ('494', '3', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_145949079988415.jpg', '200克粘米粉加280克清水、盐、五香粉拌均；PS：水量与粘米粉比例很重要，太稀太稠都影响口感；', '318');
INSERT INTO `cookbook_marche` VALUES ('495', '4', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_145949081372587.jpg', '加入沥干水的花生仁拌均；', '318');
INSERT INTO `cookbook_marche` VALUES ('496', '5', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_145949082662202.jpg', '拌均好的米浆静放10分钟；', '318');
INSERT INTO `cookbook_marche` VALUES ('497', '6', 'https://s1.st.meishij.net/rs/97/32/4758097/n4758097_145949083600739.jpg', '热锅，倒油，油温160度左右，放入空的铜勺干炸；', '318');
INSERT INTO `cookbook_marche` VALUES ('498', '1', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145744843011380.jpg', '准备好食材（2杯大米大概300克）', '319');
INSERT INTO `cookbook_marche` VALUES ('499', '2', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145744849602425.jpg', '大米洗净，放入电饭煲内胆中', '319');
INSERT INTO `cookbook_marche` VALUES ('500', '3', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145744867197024.jpg', '加入1.5倍大米的水量（大米与水的比例为1:1.5为佳），浸泡1小时', '319');
INSERT INTO `cookbook_marche` VALUES ('501', '4', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145744877880079.jpg', '泡好的大米中加入半汤匙色拉油', '319');
INSERT INTO `cookbook_marche` VALUES ('502', '5', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145744889324441.jpg', '拌匀', '319');
INSERT INTO `cookbook_marche` VALUES ('503', '6', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145744896663449.jpg', '将煲胆放入电饭煲中，选择“煲仔饭”功能即可（或者普通的“煮饭”功能也可以）', '319');
INSERT INTO `cookbook_marche` VALUES ('504', '7', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145744907524497.jpg', '煮饭的时间准备一下其他食材：姜洗净切成细丝，腊肠切成片', '319');
INSERT INTO `cookbook_marche` VALUES ('505', '8', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145744919858650.jpg', '米饭煮至水分快干时（大概煮好前15分钟），打开煲盖，铺上腊肠', '319');
INSERT INTO `cookbook_marche` VALUES ('506', '9', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145744968032520.jpg', '撒上姜丝', '319');
INSERT INTO `cookbook_marche` VALUES ('507', '10', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145744962679276.jpg', '磕入2个鸡蛋', '319');
INSERT INTO `cookbook_marche` VALUES ('508', '1', 'https://s1.st.meishij.net/rs/232/103/5463482/n5463482_145247869317388.jpg', '准备好材料，腊肠，香菇，青菜，陈皮，大米', '320');
INSERT INTO `cookbook_marche` VALUES ('509', '2', 'https://s1.st.meishij.net/rs/232/103/5463482/n5463482_145247880756401.jpg', '大米用水浸泡2小时，葱花切粒，腊肠切块，香菇用温水泡一会，陈皮用温水泡后切粒。', '320');
INSERT INTO `cookbook_marche` VALUES ('510', '3', 'https://s1.st.meishij.net/rs/232/103/5463482/n5463482_145247893210303.jpg', '水烧开下油盐，青菜下水焯水一遍（不需要太久）捞起来备用。陈皮粒混合大米一起煮，8分钟后即可转小火。把香菇，腊肠，摆进锅内，盖上盖子焖5分钟。', '320');
INSERT INTO `cookbook_marche` VALUES ('511', '1', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145243271199834.jpg', '食材准备，土鸡一只洗净，红枣洗净，当归和党参用清水冲洗一下沥干水分', '321');
INSERT INTO `cookbook_marche` VALUES ('512', '2', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145243301197781.jpg', '加入2小匙鸡精，抹在鸡身上，并稍稍按摩几下使其腌制入味', '321');
INSERT INTO `cookbook_marche` VALUES ('513', '3', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145243321752898.jpg', '然后淋上鸡汁，尽量每个部位都抹上鸡汁，并稍作按摩使其入味', '321');
INSERT INTO `cookbook_marche` VALUES ('514', '4', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145243340329583.jpg', '鸡嘴塞一颗红枣（也可不塞哈）', '321');
INSERT INTO `cookbook_marche` VALUES ('515', '5', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145243347915951.jpg', '将党参和一片当归塞到鸡肚子里面', '321');
INSERT INTO `cookbook_marche` VALUES ('516', '6', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145243367455584.jpg', '倒入红枣', '321');
INSERT INTO `cookbook_marche` VALUES ('517', '7', 'https://s1.st.meishij.net/rs/190/222/2430690/n2430690_145243392753901.jpg', '淋上橄榄油', '321');
INSERT INTO `cookbook_marche` VALUES ('518', '1', 'https://s1.st.meishij.net/rs/02/165/666252/n666252_143884410290752.jpg', '准备材料', '322');
INSERT INTO `cookbook_marche` VALUES ('519', '2', 'https://s1.st.meishij.net/rs/02/165/666252/n666252_143884319985323.jpg', '牛奶中加入白糖，玉米淀粉，搅拌均匀', '322');
INSERT INTO `cookbook_marche` VALUES ('520', '3', 'https://s1.st.meishij.net/rs/02/165/666252/n666252_143884320771131.jpg', '小火加热，牛奶粟粉，不断搅拌，炒熟', '322');
