INSERT INTO `cookbook_marche` VALUES ('522', '1', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143882934719481.jpg', '新鲜金鲳鱼一条，一般档主会帮杀好，回来自己再清理一下内脏就可以', '323');
INSERT INTO `cookbook_marche` VALUES ('523', '2', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143882934896750.jpg', '清洗干净后在鱼身正反面划几刀。', '323');
