INSERT INTO `cookbook_marche` VALUES ('616', '2', 'https://s1.st.meishij.net/rs/201/107/3964451/n3964451_143834743180682.png', '把五花肉切小块剁成肉馅，榨菜切碎，肉馅里加少许的盐，蚝油生抽五香粉胡椒粉、家乐鸡粉，料酒，用筷子往一个方向搅打至上劲，然后把榨菜拌进肉馅里备用', '339');
