INSERT INTO `cookbook_marche` VALUES ('628', '5', 'https://s1.st.meishij.net/rs/239/198/2299739/n2299739_143833199176595.jpg', '小火煮，边煮边搅拌', '341');
INSERT INTO `cookbook_marche` VALUES ('629', '1', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143824476717768.jpg', '备好食材（大米提前十分钟泡发）', '342');
INSERT INTO `cookbook_marche` VALUES ('630', '2', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143824476835829.jpg', '砂锅内先刷一层油，再把淘洗干净的大米放进，加适量水和少量盐拌匀，开中火煲（水量要比平常少1/3左右）', '342');
INSERT INTO `cookbook_marche` VALUES ('631', '3', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143824477063579.jpg', '把腊肠用开水烫一下，这样更好清洗', '342');
INSERT INTO `cookbook_marche` VALUES ('632', '4', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143824477025668.jpg', '再切成薄片', '342');
INSERT INTO `cookbook_marche` VALUES ('633', '5', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143824477473935.jpg', '烧开后把腊肠摆在饭面上，并加入少量姜丝。继续盖上锅盖调成小火煲约八分钟，听到“吱吱”响便熄火。煲好后不要打开锅盖，继续焖十分钟左右', '342');
INSERT INTO `cookbook_marche` VALUES ('634', '1', 'https://s1.st.meishij.net/rs/112/161/977862/n977862_143806094542018.jpg', '准备材料：椰子壳很是坚实，要把椰子肉和椰子汁弄出来要费功夫，我让售卖椰子的档主代劳了，椰子肉切小片，椰子汁用容器盛着待用。不过如果自己处理要想象一下椰子鸡汤的清甜，耐心地处理椰子是很值得的', '343');
INSERT INTO `cookbook_marche` VALUES ('635', '2', 'https://s1.st.meishij.net/rs/112/161/977862/n977862_143806099215668.jpg', '把鸡斩件，如果鸡汤油腻，可以把鸡皮去掉，还有一个办法是煲好后，撇去鸡汤上面的油', '343');
INSERT INTO `cookbook_marche` VALUES ('636', '1', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143799169336774.jpg', '鸡爪清洗一遍，去指尖，剁小块；锅里烧开适量水，水开后加进几滴白醋把鸡爪放进煮几分钟（煮至里面也没有血色）；把煮好的鸡爪洗净控干水份', '344');
INSERT INTO `cookbook_marche` VALUES ('637', '2', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143806236250434.jpg', '锅里烧油，鸡爪晾干水份，表面撒上少量面粉；油烧至六七成热时把鸡爪倒进（如果鸡爪没有晾干水份，一下锅马上就会有油溅也，所以要把鸡爪晾得干一些）', '344');
INSERT INTO `cookbook_marche` VALUES ('638', '3', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143806237421861.jpg', '到后期鸡爪会不停的开始有油溅出，这会开成小火用锅盖盖住，炸至金黄色便可；把炸好的鸡爪沥干油，放进备好的冰块里，半天左右，虎皮就形成了（最好至少有半天时间，这样虎皮起得更好）', '344');
INSERT INTO `cookbook_marche` VALUES ('639', '1', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143792293206821.jpg', '猪瘦肉洗净沥干水，切丝', '345');
INSERT INTO `cookbook_marche` VALUES ('640', '2', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143792295407206.jpg', '瘦肉调入盐、鸡粉、少许酱油、淀粉，香油', '345');
INSERT INTO `cookbook_marche` VALUES ('641', '3', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143792297764499.jpg', '搅拌均匀，腌制片刻', '345');
INSERT INTO `cookbook_marche` VALUES ('642', '4', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143792299737901.jpg', '腌制瘦肉的同时，取出其他食材', '345');
INSERT INTO `cookbook_marche` VALUES ('643', '5', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143792302700007.jpg', '胡萝卜去皮、洗净切丝，豆芽洗净去头尾，生菜洗净切丝，葱洗净切段', '345');
INSERT INTO `cookbook_marche` VALUES ('644', '6', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143792317314267.jpg', '热锅，放入适量油，倒入瘦肉和葱头部分爆炒至肉丝发白', '345');
INSERT INTO `cookbook_marche` VALUES ('645', '7', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143792312114251.jpg', '铲起，放在一旁备用', '345');
INSERT INTO `cookbook_marche` VALUES ('646', '1', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_143745869450830.jpg', '所有粉放一大碗中，加入少量盐，加温水调成米浆（水要分次加入，观察稀稠度酌情增减）米浆的流动状态如图，粉的干湿不同加水的分量要自己调节一下，稀则加些粘米粉，稠则加水。米浆调好后静止30分钟，等米粉完全吸收水分，这样蒸出的肠粉更滑更爽', '346');
INSERT INTO `cookbook_marche` VALUES ('647', '2', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_143745870643752.jpg', '猪肉剁烂调入盐、耗油、生抽、油、生粉、顺一方向搅拌至上劲', '346');
INSERT INTO `cookbook_marche` VALUES ('648', '3', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_143745873983093.jpg', '主材料集合，香葱切葱花', '346');
INSERT INTO `cookbook_marche` VALUES ('649', '4', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_143745881598862.jpg', '烧一锅开水放上蒸架，平底大蒸盘两个交替使用，空盘上锅蒸热', '346');
INSERT INTO `cookbook_marche` VALUES ('650', '5', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_143745882520348.jpg', '拿出擦一层油', '346');
INSERT INTO `cookbook_marche` VALUES ('651', '6', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_143745883751922.jpg', '均匀散布小肉块和葱花', '346');
INSERT INTO `cookbook_marche` VALUES ('652', '1', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143737764492218.jpg', '所需食材', '347');
INSERT INTO `cookbook_marche` VALUES ('653', '2', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143737764580916.jpg', '把红枣和五指毛桃浸泡十分钟，再洗净表面的杂质', '347');
INSERT INTO `cookbook_marche` VALUES ('654', '3', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143737764826620.jpg', '把鸡剁块，焯水后再冲冼干净', '347');
