INSERT INTO `cookbook_marche` VALUES ('656', '2', 'https://s1.st.meishij.net/rs/247/150/2975247/n2975247_143737236800366.jpg', '100克的黑凉粉倒入300亳升清水中', '348');
INSERT INTO `cookbook_marche` VALUES ('657', '3', 'https://s1.st.meishij.net/rs/247/150/2975247/n2975247_143737239920422.jpg', '搅拌至无颗粒有流动的粉糊', '348');
INSERT INTO `cookbook_marche` VALUES ('658', '4', 'https://s1.st.meishij.net/rs/247/150/2975247/n2975247_143737240264546.jpg', '2200毫升清水入锅煮开，然后倒入粉糊', '348');
INSERT INTO `cookbook_marche` VALUES ('659', '1', 'https://s1.st.meishij.net/rs/220/83/3833470/n3833470_143736922598795.jpg', '食材准备', '349');
