INSERT INTO `cookbook_marche` VALUES ('661', '3', 'https://s1.st.meishij.net/rs/220/83/3833470/n3833470_143736927187262.jpg', '面包桶内加入适量的水\r\n', '349');
INSERT INTO `cookbook_marche` VALUES ('662', '4', 'https://s1.st.meishij.net/rs/220/83/3833470/n3833470_143736929904092.jpg', '此时 加入雪梨块\r\n', '349');
INSERT INTO `cookbook_marche` VALUES ('663', '5', 'https://s1.st.meishij.net/rs/220/83/3833470/n3833470_143736930812320.jpg', '加入处理成小块的银耳片\r\n', '349');
INSERT INTO `cookbook_marche` VALUES ('664', '1', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143721483835231.jpg', '将枧水倒入装有糖浆的盆内，搅拌均匀，再加入花生油，搅拌均匀。然后筛入三分之一低粉，拌匀后，再筛入剩下的低粉，用刮刀拌匀', '350');
INSERT INTO `cookbook_marche` VALUES ('665', '2', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143721483993297.jpg', '装入保鲜袋中，放冰箱冷藏1小时以上', '350');
INSERT INTO `cookbook_marche` VALUES ('666', '3', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143721484013168.jpg', '将月饼馅和饼皮分别称量（我的是50克模具）15克饼皮，35克馅儿，取一块饼皮，压扁后，放入馅儿，推动饼皮包住馅儿', '350');
INSERT INTO `cookbook_marche` VALUES ('667', '4', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143721484246593.jpg', '在模具里面撒上手粉，然后磕出多余的粉，只需留薄薄一层就好', '350');
INSERT INTO `cookbook_marche` VALUES ('668', '1', 'https://s1.st.meishij.net/rs/141/154/4351141/n4351141_143722265057939.jpg', '大米淘洗干净，放在盆里加400g清水浸泡15分钟备用', '351');
INSERT INTO `cookbook_marche` VALUES ('669', '2', 'https://s1.st.meishij.net/rs/141/154/4351141/n4351141_143722268970794.jpg', ' 将腊肠和胡萝卜切成小丁，准备好玉米粒和青豆', '351');
INSERT INTO `cookbook_marche` VALUES ('670', '3', 'https://s1.st.meishij.net/rs/141/154/4351141/n4351141_143722272908667.jpg', ' 准备好生姜末和大蒜末', '351');
INSERT INTO `cookbook_marche` VALUES ('671', '4', 'https://s1.st.meishij.net/rs/141/154/4351141/n4351141_143722276707742.jpg', ' 电磁炉火锅档800瓦的火加热锅，锅里放油，油温六成热时放蒜姜末炒香', '351');
INSERT INTO `cookbook_marche` VALUES ('672', '5', 'https://s1.st.meishij.net/rs/141/154/4351141/n4351141_143722285661701.jpg', ' 青豆、玉米、红萝卜丁、风干腊肠丁，一股脑儿倒入锅里，翻炒2分钟左右', '351');
INSERT INTO `cookbook_marche` VALUES ('673', '6', 'https://s1.st.meishij.net/rs/141/154/4351141/n4351141_143722289360002.jpg', '将浸泡好的大米倒入锅里，翻炒2分钟左右', '351');
INSERT INTO `cookbook_marche` VALUES ('674', '7', 'https://s1.st.meishij.net/rs/141/154/4351141/n4351141_143722304364405.jpg', ' 加入15ml的黄酒', '351');
INSERT INTO `cookbook_marche` VALUES ('675', '1', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143726586604850.jpg', '主料', '352');
INSERT INTO `cookbook_marche` VALUES ('676', '2', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143726586640446.jpg', '猪肉末里放入盐，料酒，胡椒粉，蚝油，1个鸡蛋，生抽，葱末，姜末，少许香油，分次加入花椒水', '352');
INSERT INTO `cookbook_marche` VALUES ('677', '3', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143726586704461.jpg', '朝一个方向搅打上劲儿', '352');
INSERT INTO `cookbook_marche` VALUES ('678', '4', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143726586856500.jpg', '豆腐切块，把中间挖去', '352');
INSERT INTO `cookbook_marche` VALUES ('679', '5', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143726586995347.jpg', '酿入肉末', '352');
INSERT INTO `cookbook_marche` VALUES ('680', '6', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143726586985354.jpg', '开口的一面拍少许面粉', '352');
INSERT INTO `cookbook_marche` VALUES ('681', '7', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143726587005478.jpg', '开口的一面朝下，逐个摆入加了油的电饼铛煎制', '352');
INSERT INTO `cookbook_marche` VALUES ('682', '1', 'https://s1.st.meishij.net/rs/112/200/2987612/n2987612_143720962560625.jpg', '将葱洗干净，切末', '353');
INSERT INTO `cookbook_marche` VALUES ('683', '2', 'https://s1.st.meishij.net/rs/112/200/2987612/n2987612_143720963316903.jpg', '胡萝卜切丝，玉米剥粒', '353');
INSERT INTO `cookbook_marche` VALUES ('684', '3', 'https://s1.st.meishij.net/rs/112/200/2987612/n2987612_143720963846286.jpg', '猪肉馅加入酱油、鸡精、胡椒粉，和葱搅拌', '353');
INSERT INTO `cookbook_marche` VALUES ('685', '1', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143720958212978.jpg', '买回来的新鲜虾洗净', '354');
INSERT INTO `cookbook_marche` VALUES ('686', '2', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143720954765002.jpg', '用牙签把虾的沙线挑出，剥掉虾壳', '354');
INSERT INTO `cookbook_marche` VALUES ('687', '3', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143720969597264.jpg', '准备好的食材：虾仁、葱花、姜末、胡萝卜小丁、稍肥点的猪肉末', '354');
INSERT INTO `cookbook_marche` VALUES ('688', '4', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143720971756864.jpg', '胡萝卜切稍厚点的圆片', '354');
INSERT INTO `cookbook_marche` VALUES ('689', '5', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143720992737678.jpg', '把一半的虾仁切大块，用1克盐和3克料酒腌制10分钟', '354');
INSERT INTO `cookbook_marche` VALUES ('690', '6', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143720983725222.jpg', '再把一半的虾仁剁成泥，先用刀面把虾仁拍扁后再剁', '354');
INSERT INTO `cookbook_marche` VALUES ('691', '7', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143721026202251.jpg', '把虾仁泥和肉末倒在一起', '354');
INSERT INTO `cookbook_marche` VALUES ('692', '8', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143721028395580.jpg', '放入姜末、3克盐、鸡精、胡椒粉、糖和3克玉米油，放好调料后朝一个方向搅匀，如果太干，可以加少许清水', '354');
INSERT INTO `cookbook_marche` VALUES ('693', '9', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143720984132049.jpg', '再加10克胡萝卜小丁搅匀', '354');
INSERT INTO `cookbook_marche` VALUES ('694', '10', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143721018816106.jpg', '澄面和玉米淀粉', '354');
INSERT INTO `cookbook_marche` VALUES ('695', '11', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143721018659178.jpg', '盆里放入100克澄面、10克淀粉（留20克淀粉先备用）和1g盐，用筷子搅匀', '354');
INSERT INTO `cookbook_marche` VALUES ('696', '1', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143714660996648.jpg', '食材：粘米粉、鸡蛋、葱', '355');
INSERT INTO `cookbook_marche` VALUES ('697', '2', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143714662040612.jpg', '粘米粉倒入大碗中，加上适量清水调成米浆', '355');
INSERT INTO `cookbook_marche` VALUES ('698', '3', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143714663351705.jpg', '敲入鸡蛋，搅拌均匀', '355');
INSERT INTO `cookbook_marche` VALUES ('699', '4', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143714664790231.jpg', '调入盐，继续搅拌片刻', '355');
INSERT INTO `cookbook_marche` VALUES ('700', '5', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143714666088148.jpg', '蒸盆内刷一层食用油', '355');
INSERT INTO `cookbook_marche` VALUES ('701', '6', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143714692870762.jpg', '倒入调匀的鸡蛋米浆', '355');
INSERT INTO `cookbook_marche` VALUES ('702', '1', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143710839230876.jpg', '牛肉片先用盐、料酒、老抽、色拉油腌制一会', '356');
INSERT INTO `cookbook_marche` VALUES ('703', '2', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143710842048077.jpg', '豆芽菜清洗干净沥干水份备用', '356');
INSERT INTO `cookbook_marche` VALUES ('704', '3', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143710842589141.jpg', '洋葱切成丝', '356');
INSERT INTO `cookbook_marche` VALUES ('705', '4', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143710844358594.jpg', '起油锅先把牛肉片炒熟', '356');
INSERT INTO `cookbook_marche` VALUES ('706', '5', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143710846653561.jpg', '等牛肉变色盛出备用', '356');
INSERT INTO `cookbook_marche` VALUES ('707', '6', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143710848455565.jpg', '锅里水煮开后放入干河粉', '356');
INSERT INTO `cookbook_marche` VALUES ('708', '7', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143710849777668.jpg', '煮软后捞出沥干水分备用', '356');
INSERT INTO `cookbook_marche` VALUES ('709', '1', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143711010806782.jpg', '瘦肉洗净、沥干水', '357');
INSERT INTO `cookbook_marche` VALUES ('710', '2', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143711011979871.jpg', '对半切开两块，用竹签将瘦肉戳一些密集的小孔，这样腌制起来容易入味，再涂抹少量食盐', '357');
INSERT INTO `cookbook_marche` VALUES ('711', '3', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143711012775726.jpg', '瘦肉放入大盆里，倒入叉烧酱、酱油、排骨酱', '357');
INSERT INTO `cookbook_marche` VALUES ('712', '4', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143711014139925.jpg', '调入胡椒粉', '357');
INSERT INTO `cookbook_marche` VALUES ('713', '5', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_143711015547768.jpg', '大蒜去皮洗净，拍碎一起放入盆中，戴上一次性手套，抓匀瘦肉，让肉的每一个部位都渗入酱料，裹上保鲜膜，放入冰箱保鲜腌制半天', '357');
INSERT INTO `cookbook_marche` VALUES ('714', '1', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143710250033896.jpg', '用姜、蒜、叉烧酱、生抽、料酒把肉搅拌均匀腌制起 ', '358');
INSERT INTO `cookbook_marche` VALUES ('715', '2', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143710250637963.jpg', '保鲜膜包起来放在冰箱里腌制9小时', '358');
INSERT INTO `cookbook_marche` VALUES ('716', '1', 'https://s1.st.meishij.net/rs/97/121/30347/n30347_142734365687382.jpg', '蜜汁叉烧肉切小丁，加入李锦记叉烧酱，加入少许蜂蜜，搅匀后，加入玉米淀粉，调和均匀，做成叉烧馅料；将飞饼两张从冰箱取出、解冻', '359');
INSERT INTO `cookbook_marche` VALUES ('717', '1', 'https://s1.st.meishij.net/rs/112/158/2414612/n2414612_142252259939049.jpg', '红枣洗净后，去核，一分为二，然后每一边再切成3份', '360');
INSERT INTO `cookbook_marche` VALUES ('718', '2', 'https://s1.st.meishij.net/rs/112/158/2414612/n2414612_142252260460288.jpg', '将红枣肉里放到小碗里', '360');
INSERT INTO `cookbook_marche` VALUES ('719', '3', 'https://s1.st.meishij.net/rs/112/158/2414612/n2414612_142252260988800.jpg', '125克砵仔糕粉，用125克清水开成粉浆，搅拌均匀', '360');
INSERT INTO `cookbook_marche` VALUES ('720', '4', 'https://s1.st.meishij.net/rs/112/158/2414612/n2414612_142252261463293.jpg', '63克水加入63克细砂糖，煮沸腾', '360');
INSERT INTO `cookbook_marche` VALUES ('721', '1', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_142227803427449.jpg', '准备所有食材：牛肉切片，搁鸡粉、盐、香油、生粉加两滴水腌制片刻、豆芽洗净摘去两头、沥干水，葱洗净去须切段，生菜洗净切丝；\r\n河粉打散备用；\r\n用碗调好酱汁放在一旁备用：（以免到时手忙脚乱、炒久了影响口感）加上蚝油、生抽、老抽、鸡粉、白糖、香油搅拌均匀；\r\n鸡蛋敲破、用筷子搅拌成液体状', '361');
INSERT INTO `cookbook_marche` VALUES ('722', '2', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_142227806113190.jpg', '热锅、加适量油，鸡蛋液煎至两面金黄色的鸡蛋薄片；\r\n铲起、切丝备用；\r\n再次烧热锅，倒油、葱头爆香，倒下腌制好的牛肉保持大火爆炒至9成熟盛起；\r\n原锅继续烧热、放适量食用油，下豆芽、生菜爆炒', '361');
INSERT INTO `cookbook_marche` VALUES ('723', '1', 'https://s1.st.meishij.net/rs/237/178/1232237/n1232237_142201808438134.jpg', '准备做馅的材料（肥猪肉、鲜虾、姜、葱）', '362');
INSERT INTO `cookbook_marche` VALUES ('724', '2', 'https://s1.st.meishij.net/rs/237/178/1232237/n1232237_142201816880902.jpg', '将鲜虾去壳、去头、去虾线和内脏', '362');
INSERT INTO `cookbook_marche` VALUES ('725', '3', 'https://s1.st.meishij.net/rs/237/178/1232237/n1232237_142201821083520.jpg', '将大的虾挑出来做整虾，小的切成丁，肥猪肉也切成丁', '362');
INSERT INTO `cookbook_marche` VALUES ('726', '4', 'https://s1.st.meishij.net/rs/237/178/1232237/n1232237_142201826919787.jpg', '再把虾肉和肥猪肉混合剁成肉泥', '362');
INSERT INTO `cookbook_marche` VALUES ('727', '5', 'https://s1.st.meishij.net/rs/237/178/1232237/n1232237_142201830085932.jpg', '然后用手抓起虾泥在菜板上摔至上劲（比较粘稠即可）', '362');
INSERT INTO `cookbook_marche` VALUES ('728', '6', 'https://s1.st.meishij.net/rs/237/178/1232237/n1232237_142201832740145.jpg', '再把虾泥放入碗内，加入葱白沫、姜沫、料酒、盐、鱼露、胡椒粉', '362');
INSERT INTO `cookbook_marche` VALUES ('729', '7', 'https://s1.st.meishij.net/rs/237/178/1232237/n1232237_142201835479342.jpg', '再把虾泥打搅上劲（很粘稠的样子）', '362');
INSERT INTO `cookbook_marche` VALUES ('730', '8', 'https://s1.st.meishij.net/rs/237/178/1232237/n1232237_142201838290356.jpg', '然后加入葱花拌匀', '362');
INSERT INTO `cookbook_marche` VALUES ('731', '9', 'https://s1.st.meishij.net/rs/237/178/1232237/n1232237_142201845967818.jpg', '整虾加入姜片、葱白、盐、料酒拌匀腌制', '362');
INSERT INTO `cookbook_marche` VALUES ('732', '10', 'https://s1.st.meishij.net/rs/237/178/1232237/n1232237_142201847427374.jpg', '澄粉、淀粉、盐放入大碗内，再拌匀', '362');
INSERT INTO `cookbook_marche` VALUES ('733', '11', 'https://s1.st.meishij.net/rs/237/178/1232237/n1232237_142201853065922.jpg', '开水加入澄粉内，边加边搅拌', '362');
INSERT INTO `cookbook_marche` VALUES ('734', '1', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_142192691170403.jpg', '糯米洗净浸泡2小时', '363');
INSERT INTO `cookbook_marche` VALUES ('735', '2', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_142192695081728.jpg', '香菇洗净泡发', '363');
INSERT INTO `cookbook_marche` VALUES ('736', '3', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_142192704064971.jpg', '干荷叶热水泡软', '363');
INSERT INTO `cookbook_marche` VALUES ('737', '4', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_142192708988998.jpg', '泡好的糯米调入3克的盐和几滴油拌匀，加入刚没过米面的水蒸熟，约需45分钟', '363');
INSERT INTO `cookbook_marche` VALUES ('738', '5', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_142192728001610.jpg', '鸡腿去除骨头（骨头可留来煲汤）', '363');
INSERT INTO `cookbook_marche` VALUES ('739', '6', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_142192731470283.jpg', '鸡肉切小块，调入蚝油、生抽、生粉、油、盐腌一下', '363');
INSERT INTO `cookbook_marche` VALUES ('740', '7', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_142192734516958.jpg', '泡发好的香菇切丝，红枣、姜也切丝和杞子一起与鸡肉拌', '363');
INSERT INTO `cookbook_marche` VALUES ('741', '8', 'https://s1.st.meishij.net/rs/17/180/732517/n732517_142192737565784.jpg', '蒸好的糯米饭打散放凉', '363');
INSERT INTO `cookbook_marche` VALUES ('742', '1', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_142191527971510.jpg', '河粉拆开包装，（市场里有散卖的河粉也一样）用一个盆子盛起，用筷子划散，韭黄洗净沥干水，切段', '364');
INSERT INTO `cookbook_marche` VALUES ('743', '2', 'https://s1.st.meishij.net/rs/246/137/1534496/n1534496_142191529536514.jpg', '瘦肉切片、鸡粉、生抽、生粉、搅拌均匀腌制一下', '364');
