INSERT INTO `cookbook_marche` VALUES ('825', '1', 'https://s1.st.meishij.net/rs/103/195/2423853/n2423853_46125.jpg', '瘦肉的加工：整个过程需要一锅煮开的热水，所以可以先把水煮着，然后加工这些食材，瘦肉的加工只需要简单切片', '378');
INSERT INTO `cookbook_marche` VALUES ('826', '2', 'https://s1.st.meishij.net/rs/103/195/2423853/n2423853_27021.jpg', '花生酱的加工：水开了之后，把花生酱搅拌融化，加上适量的老抽上色和调味，浓稠程度大概这样就可以了，也可以再稀一点，不要太浓稠了，不然后面和粿条搅拌的时候不好拌匀', '378');
INSERT INTO `cookbook_marche` VALUES ('827', '3', 'https://s1.st.meishij.net/rs/103/195/2423853/n2423853_27414.jpg', '沙茶酱的加工：沙茶酱比花生酱简单多了，加勺热水把它弄化就可以，因为本身就够咸，不必再加任何调味', '378');
INSERT INTO `cookbook_marche` VALUES ('828', '1', 'https://s1.st.meishij.net/rs/103/195/2423853/n2423853_77868.jpg', '白菜、香菇都是洗净切丝，瘦肉剁成肉末，很简单的一个加工', '379');
INSERT INTO `cookbook_marche` VALUES ('829', '2', 'https://s1.st.meishij.net/rs/103/195/2423853/n2423853_58883.jpg', '花生酱加开水调到变成如图的稠状液体，实在不知道怎么称呼这种状态，然后加上酱油调味，就变成如图的颜色，最好不要调得太稠，因为放置会变稠', '379');
INSERT INTO `cookbook_marche` VALUES ('830', '3', 'https://s1.st.meishij.net/rs/103/195/2423853/n2423853_98357.jpg', '肠粉皮摊开在盘子里，如果怕粘底的话，可以在盘子里刷上薄薄的一层植物油', '379');
INSERT INTO `cookbook_marche` VALUES ('831', '4', 'https://s1.st.meishij.net/rs/103/195/2423853/n2423853_19111.jpg', '铺好之后，铺上白菜丝、香菇丝、肉末等馅料，最后打上一个鸡蛋，划散', '379');
INSERT INTO `cookbook_marche` VALUES ('832', '1', 'https://s1.st.meishij.net/rs/77/37/2384327/n2384327_02474.jpg', '先将鲜蚝用清水洗净，放入筛网沥干水分备用', '380');
INSERT INTO `cookbook_marche` VALUES ('833', '2', 'https://s1.st.meishij.net/rs/77/37/2384327/n2384327_49823.jpg', '加入盐，味精，番薯粉，打入鸡蛋充分拌匀，使鲜蚝全部渗入粉', '380');
INSERT INTO `cookbook_marche` VALUES ('834', '3', 'https://s1.st.meishij.net/rs/77/37/2384327/n2384327_56791.jpg', '混合拌匀成浆糊状态，不需要加水，利用鲜蚝自身带的水分', '380');
INSERT INTO `cookbook_marche` VALUES ('835', '4', 'https://s1.st.meishij.net/rs/77/37/2384327/n2384327_08598.jpg', '放入喜欢的辣椒酱，搅匀', '380');
INSERT INTO `cookbook_marche` VALUES ('836', '5', 'https://s1.st.meishij.net/rs/77/37/2384327/n2384327_86953.jpg', '把葱洗净，白色部分也同样用上，切碎加入到鲜蚝浆糊中，加入的葱花多吃起来更美味，也可以在里面加入香菜碎', '380');
INSERT INTO `cookbook_marche` VALUES ('837', '1', 'https://s1.st.meishij.net/rs/207/90/460207/n460207_47370.jpg', '将马蹄粉分成两份，其中一份加350克的清水，搅拌成生浆', '382');
INSERT INTO `cookbook_marche` VALUES ('838', '2', 'https://s1.st.meishij.net/rs/207/90/460207/n460207_79353.jpg', '再取剩下的清水400克和200克白砂糖混合煮融', '382');
INSERT INTO `cookbook_marche` VALUES ('839', '3', 'https://s1.st.meishij.net/rs/207/90/460207/n460207_45227.jpg', '将焦糖浆放至微温，将生浆和糖水混合成焦糖马蹄浆备用', '382');
INSERT INTO `cookbook_marche` VALUES ('840', '4', 'https://s1.st.meishij.net/rs/207/90/460207/n460207_07080.jpg', '另外一份马蹄粉加350克椰汁搅拌成生浆', '382');
INSERT INTO `cookbook_marche` VALUES ('841', '5', 'https://s1.st.meishij.net/rs/207/90/460207/n460207_33871.jpg', '剩下的400克椰浆煮至微沸，倒入生浆中，混合成椰汁马蹄浆', '382');
INSERT INTO `cookbook_marche` VALUES ('842', '1', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_64496.jpg', '鲜奶倒进碗里，封上保鲜膜，放进锅里蒸10分钟（水开再放入，保持中火）', '387');
INSERT INTO `cookbook_marche` VALUES ('843', '2', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_91122.jpg', '把鸡蛋的蛋清和蛋黄分离，蛋清留着备用', '387');
INSERT INTO `cookbook_marche` VALUES ('844', '3', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_68206.jpg', '蒸好的牛奶掀去保鲜膜，放在阴凉处晾凉，表面的膜会慢慢凝结显现', '387');
INSERT INTO `cookbook_marche` VALUES ('845', '4', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_90653.jpg', '蛋清加入适量白砂糖，搅拌均匀', '387');
INSERT INTO `cookbook_marche` VALUES ('846', '5', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_56881.jpg', '牛奶晾凉（不用全凉，温温也行，关键是膜形成就行）后，用小刀在膜的边缘划一小口，\r\n用刀子顶住膜，然后把里边的牛奶倒进另一个碗里，碗里的牛奶不要全倒光，保留1/4', '387');
INSERT INTO `cookbook_marche` VALUES ('847', '6', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_21585.jpg', '倒出来的那部分牛奶加入蛋清，搅拌均匀后，用筛子筛掉表面的泡沫', '387');
INSERT INTO `cookbook_marche` VALUES ('848', '1', 'https://s1.st.meishij.net/rs/21/119/2654771/n2654771_20600.jpg', '材料准备好：牛腩洗净，姜、青椒切片，萝卜切块，干辣椒切段', '564');
INSERT INTO `cookbook_marche` VALUES ('849', '2', 'https://s1.st.meishij.net/rs/21/119/2654771/n2654771_34786.jpg', '牛腩逆着条纹切麻将大小；牛腩冷水下锅，放姜片，煮出血水后捞出', '564');
INSERT INTO `cookbook_marche` VALUES ('850', '3', 'https://s1.st.meishij.net/rs/21/119/2654771/n2654771_48109.jpg', '将焯水后的牛腩放入沙煲，注入足量水，放姜片，大火烧开，小火煲1小时，可用压力煲25分钟；随后将煮好的牛腩捞出，控干，牛肉汤留着备用', '564');
INSERT INTO `cookbook_marche` VALUES ('851', '4', 'https://s1.st.meishij.net/rs/21/119/2654771/n2654771_73744.jpg', '锅里放植物油，将牛肉放入煸炒一会；放姜、干辣椒、八角、香叶炒香', '564');
INSERT INTO `cookbook_marche` VALUES ('852', '1', 'http://pgsnqecfs.bkt.clouddn.com/a2d5df95-2c6d-46d3-8bf3-bf1697617b71.jpg', '我建议', '658');
INSERT INTO `cookbook_marche` VALUES ('853', '2', 'http://pgsnqecfs.bkt.clouddn.com/b72d9dea-db68-4c86-8ccc-30c07ce62c26.jpg', '我觉得', '658');
INSERT INTO `cookbook_marche` VALUES ('854', '3', 'https://s1.st.meishij.net/rs/216/197/6174466/n6174466_152776195850045.jpg', '把南瓜泥倒入面包桶里，加入面粉和酵母，启动和面程序搅拌均匀，大约五分钟就够了。没有面包机，也可以直接用饭勺搅拌均匀。', '658');
INSERT INTO `cookbook_marche` VALUES ('855', '1', 'https://s1.st.meishij.net/rs/150/187/12421900/n12421900_151779675659844.jpg', '南瓜削皮去籽切小块，当然是块越小越容易熟烂，上锅蒸熟。如何判断南瓜熟没熟，拿筷子一捅就能捅出个洞洞来，就是熟透了(～￣▽￣)～', '659');
INSERT INTO `cookbook_marche` VALUES ('856', '2', 'https://s1.st.meishij.net/rs/150/187/12421900/n12421900_151779676906966.jpg', '将蒸熟的南瓜导入汉美驰破壁机的搅拌杯中。一般的做法是把南瓜捣成泥，但是这样既费时费力，又不能保证南瓜和面粉混合均匀，用破壁机把南瓜打成酱之后再和面，会混合的更加充分。', '659');
INSERT INTO `cookbook_marche` VALUES ('857', '3', 'https://s1.st.meishij.net/rs/150/187/12421900/n12421900_151779678651337.jpg', '盖紧盖子，将搅拌杯放到底座上，确认放置稳妥，插上电源，打开机器右边的开关，是的，你会在我的每一个汉美驰破壁机食谱当中看到这段话，就好像每一次坐飞机乘务员都会告诉你，飞机即将降落请您收好小桌板并打开遮光布一样，确保使用安全。然后选择右下角的“纯果汁”程序，只需等待55秒，南瓜酱就做成了。', '659');
INSERT INTO `cookbook_marche` VALUES ('858', '4', 'https://s1.st.meishij.net/rs/150/187/12421900/n12421900_151779679707059.jpg', '刚打完的南瓜酱还热气腾腾的，弄得我好像对焦没有对好，但是可以看出汉美驰打出来的南瓜酱之细腻，恨不得再加点水直接打成南瓜汁喝掉了（づ￣3￣）づ╭❤～', '659');
INSERT INTO `cookbook_marche` VALUES ('859', '5', 'https://s1.st.meishij.net/rs/150/187/12421900/n12421900_151779680966995.jpg', '将打好的南瓜酱置于一个大些的容器中。为什么要大些？因为要拿它和面呀！', '659');
INSERT INTO `cookbook_marche` VALUES ('860', '1', 'https://s1.st.meishij.net/rs/87/178/5169587/n5169587_151670233538825.jpg', '除了牛奶以外的食材大集合，糖根据需要放哈', '660');
INSERT INTO `cookbook_marche` VALUES ('861', '2', 'https://s1.st.meishij.net/rs/87/178/5169587/n5169587_151670233598223.jpg', '放入牛奶拌匀，是个粘不拉几的一坨，哈哈哈', '660');
INSERT INTO `cookbook_marche` VALUES ('862', '1', 'https://s1.st.meishij.net/rs/32/219/3992282/n3992282_151618129644056.jpg', '豆腐用厨房纸巾吸取表面水分。', '661');
INSERT INTO `cookbook_marche` VALUES ('863', '2', 'https://s1.st.meishij.net/rs/32/219/3992282/n3992282_151618134440058.jpg', '将豆腐切成厚度为0.8cm左右的薄片。', '661');
INSERT INTO `cookbook_marche` VALUES ('864', '3', 'https://s1.st.meishij.net/rs/32/219/3992282/n3992282_151618138192132.jpg', '平底锅内倒入少许油，将豆腐小火煎至两面金黄。', '661');
INSERT INTO `cookbook_marche` VALUES ('865', '4', 'https://s1.st.meishij.net/rs/32/219/3992282/n3992282_151618142603687.jpg', '煎好的豆腐捞出备用。', '661');
INSERT INTO `cookbook_marche` VALUES ('866', '5', 'https://s1.st.meishij.net/rs/32/219/3992282/n3992282_151618148064175.jpg', '另起锅，倒入适量食用油，先加入两勺番茄酱翻炒。', '661');
INSERT INTO `cookbook_marche` VALUES ('867', '1', 'https://s1.st.meishij.net/rs/32/219/3992282/n3992282_151617410478115.jpg', '准备食材。', '662');
INSERT INTO `cookbook_marche` VALUES ('868', '2', 'https://s1.st.meishij.net/rs/32/219/3992282/n3992282_151617411142831.jpg', '豆腐用勺子碾碎。', '662');
INSERT INTO `cookbook_marche` VALUES ('869', '3', 'https://s1.st.meishij.net/rs/32/219/3992282/n3992282_151617414819383.jpg', '加入两勺香菇小酱。', '662');
INSERT INTO `cookbook_marche` VALUES ('870', '1', 'https://s1.st.meishij.net/rs/134/61/5140384/n5140384_151573844504388.jpg', '原料：大枣、红皮花生、面粉、玉米面', '663');
INSERT INTO `cookbook_marche` VALUES ('871', '2', 'https://s1.st.meishij.net/rs/134/61/5140384/n5140384_151573845954700.jpg', '将大枣和花生洗净后用清水浸泡。我用的大枣是干的，所以用水泡一下', '663');
INSERT INTO `cookbook_marche` VALUES ('872', '3', 'https://s1.st.meishij.net/rs/134/61/5140384/n5140384_151573847170159.jpg', '将玉米面倒入盆中，加入开水用筷子搅拌均匀', '663');
INSERT INTO `cookbook_marche` VALUES ('873', '4', 'https://s1.st.meishij.net/rs/134/61/5140384/n5140384_151573848008570.jpg', '再和成面团，晾凉', '663');
INSERT INTO `cookbook_marche` VALUES ('874', '5', 'https://s1.st.meishij.net/rs/134/61/5140384/n5140384_151573848958166.jpg', '面粉中加入泡打粉，混合均匀', '663');
INSERT INTO `cookbook_marche` VALUES ('875', '6', 'https://s1.st.meishij.net/rs/134/61/5140384/n5140384_151573849783608.jpg', '将酵母粉与浸泡花生的清水混合静置一会', '663');
INSERT INTO `cookbook_marche` VALUES ('876', '7', 'https://s1.st.meishij.net/rs/134/61/5140384/n5140384_151573850583173.jpg', '倒入面粉中，搅拌一下', '663');
INSERT INTO `cookbook_marche` VALUES ('877', '1', 'https://s1.st.meishij.net/rs/235/48/3699735/n3699735_151563362826779.jpg', '牛奶中加入酵母混合静置三分钟。', '664');
INSERT INTO `cookbook_marche` VALUES ('878', '2', 'https://s1.st.meishij.net/rs/235/48/3699735/n3699735_151563361946715.jpg', ' 加入玉米面和面粉。', '664');
INSERT INTO `cookbook_marche` VALUES ('879', '3', 'https://s1.st.meishij.net/rs/235/48/3699735/n3699735_151563362216276.jpg', '拌匀，达到比正常面团稍稀一点的程度。', '664');
INSERT INTO `cookbook_marche` VALUES ('880', '4', 'https://s1.st.meishij.net/rs/235/48/3699735/n3699735_151563361547379.jpg', '枸杞洗净。', '664');
INSERT INTO `cookbook_marche` VALUES ('881', '5', 'https://s1.st.meishij.net/rs/235/48/3699735/n3699735_151563360317007.jpg', '玉米面糊放入容器中。', '664');
INSERT INTO `cookbook_marche` VALUES ('882', '1', 'https://s1.st.meishij.net/rs/25/20/10817525/n10817525_151549154495117.jpg', '南瓜切成块，放入蒸锅中蒸15分钟', '665');
INSERT INTO `cookbook_marche` VALUES ('883', '2', 'https://s1.st.meishij.net/rs/25/20/10817525/n10817525_151549154839398.jpg', '蒸熟的南瓜加入牛奶，放入，料理机搅碎', '665');
INSERT INTO `cookbook_marche` VALUES ('884', '3', 'https://s1.st.meishij.net/rs/25/20/10817525/n10817525_151549155367023.jpg', '然后放凉后加入酵母粉', '665');
INSERT INTO `cookbook_marche` VALUES ('885', '4', 'https://s1.st.meishij.net/rs/25/20/10817525/n10817525_151549155860665.jpg', '在加入适量面粉搅拌', '665');
INSERT INTO `cookbook_marche` VALUES ('886', '5', 'https://s1.st.meishij.net/rs/25/20/10817525/n10817525_151549156362412.jpg', '面团放入温暖处醒发至图中样子', '665');
INSERT INTO `cookbook_marche` VALUES ('887', '6', 'https://s1.st.meishij.net/rs/25/20/10817525/n10817525_151549156925135.jpg', '找一个六寸模具，刷一层薄薄的油', '665');
INSERT INTO `cookbook_marche` VALUES ('888', '1', 'https://s1.st.meishij.net/rs/172/238/4872172/n4872172_151427225297593.jpg', '糯米加温水和面，面和的稍微稀一点，刚刚可以流动为好，盘底抹油，然后上锅蒸大概12分钟', '666');
INSERT INTO `cookbook_marche` VALUES ('889', '2', 'https://s1.st.meishij.net/rs/172/238/4872172/n4872172_151427225609830.jpg', '蒸好的糯米趁热搅拌，待晾凉后擀饼，撒豆面。然后抹上薄薄的花生酱，再把混合好的白糖，芝麻，核桃仁（2:1:1）撒在上面，然后卷卷', '666');
INSERT INTO `cookbook_marche` VALUES ('890', '1', 'https://s1.st.meishij.net/rs/41/172/1918041/n1918041_151515209251959.jpg', '牛奶加温至30度，加入酵母', '667');
INSERT INTO `cookbook_marche` VALUES ('891', '2', 'https://s1.st.meishij.net/rs/41/172/1918041/n1918041_151515209448982.jpg', '牛奶与酵母混合液中放入面粉，顺时针拌匀', '667');
INSERT INTO `cookbook_marche` VALUES ('892', '3', 'https://s1.st.meishij.net/rs/41/172/1918041/n1918041_151515209572071.jpg', '红枣去核切碎，放入和好的面中拌匀待用', '667');
INSERT INTO `cookbook_marche` VALUES ('893', '1', 'https://s1.st.meishij.net/rs/178/85/5833928/n5833928_151503232411767.png', '参考月龄：11个月以上，对食材不过敏的宝宝\r\n食材准备：红枣20克、低筋面粉120克、红糖35克、酵母3克\r\n烹饪时间：50分钟\r\n难度分析：初级', '668');
INSERT INTO `cookbook_marche` VALUES ('894', '2', 'https://s1.st.meishij.net/rs/178/85/5833928/n5833928_151503232813923.png', '红糖倒入大碗中，加入80克温水，用勺子搅拌一下化开，晾凉一会儿。\r\n红糖水要晾凉到大概40度左右时，再进行下一步。\r\n小月龄的宝宝可以将糖减到最少甚至不加都是可以的。', '668');
INSERT INTO `cookbook_marche` VALUES ('895', '3', 'https://s1.st.meishij.net/rs/178/85/5833928/n5833928_151503233184114.gif', '倒入低筋面粉，加入酵母。\r\n家用建议购买小袋的酵母，然后室温避光保存，避免酵母失效导致发酵过程失败。', '668');
INSERT INTO `cookbook_marche` VALUES ('896', '4', 'https://s1.st.meishij.net/rs/178/85/5833928/n5833928_151503233414304.png', '用筷子搅拌均匀，直至成为粘稠不易滑落的状态。\r\n千万不要过度搅拌，也不用上手揉面哦，因为是面糊状的，没法揉的。\r\n敲小黑板：重点来了，我配方中的量一定一定一定不要随意更改，如果想加量，可以按照比例增加。\r\n建议麻麻们购入一个可以精确到0.1的电子秤，这样成功率会大大提升，做各种蛋糕、饼干、溶豆都需要用到的。', '668');
INSERT INTO `cookbook_marche` VALUES ('897', '5', 'https://s1.st.meishij.net/rs/178/85/5833928/n5833928_151503233844324.gif', '容器底部铺上油纸。\r\n只要耐热容器就可以哈，啥都行。如果没有油纸，可以在模具底下可抹一层油，这样蒸完后比较容易脱模。', '668');
INSERT INTO `cookbook_marche` VALUES ('898', '6', 'https://s1.st.meishij.net/rs/178/85/5833928/n5833928_151503234199440.gif', '倒入面糊，放至温暖处发酵至2倍大。\r\n如果家里烤箱有发酵功能的直接用烤箱发酵，然后在蒸锅中加入半锅40度左右的热水，面团放在笼屉上，盖上锅盖发酵。\r\n发酵的过程，一定要耐心点儿，慢慢等。自己可以在容器上做好标记，发酵到2倍大，就可以停止，避免发酵时间过长，会产生酸败的异味。', '668');
INSERT INTO `cookbook_marche` VALUES ('899', '1', 'https://s1.st.meishij.net/rs/157/47/3324407/n3324407_151489535085509.jpg', '葱切末', '669');
INSERT INTO `cookbook_marche` VALUES ('900', '2', 'https://s1.st.meishij.net/rs/157/47/3324407/n3324407_151489535133543.jpg', '黄瓜切丝', '669');
INSERT INTO `cookbook_marche` VALUES ('901', '3', 'https://s1.st.meishij.net/rs/157/47/3324407/n3324407_151489535314996.jpg', '胡萝卜切丝后焯水2分钟', '669');
INSERT INTO `cookbook_marche` VALUES ('902', '4', 'https://s1.st.meishij.net/rs/157/47/3324407/n3324407_151489535522477.jpg', '豆芽焯水2分钟', '669');
INSERT INTO `cookbook_marche` VALUES ('903', '5', 'https://s1.st.meishij.net/rs/157/47/3324407/n3324407_151489535699905.jpg', '里脊切块后用料酒抓匀', '669');
