INSERT INTO `cookbook_marche` VALUES ('1098', '6', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_150969510490544.jpg', '盖上盖子放在温暖的地方发酵至体积两倍大。', '705');
INSERT INTO `cookbook_marche` VALUES ('1099', '7', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_150969511313313.jpg', '加入一把葡萄干，可以切碎也可以不切碎。', '705');
