INSERT INTO `cookbook_marche` VALUES ('1102', '3', 'https://s1.st.meishij.net/rs/137/187/359387/n359387_150944854042790.jpg', '取出面团撒上煮熟的豆子', '706');
INSERT INTO `cookbook_marche` VALUES ('1103', '1', 'https://s1.st.meishij.net/rs/106/232/3995606/n3995606_150893830806110.jpg', '我喜欢自然发酵，第一天晚上和面，喜欢甜口加入适量白糖发酵10小时左右，发好的面酸酸的味道；', '707');
INSERT INTO `cookbook_marche` VALUES ('1104', '2', 'https://s1.st.meishij.net/rs/106/232/3995606/n3995606_150893831102443.jpg', '芸豆入锅基本煮熟', '707');
