INSERT INTO `cookbook_marche` VALUES ('1107', '1', 'https://s1.st.meishij.net/rs/245/191/5172995/n5172995_150944362351763.jpg', '准备好所需的食材', '708');
INSERT INTO `cookbook_marche` VALUES ('1108', '2', 'https://s1.st.meishij.net/rs/245/191/5172995/n5172995_150944363678704.jpg', '干净的盘里倒入糯米粉、加水用勺子搅拌混合成面糊', '708');
INSERT INTO `cookbook_marche` VALUES ('1109', '3', 'https://s1.st.meishij.net/rs/245/191/5172995/n5172995_150944367842980.jpg', '表面盖保鲜膜，放入微波炉中高火2-3分钟左右，取出翻一下，再放入微波炉叮2分钟至面糊熟透', '708');
INSERT INTO `cookbook_marche` VALUES ('1110', '4', 'https://s1.st.meishij.net/rs/245/191/5172995/n5172995_150944361721093.jpg', ' 然后取出面团放在面板上，面板上放上一张保鲜膜，以免会粘上', '708');
INSERT INTO `cookbook_marche` VALUES ('1111', '1', 'https://s1.st.meishij.net/rs/216/236/6434216/n6434216_150942526042305.jpg', '准备食材', '709');
INSERT INTO `cookbook_marche` VALUES ('1112', '2', 'https://s1.st.meishij.net/rs/216/236/6434216/n6434216_150942526861583.jpg', '南瓜去皮去瓤切块', '709');
INSERT INTO `cookbook_marche` VALUES ('1113', '3', 'https://s1.st.meishij.net/rs/216/236/6434216/n6434216_150942528177298.jpg', '放锅中蒸二十分钟', '709');
INSERT INTO `cookbook_marche` VALUES ('1114', '4', 'https://s1.st.meishij.net/rs/216/236/6434216/n6434216_150942528997057.jpg', '按压成泥', '709');
INSERT INTO `cookbook_marche` VALUES ('1115', '5', 'https://s1.st.meishij.net/rs/216/236/6434216/n6434216_150942529687126.jpg', '加入糖、水、奶粉混合液搅拌均匀', '709');
INSERT INTO `cookbook_marche` VALUES ('1116', '6', 'https://s1.st.meishij.net/rs/216/236/6434216/n6434216_150942530268374.jpg', '加入酵母搅拌均匀', '709');
