INSERT INTO `cookbook_marche` VALUES ('1118', '1', 'https://s1.st.meishij.net/rs/164/48/10449664/n10449664_150937163322401.jpg', '把相同重量的糯米面和水搅拌在一起，水开后放入蒸锅蒸二十分钟，那出来晾凉。', '710');
INSERT INTO `cookbook_marche` VALUES ('1119', '2', 'https://s1.st.meishij.net/rs/164/48/10449664/n10449664_150937164437487.jpg', '取炒熟的黄豆面均匀的铺在面板上防粘，把晾凉的糯米团放在上面裹上黄豆面。', '710');
INSERT INTO `cookbook_marche` VALUES ('1120', '3', 'https://s1.st.meishij.net/rs/164/48/10449664/n10449664_150937165186242.jpg', '用擀面杖把糯米团擀成长条形状。', '710');
INSERT INTO `cookbook_marche` VALUES ('1121', '1', 'https://s1.st.meishij.net/rs/36/220/3242536/n3242536_150906816809139.jpg', '南瓜蒸熟，捣成泥', '711');
INSERT INTO `cookbook_marche` VALUES ('1122', '2', 'https://s1.st.meishij.net/rs/36/220/3242536/n3242536_150906818340191.jpg', '放入糖，酵母和中筋面粉，揉成面团', '711');
INSERT INTO `cookbook_marche` VALUES ('1123', '3', 'https://s1.st.meishij.net/rs/36/220/3242536/n3242536_150906822638575.jpg', '模具刷油，手沾水把面团取出来放入模具按压一下，放入蒸锅，冷水下锅，开火1分钟，关火，把面团放入蒸锅发酵50分钟', '711');
INSERT INTO `cookbook_marche` VALUES ('1124', '1', 'https://s1.st.meishij.net/rs/155/119/4467405/n4467405_150737444197479.jpg', '糯米粉加入水搅拌均匀', '712');
INSERT INTO `cookbook_marche` VALUES ('1125', '2', 'https://s1.st.meishij.net/rs/155/119/4467405/n4467405_150737444272283.jpg', '糯米团上锅蒸20分钟', '712');
INSERT INTO `cookbook_marche` VALUES ('1126', '3', 'https://s1.st.meishij.net/rs/155/119/4467405/n4467405_150737444352174.jpg', '封上保鲜膜晾凉', '712');
INSERT INTO `cookbook_marche` VALUES ('1127', '4', 'https://s1.st.meishij.net/rs/155/119/4467405/n4467405_150737444338386.jpg', '黄豆粉炒熟', '712');
INSERT INTO `cookbook_marche` VALUES ('1128', '5', 'https://s1.st.meishij.net/rs/155/119/4467405/n4467405_150737444419519.jpg', '面板撒上黄豆粉 放上面团', '712');
INSERT INTO `cookbook_marche` VALUES ('1129', '1', 'https://s1.st.meishij.net/rs/101/219/6242351/n6242351_150708581883753.jpg', '剥衣服就不用我交了吧（图里我已经剥了都已经捏了）', '713');
INSERT INTO `cookbook_marche` VALUES ('1130', '2', 'https://s1.st.meishij.net/rs/101/219/6242351/n6242351_150708584006186.jpg', '带上手套捏成坨坨状', '713');
INSERT INTO `cookbook_marche` VALUES ('1131', '3', 'https://s1.st.meishij.net/rs/101/219/6242351/n6242351_150708586095642.jpg', '在案板上撒大量的炒熟的黄豆面，这样才不粘', '713');
INSERT INTO `cookbook_marche` VALUES ('1132', '4', 'https://s1.st.meishij.net/rs/101/219/6242351/n6242351_150708588298412.jpg', '豆沙准备可以自己做可以买（做的时候炒豆沙有一刻豆沙会特别稀再熬两三分钟就好了）（买的太硬不好涂在粽子上加点水搅匀稀一点）', '713');
INSERT INTO `cookbook_marche` VALUES ('1133', '5', 'https://s1.st.meishij.net/rs/101/219/6242351/n6242351_150708589450776.jpg', '涂豆沙啦多少你说了算', '713');
INSERT INTO `cookbook_marche` VALUES ('1134', '1', 'https://s1.st.meishij.net/rs/42/126/5781542/n5781542_150658820565483.jpg', '紫薯切片放入碗中，倒入适量开水，微波炉三分钟，用勺子捣成泥，如果感觉不够甜，根据口味加入适量红糖搅拌。', '714');
INSERT INTO `cookbook_marche` VALUES ('1135', '2', 'https://s1.st.meishij.net/rs/42/126/5781542/n5781542_150658820855351.jpg', '自制黄豆粉。提前已经做好。黄豆洗净微波烘干，放入九阳多功能料理机打成粉，将粉放入锅中炒熟，炒至微微发焦黄，散发出强烈黄豆香味。', '714');
INSERT INTO `cookbook_marche` VALUES ('1136', '3', 'https://s1.st.meishij.net/rs/42/126/5781542/n5781542_150658821128721.jpg', '糯米粉加入开水，少许油，适量白糖搅拌。注意面糊不能太稀了。', '714');
INSERT INTO `cookbook_marche` VALUES ('1137', '4', 'https://s1.st.meishij.net/rs/42/126/5781542/n5781542_150658821409651.jpg', '放入袋中入微波炉两分钟，面团已成熟。也可放锅中蒸熟，个人感觉还是微波炉方便。将熟面团放入冰箱放凉备用。', '714');
INSERT INTO `cookbook_marche` VALUES ('1138', '1', 'https://s1.st.meishij.net/rs/87/178/5169587/n5169587_150347772808267.jpg', '红豆泡水一夜', '715');
INSERT INTO `cookbook_marche` VALUES ('1139', '2', 'https://s1.st.meishij.net/rs/87/178/5169587/n5169587_150347772909605.jpg', '然后放电饭煲里，按煮粥键煮烂，这一步不需要放太多水，煮烂之后倒到炒锅收了一下水份', '715');
INSERT INTO `cookbook_marche` VALUES ('1140', '3', 'https://s1.st.meishij.net/rs/87/178/5169587/n5169587_150347773020833.jpg', '我直接用料理机搅碎，连皮一起。喜欢细腻口感的可以过筛，过筛比较费事，每次放到晒网的量不要多哦，不然不好过', '715');
INSERT INTO `cookbook_marche` VALUES ('1141', '4', 'https://s1.st.meishij.net/rs/87/178/5169587/n5169587_150347773124959.jpg', '好啦，倒回锅里。因为我之前的红豆收的水不多了，所以现在看着已经黏黏的啦。放入油和适量红糖，我没称红糖的量，边炒边尝，吃吃真开心哈哈哈', '715');
INSERT INTO `cookbook_marche` VALUES ('1142', '5', 'https://s1.st.meishij.net/rs/87/178/5169587/n5169587_150347773378873.jpg', '好啦就拿出来', '715');
INSERT INTO `cookbook_marche` VALUES ('1143', '1', 'https://s1.st.meishij.net/rs/210/167/9979460/n9979460_150192451882007.jpg', '食材：脱皮干豌豆 200克，白糖 20克', '716');
INSERT INTO `cookbook_marche` VALUES ('1144', '2', 'https://s1.st.meishij.net/rs/210/167/9979460/n9979460_150192452258017.jpg', '干豌豆加3倍水冰箱中浸泡4个小时以上。\r\n小贴士：泡之前将一些杂豆，坏豆挑出来。柚子妈是在马云家买的脱皮干豌豆。', '716');
INSERT INTO `cookbook_marche` VALUES ('1145', '3', 'https://s1.st.meishij.net/rs/210/167/9979460/n9979460_150192452770662.jpg', '泡好的豌豆，放入锅中大火煮开，转小火20分钟。用漏筛捞出来放入料理机。\r\n小贴士：煮的时候容易扑锅，记得锅不要盖严！用漏筛很重要，把水分滤掉再打成泥。水越多炒的时间越长，但也不能一点水没有，没有水接下来没法打泥，或者打的不细腻。', '716');
INSERT INTO `cookbook_marche` VALUES ('1146', '4', 'https://s1.st.meishij.net/rs/210/167/9979460/n9979460_150192453205778.jpg', '打成泥。豌豆泥里有星点的遗漏水分，就靠这些水分打成泥，没有过多的水，靠多打1,2分钟，打的细腻。', '716');
INSERT INTO `cookbook_marche` VALUES ('1147', '5', 'https://s1.st.meishij.net/rs/210/167/9979460/n9979460_150192453682326.jpg', '豌豆茸放入锅中小火炒，将多余的水分炒干。\r\n小贴士：这样打出来的豌豆茸，已经很有形，基本不用炒。', '716');
INSERT INTO `cookbook_marche` VALUES ('1148', '1', 'https://s1.st.meishij.net/rs/123/248/5812123/n5812123_150189935242200.jpg', '先提前准备好吃面所用的菜码，将所有青菜洗净，胡萝卜切丝，芹菜切段，黄豆我是头天晚上就泡上滴。\r\n', '717');
INSERT INTO `cookbook_marche` VALUES ('1149', '2', 'https://s1.st.meishij.net/rs/123/248/5812123/n5812123_150189940156777.jpg', '烧一锅热水，将芹菜丁、胡萝卜丝和黄豆用沸水焯一下，黄瓜切丝直接食用。', '717');
INSERT INTO `cookbook_marche` VALUES ('1150', '3', 'https://s1.st.meishij.net/rs/123/248/5812123/n5812123_150189952450397.jpg', '将干黄酱提前用适量的水搅拌开。', '717');
INSERT INTO `cookbook_marche` VALUES ('1151', '1', 'https://s1.st.meishij.net/rs/47/119/5279797/n5279797_150121388424164.jpg', '备好食材', '718');
INSERT INTO `cookbook_marche` VALUES ('1152', '2', 'https://s1.st.meishij.net/rs/47/119/5279797/n5279797_150121389271114.jpg', '豌豆洗净泡一夜', '718');
INSERT INTO `cookbook_marche` VALUES ('1153', '3', 'https://s1.st.meishij.net/rs/47/119/5279797/n5279797_150121389828068.jpg', '再次冲水洗净，倒去水', '718');
INSERT INTO `cookbook_marche` VALUES ('1154', '4', 'https://s1.st.meishij.net/rs/47/119/5279797/n5279797_150121390554402.jpg', '倒入压力锅内胆，加入和豌豆齐平的水量', '718');
INSERT INTO `cookbook_marche` VALUES ('1155', '5', 'https://s1.st.meishij.net/rs/47/119/5279797/n5279797_150121391154410.jpg', '压35分钟', '718');
INSERT INTO `cookbook_marche` VALUES ('1156', '6', 'https://s1.st.meishij.net/rs/47/119/5279797/n5279797_150121391840520.jpg', '压好的豌豆软烂成泥', '718');
INSERT INTO `cookbook_marche` VALUES ('1157', '1', 'https://s1.st.meishij.net/rs/79/162/2728079/n2728079_150103472643818.jpg', '超市有做好的灌肠，切成薄片', '719');
INSERT INTO `cookbook_marche` VALUES ('1158', '2', 'https://s1.st.meishij.net/rs/79/162/2728079/n2728079_150103472821021.jpg', '锅内热油，中火，把灌肠当进入炸，炸到两面都起皮泡', '719');
INSERT INTO `cookbook_marche` VALUES ('1159', '1', 'https://s1.st.meishij.net/rs/86/63/1640836/n1640836_149865839128230.jpg', '准备面粉', '720');
INSERT INTO `cookbook_marche` VALUES ('1160', '2', 'https://s1.st.meishij.net/rs/86/63/1640836/n1640836_149865839914789.jpg', '将面粉倒入盆中，真空包装倒出来还很瓷实，没有散\r\n', '720');
INSERT INTO `cookbook_marche` VALUES ('1161', '3', 'https://s1.st.meishij.net/rs/86/63/1640836/n1640836_149865842939232.jpg', '将面粉中徐徐倒入清水和成面团，饧面40分钟左右\r\n', '720');
INSERT INTO `cookbook_marche` VALUES ('1162', '4', 'https://s1.st.meishij.net/rs/86/63/1640836/n1640836_149865845403060.jpg', '猪肉馅加入姜末 花椒粉 生抽 甜面酱 味精搅打上劲，静置30分钟\r\n', '720');
INSERT INTO `cookbook_marche` VALUES ('1163', '5', 'https://s1.st.meishij.net/rs/86/63/1640836/n1640836_149865847833935.jpg', '木耳泡发，洗净切碎', '720');
INSERT INTO `cookbook_marche` VALUES ('1164', '6', 'https://s1.st.meishij.net/rs/86/63/1640836/n1640836_149865850424089.jpg', '大葱洗净，切碎', '720');
INSERT INTO `cookbook_marche` VALUES ('1165', '7', 'https://s1.st.meishij.net/rs/86/63/1640836/n1640836_149865852072063.jpg', '将肉馅加入适量食用油搅打均匀加入适量盐调味，最后倒入葱末和木耳碎，将香油倒在葱末上拌匀后与肉混合\r\n', '720');
INSERT INTO `cookbook_marche` VALUES ('1166', '8', 'https://s1.st.meishij.net/rs/86/63/1640836/n1640836_149865853336113.jpg', '搅打均匀，馅和好了', '720');
INSERT INTO `cookbook_marche` VALUES ('1167', '1', 'https://s1.st.meishij.net/rs/34/213/8553284/n8553284_149780699919562.jpg', '将牛奶或者等量的水分次倒入糯米粉中，直至可以揉成面团', '721');
INSERT INTO `cookbook_marche` VALUES ('1168', '2', 'https://s1.st.meishij.net/rs/34/213/8553284/n8553284_149780700744771.jpg', '热水上锅蒸15-20分钟', '721');
INSERT INTO `cookbook_marche` VALUES ('1169', '3', 'https://s1.st.meishij.net/rs/34/213/8553284/n8553284_149780701534716.jpg', '将熟黄豆粉均匀筛在砧板上', '721');
INSERT INTO `cookbook_marche` VALUES ('1170', '4', 'https://s1.st.meishij.net/rs/34/213/8553284/n8553284_149780702245193.jpg', '趁热擀成长方形，小心不要被烫到哦！（忽略我这个有点手残）', '721');
INSERT INTO `cookbook_marche` VALUES ('1171', '1', 'https://s1.st.meishij.net/rs/165/229/3682415/n3682415_149770352104431.jpg', '红豆提前一夜泡发，锅中加入较多的水，煮半小时左右，至红豆软，有高压锅也可用高压锅直接压。另取锅，加入适量花生油，加入红豆，黑糖，炒至黑糖融化，红豆沙稠度适宜。', '722');
INSERT INTO `cookbook_marche` VALUES ('1172', '2', 'https://s1.st.meishij.net/rs/165/229/3682415/n3682415_149770352387140.jpg', '糯米粉多次慢慢添加温水，调成糯米浆，糯米浆倒入浅盘如蒸锅，蒸15分钟。', '722');
INSERT INTO `cookbook_marche` VALUES ('1173', '3', 'https://s1.st.meishij.net/rs/165/229/3682415/n3682415_149770352324549.jpg', '将炒好的红豆粒放到这种细筛中，用勺子不断碾压，分离出红豆皮。', '722');
INSERT INTO `cookbook_marche` VALUES ('1174', '1', 'https://s1.st.meishij.net/rs/70/134/9908570/n9908570_149440203152323.jpeg', '糯米粉加水', '723');
INSERT INTO `cookbook_marche` VALUES ('1175', '2', 'https://s1.st.meishij.net/rs/70/134/9908570/n9908570_149440204031009.jpeg', '用筷子搅拌均匀，没有干粉', '723');
INSERT INTO `cookbook_marche` VALUES ('1176', '3', 'https://s1.st.meishij.net/rs/70/134/9908570/n9908570_149440204909060.jpeg', '另外准备一个干净的碗，抹点油，方便糯米粉后期取出来，放入锅中，大火蒸15-20分钟', '723');
INSERT INTO `cookbook_marche` VALUES ('1177', '4', 'https://s1.st.meishij.net/rs/70/134/9908570/n9908570_149440206050530.jpeg', '蒸好后取出，趁热用筷子搅拌滑顺，盖保鲜膜放置温热', '723');
INSERT INTO `cookbook_marche` VALUES ('1178', '1', 'https://s1.st.meishij.net/rs/53/149/2912303/n2912303_148816051308359.jpg', '准备食材', '724');
INSERT INTO `cookbook_marche` VALUES ('1179', '2', 'https://s1.st.meishij.net/rs/53/149/2912303/n2912303_148816051521027.jpg', '面粉加适量白糖，适量温水（体温差不多温度）和面', '724');
INSERT INTO `cookbook_marche` VALUES ('1180', '3', 'https://s1.st.meishij.net/rs/53/149/2912303/n2912303_148816052062767.jpg', '和成不沾手的面团就可以', '724');
INSERT INTO `cookbook_marche` VALUES ('1181', '4', 'https://s1.st.meishij.net/rs/53/149/2912303/n2912303_148816052260091.jpg', '容器底部抹油，将面团放入，表面盖保鲜膜', '724');
INSERT INTO `cookbook_marche` VALUES ('1182', '5', 'https://s1.st.meishij.net/rs/53/149/2912303/n2912303_148816052400085.jpg', '上汽至少蒸30分钟，面团很大，不容易熟，用筷子扎眼检验面团是否熟了', '724');
INSERT INTO `cookbook_marche` VALUES ('1183', '1', 'https://s1.st.meishij.net/rs/229/01/4875479/n4875479_148663763969039.jpg', '黄豆面在锅里炒熟', '725');
INSERT INTO `cookbook_marche` VALUES ('1184', '2', 'https://s1.st.meishij.net/rs/229/01/4875479/n4875479_148663764358838.jpg', '糯米粉和奶粉混合好，加清水搅拌均匀', '725');
INSERT INTO `cookbook_marche` VALUES ('1185', '3', 'https://s1.st.meishij.net/rs/229/01/4875479/n4875479_148663764717176.jpg', '烤盘刷一层油，放入蒸锅里小火蒸20分钟至粉浆不再流动即可，蒸好后取出冷却', '725');
INSERT INTO `cookbook_marche` VALUES ('1186', '1', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656079995946.jpg', '揉面的时候，不需要发酵，但面一定要硬。', '726');
INSERT INTO `cookbook_marche` VALUES ('1187', '2', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656087142490.jpg', '像这样有些干皮都可以，因为揉好后需要盖上布醒发半小时，如果水多了面团本身就软，那做出来的小面疙瘩特别爱粘。', '726');
INSERT INTO `cookbook_marche` VALUES ('1188', '3', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656090684748.jpg', '取出来后擀开，擀成1厘米左右厚度的面片。', '726');
INSERT INTO `cookbook_marche` VALUES ('1189', '4', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656093999136.jpg', '把面片切成宽度也是1厘米的小面条，接着切成小方疙瘩。切的过程中，面片地下要放适量薄粉。', '726');
INSERT INTO `cookbook_marche` VALUES ('1190', '5', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656097118855.jpg', '接着开始“盆盆颠”，要是家里只有一个盆，那就放少量疙瘩，左右晃动，放多了很容易颠出去。', '726');
INSERT INTO `cookbook_marche` VALUES ('1191', '6', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_148656099964463.jpg', '锅内烧开水，下面疙瘩，等水再次沸腾后，再煮1分钟。', '726');
INSERT INTO `cookbook_marche` VALUES ('1192', '1', 'https://s1.st.meishij.net/rs/155/126/5469155/n5469155_148473033201752.jpg', '无油无水的锅里放入干的黄豆，小火炒5分钟左右（我这里炒的是250克份量）', '727');
INSERT INTO `cookbook_marche` VALUES ('1193', '2', 'https://s1.st.meishij.net/rs/155/126/5469155/n5469155_148473041811876.jpg', '炒好的黄豆装盆子散热\r\n', '727');
INSERT INTO `cookbook_marche` VALUES ('1194', '3', 'https://s1.st.meishij.net/rs/155/126/5469155/n5469155_148473052269465.jpg', '把黄豆放破壁机里打粉，考虑到让孩子多吃粗粮，打好的黄豆面我没过筛，喜欢口感细腻一点的可以过筛', '727');
INSERT INTO `cookbook_marche` VALUES ('1195', '4', 'https://s1.st.meishij.net/rs/155/126/5469155/n5469155_148473073550890.jpg', '把所有材料分别称出来，糯米粉是自己磨的，所以看起来不像买的那么白', '727');
INSERT INTO `cookbook_marche` VALUES ('1196', '5', 'https://s1.st.meishij.net/rs/155/126/5469155/n5469155_148473081331582.jpg', '干净的盆子里放入180克水，10克糖，15克植物油，搅拌到水里有细小的油星\r\n', '727');
INSERT INTO `cookbook_marche` VALUES ('1197', '6', 'https://s1.st.meishij.net/rs/155/126/5469155/n5469155_148473091659372.jpg', '放入糯米粉和粘米粉', '727');
INSERT INTO `cookbook_marche` VALUES ('1198', '7', 'https://s1.st.meishij.net/rs/155/126/5469155/n5469155_148473102643308.jpg', '把粉和水搅拌均匀到没有颗粒\r\n', '727');
INSERT INTO `cookbook_marche` VALUES ('1199', '8', 'https://s1.st.meishij.net/rs/155/126/5469155/n5469155_148473113262771.jpg', '盖上保鲜膜静置20分钟（保鲜膜的作用是防止灰尘掉面糊里和蒸的时候没有水滴入面糊）\r\n', '727');
INSERT INTO `cookbook_marche` VALUES ('1200', '1', 'https://s1.st.meishij.net/rs/138/164/8978638/n8978638_148306063597841.jpg', '芝麻酱加碾碎的红糖和香油搅拌均匀，再加10g水搅拌成粘稠状；', '728');
INSERT INTO `cookbook_marche` VALUES ('1201', '2', 'https://s1.st.meishij.net/rs/138/164/8978638/n8978638_148306063975701.jpg', '80克温水加酵母和主料中的细砂糖化开，待表面起密集泡沫时加入全麦粉，活成光滑面团；', '728');
INSERT INTO `cookbook_marche` VALUES ('1202', '3', 'https://s1.st.meishij.net/rs/138/164/8978638/n8978638_148306064426123.jpg', '放在温暖处发酵至原来两倍大左右；', '728');
INSERT INTO `cookbook_marche` VALUES ('1203', '4', 'https://s1.st.meishij.net/rs/138/164/8978638/n8978638_148306064821095.jpg', '面团排气，分成六等份搓圆，同时芝麻酱也分成六等份搓圆；', '728');
INSERT INTO `cookbook_marche` VALUES ('1204', '5', 'https://s1.st.meishij.net/rs/138/164/8978638/n8978638_148306065340385.jpg', '取一块面团，放入芝麻酱团；', '728');
INSERT INTO `cookbook_marche` VALUES ('1205', '6', 'https://s1.st.meishij.net/rs/138/164/8978638/n8978638_148306065769536.jpg', '包好收口朝下放置；', '728');
INSERT INTO `cookbook_marche` VALUES ('1206', '7', 'https://s1.st.meishij.net/rs/138/164/8978638/n8978638_148306066147833.jpg', '擀成椭圆长条；', '728');
INSERT INTO `cookbook_marche` VALUES ('1207', '8', 'https://s1.st.meishij.net/rs/138/164/8978638/n8978638_148306066614670.jpg', '从一端卷起来至另一端，收口朝下；', '728');
INSERT INTO `cookbook_marche` VALUES ('1208', '1', 'https://s1.st.meishij.net/rs/46/131/5032796/n5032796_148301956059049.jpg', '面条', '729');
INSERT INTO `cookbook_marche` VALUES ('1209', '2', 'https://s1.st.meishij.net/rs/46/131/5032796/n5032796_148301956357140.jpg', '锅里烧水煮面', '729');
INSERT INTO `cookbook_marche` VALUES ('1210', '3', 'https://s1.st.meishij.net/rs/46/131/5032796/n5032796_148301956531328.jpg', '面用凉水过一下，捞出控干水份', '729');
INSERT INTO `cookbook_marche` VALUES ('1211', '4', 'https://s1.st.meishij.net/rs/46/131/5032796/n5032796_148301956764812.jpg', '油烧热', '729');
INSERT INTO `cookbook_marche` VALUES ('1212', '5', 'https://s1.st.meishij.net/rs/46/131/5032796/n5032796_148301956916649.jpg', '放上葱姜蒜爆香，放肉沫', '729');
INSERT INTO `cookbook_marche` VALUES ('1213', '1', 'https://s1.st.meishij.net/rs/101/104/1588601/n1588601_148298110757466.jpg', '黄豆锅子炒熟，', '730');
INSERT INTO `cookbook_marche` VALUES ('1214', '2', 'https://s1.st.meishij.net/rs/101/104/1588601/n1588601_148298110917643.jpg', '用料理机打成粉，多打几遍。', '730');
INSERT INTO `cookbook_marche` VALUES ('1215', '3', 'https://s1.st.meishij.net/rs/101/104/1588601/n1588601_148298111125617.jpg', '糯米粉加入清水，揉成团。', '730');
INSERT INTO `cookbook_marche` VALUES ('1216', '4', 'https://s1.st.meishij.net/rs/101/104/1588601/n1588601_148298111444207.jpg', '将面团整平，放入盘内，盖上盖子，放入美的早餐机内，选择蒸功能，大约25分钟。', '730');
INSERT INTO `cookbook_marche` VALUES ('1217', '5', 'https://s1.st.meishij.net/rs/101/104/1588601/n1588601_148298111797432.jpg', '蒸好的糯米团，稍微冷却，不要太凉，放在铺了保鲜膜的台面上，表面再盖一层保鲜膜，用擀面杖慢慢擀成长方形面片。', '730');
INSERT INTO `cookbook_marche` VALUES ('1218', '1', 'https://s1.st.meishij.net/rs/160/125/31410/n31410_148297983866707.jpg', '准备原料', '731');
INSERT INTO `cookbook_marche` VALUES ('1219', '2', 'https://s1.st.meishij.net/rs/160/125/31410/n31410_148297984245327.jpg', '杏仁用清水事先浸泡，中途换水几次', '731');
INSERT INTO `cookbook_marche` VALUES ('1220', '3', 'https://s1.st.meishij.net/rs/160/125/31410/n31410_148297984518678.jpg', '将杏仁的皮剥掉，漂洗干净', '731');
INSERT INTO `cookbook_marche` VALUES ('1221', '4', 'https://s1.st.meishij.net/rs/160/125/31410/n31410_148297984858402.jpg', '琼脂用清水浸泡', '731');
INSERT INTO `cookbook_marche` VALUES ('1222', '5', 'https://s1.st.meishij.net/rs/160/125/31410/n31410_148297985119664.jpg', '杏仁倒入美的破壁机里，加入清水', '731');
INSERT INTO `cookbook_marche` VALUES ('1223', '6', 'https://s1.st.meishij.net/rs/160/125/31410/n31410_148297985413142.jpg', '选择果蔬功能，工作', '731');
INSERT INTO `cookbook_marche` VALUES ('1224', '7', 'https://s1.st.meishij.net/rs/160/125/31410/n31410_148297985729086.jpg', '搅打均匀成杏仁露', '731');
INSERT INTO `cookbook_marche` VALUES ('1225', '1', 'https://s1.st.meishij.net/rs/32/113/8903282/n8903282_148176427375073.jpg', '往盆里倒入一定量的糯米粉', '732');
INSERT INTO `cookbook_marche` VALUES ('1226', '2', 'https://s1.st.meishij.net/rs/32/113/8903282/n8903282_148176432937167.jpg', '一边往盆里加凉白开一边用筷子搅拌，水倒完了糯米粉也就搅拌成团状了', '732');
INSERT INTO `cookbook_marche` VALUES ('1227', '3', 'https://s1.st.meishij.net/rs/32/113/8903282/n8903282_148176439219950.jpg', '准备一个盘子，里面刷一层油。把糯米团平铺到盘子上面去', '732');
INSERT INTO `cookbook_marche` VALUES ('1228', '4', 'https://s1.st.meishij.net/rs/32/113/8903282/n8903282_148176447365486.jpg', '上锅蒸15---20分钟，一定要蒸熟了。', '732');
INSERT INTO `cookbook_marche` VALUES ('1229', '5', 'https://s1.st.meishij.net/rs/32/113/8903282/n8903282_148176459184631.jpg', '这个功夫呢小火慢炒生的黄豆粉，同时把白砂糖加进去一起炒。炒到变颜色了就说明炒熟了', '732');
INSERT INTO `cookbook_marche` VALUES ('1230', '6', 'https://s1.st.meishij.net/rs/32/113/8903282/n8903282_148176461988346.jpg', '红豆粉由黄色变成这样的褐色了就可以了', '732');
INSERT INTO `cookbook_marche` VALUES ('1231', '1', 'https://s1.st.meishij.net/rs/183/67/8454433/n8454433_148004796909549.jpg', '将新鲜的红果用流动水冲洗干净，为了好看，用管状圆柱剔除柄、根底及核。', '733');
INSERT INTO `cookbook_marche` VALUES ('1232', '2', 'https://s1.st.meishij.net/rs/183/67/8454433/n8454433_148004798310186.jpg', '把加工好的红果肉放入汤锅中，加入等量的纯净水和约1/3量的黄冰糖，大火烧沸后转小火继续熬煮，其间不要加水，需不时用汤勺翻搅以防糊底。', '733');
INSERT INTO `cookbook_marche` VALUES ('1233', '1', 'https://s1.st.meishij.net/rs/80/157/6414330/n6414330_147653077887635.jpg', '准备食材，黄花，木耳，香菇，腐竹泡发洗净待用，鸡丝，鸡汤，鸡蛋，葱姜碎，酱油，盐备齐', '734');
INSERT INTO `cookbook_marche` VALUES ('1234', '2', 'https://s1.st.meishij.net/rs/80/157/6414330/n6414330_147653078588620.jpg', '热锅凉油爆香葱姜末', '734');
INSERT INTO `cookbook_marche` VALUES ('1235', '3', 'https://s1.st.meishij.net/rs/80/157/6414330/n6414330_147653079134930.jpg', '然后下入食材炒匀，加入酱油，生抽和盐', '734');
INSERT INTO `cookbook_marche` VALUES ('1236', '4', 'https://s1.st.meishij.net/rs/80/157/6414330/n6414330_147653079644286.jpg', '然后倒入鸡汤，添加适量水，开锅倒入水淀粉勾芡', '734');
INSERT INTO `cookbook_marche` VALUES ('1237', '1', 'https://s1.st.meishij.net/rs/162/173/6668412/n6668412_147643360711866.jpg', '准备食材：里脊肉：500g，白胡椒：1小勺，料酒：2勺，盐：2克，鸡蛋：1个，甜面酱：5勺，酱油：2勺，番茄酱：2勺，糖：10g ，淀粉：少许。将清洗干净的里脊肉切丝，加少许盐，白胡椒，料酒腌制入底味。', '735');
INSERT INTO `cookbook_marche` VALUES ('1238', '2', 'https://s1.st.meishij.net/rs/162/173/6668412/n6668412_147643364723844.jpg', '准备食材：豆腐皮：适量。豆腐皮清洗干净切成大小适中的片入锅蒸制，开锅5分钟。取出后，在凉开水中浸泡备用，以防豆皮变干。', '735');
INSERT INTO `cookbook_marche` VALUES ('1239', '3', 'https://s1.st.meishij.net/rs/162/173/6668412/n6668412_147643376469561.jpg', '准备食材：黄瓜：适量，大葱：适量，姜：1小块。', '735');
INSERT INTO `cookbook_marche` VALUES ('1240', '4', 'https://s1.st.meishij.net/rs/162/173/6668412/n6668412_147643382389869.jpg', '取一部分葱、姜切成丝泡水备用。', '735');
INSERT INTO `cookbook_marche` VALUES ('1241', '5', 'https://s1.st.meishij.net/rs/162/173/6668412/n6668412_147643388948874.jpg', '所有准备工作作好后，把事先腌制好的肉丝加一个鸡蛋和适量干淀粉抓匀。', '735');
INSERT INTO `cookbook_marche` VALUES ('1242', '6', 'https://s1.st.meishij.net/rs/162/173/6668412/n6668412_147643392248472.jpg', '锅中倒油，五成热倒入肉丝滑炒。肉丝入锅迅速打散后就不要急于翻炒了，待挂的浆稍稍固定一下，再进行翻动，这样就不至于脱浆了。肉丝炒至变白色，说明已经至少八成熟。捞出控油备用。', '735');
INSERT INTO `cookbook_marche` VALUES ('1243', '7', 'https://s1.st.meishij.net/rs/162/173/6668412/n6668412_147643397273084.jpg', '将黄瓜、大葱切丝。', '735');
INSERT INTO `cookbook_marche` VALUES ('1244', '1', 'https://s1.st.meishij.net/rs/202/67/4766952/n4766952_147469844781862.jpg', '豌豆晚上泡一晚', '736');
INSERT INTO `cookbook_marche` VALUES ('1245', '2', 'https://s1.st.meishij.net/rs/202/67/4766952/n4766952_147469850739738.jpg', '蒸熟，一定要蒸透，这样才细腻', '736');
INSERT INTO `cookbook_marche` VALUES ('1246', '1', 'https://s1.st.meishij.net/rs/78/155/8413828/n8413828_147435076946366.jpg', '先将花生米炒熟，炒香，然后去皮待用。', '737');
INSERT INTO `cookbook_marche` VALUES ('1247', '2', 'https://s1.st.meishij.net/rs/78/155/8413828/n8413828_147435077353368.jpg', '将黑白芝麻一起炒出香味即可', '737');
INSERT INTO `cookbook_marche` VALUES ('1248', '3', 'https://s1.st.meishij.net/rs/78/155/8413828/n8413828_147435077714971.jpg', '将干米饭直接放在炒锅里炒至颜色变黄，能闻到面粉自有的香味即可。', '737');
INSERT INTO `cookbook_marche` VALUES ('1249', '1', 'https://s1.st.meishij.net/rs/232/89/2459982/n2459982_147348658239234.jpg', '西葫芦擦成丝', '738');
