INSERT INTO `cookbook_marche` VALUES ('1252', '1', 'https://s1.st.meishij.net/rs/176/16/3879176/n3879176_147248151895043.jpg', '将面粉中加入白糖，鸡蛋，油，再加入水合成光滑的面团。用擀面仗或者是用压面机将面团压成薄片', '739');
