INSERT INTO `cookbook_marche` VALUES ('1256', '1', 'https://s1.st.meishij.net/rs/113/214/3928613/n3928613_147220618047624.jpg', ' 先制作黄豆面，黄豆洗干净放入面包机中启动翻炒干活程序，45分钟后黄豆就炒熟炒香。\r\n', '740');
