INSERT INTO `cookbook_marche` VALUES ('1259', '4', 'https://s1.st.meishij.net/rs/113/214/3928613/n3928613_147220622327419.jpg', ' 糯米提前泡3-4小时左右。\r\n', '740');
INSERT INTO `cookbook_marche` VALUES ('1260', '5', 'https://s1.st.meishij.net/rs/113/214/3928613/n3928613_147220623609087.jpg', '泡好的糯米沥干水，再重新加入水放入面包机当中。（水量仅供参考，根据糯米泡发时的吸水性自行调节加水量。）\r\n', '740');
