INSERT INTO `cookbook_marche` VALUES ('1266', '4', 'https://s1.st.meishij.net/rs/113/188/5984613/n5984613_146484173097674.jpg', '将黄豆面放入无油无水的锅中，小火不停翻炒至颜色变深，并能闻到明显的豆香味即可，约5分钟，炒好的黄豆面晾凉过筛备用', '742');
