INSERT INTO `cookbook_marche` VALUES ('1335', '6', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_146175439047274.jpg', '将胡萝卜用擦板擦细丝，放入1克食盐腌制10分钟；', '754');
INSERT INTO `cookbook_marche` VALUES ('1336', '1', 'https://s1.st.meishij.net/rs/194/87/271944/n271944_146172699232464.jpg', '新鲜花椒芽洗净，控干水分，鸡蛋打成蛋液，加入盐混合均匀', '755');
INSERT INTO `cookbook_marche` VALUES ('1337', '2', 'https://s1.st.meishij.net/rs/194/87/271944/n271944_146172701737817.jpg', '花椒芽放入鸡蛋液中，裹匀蛋液', '755');
INSERT INTO `cookbook_marche` VALUES ('1338', '1', 'https://s1.st.meishij.net/rs/190/52/2888190/n2888190_146164838571047.jpg', '糯米粉放上水和白糖，和成稀糊，水多点没关系', '756');
INSERT INTO `cookbook_marche` VALUES ('1339', '2', 'https://s1.st.meishij.net/rs/190/52/2888190/n2888190_146164838885674.jpg', '蜜枣切成小块', '756');
INSERT INTO `cookbook_marche` VALUES ('1340', '3', 'https://s1.st.meishij.net/rs/190/52/2888190/n2888190_146164839147754.jpg', '不粘锅放上10g黄油', '756');
INSERT INTO `cookbook_marche` VALUES ('1341', '1', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146154590460241.jpg', '网购的去皮干豌豆', '757');
INSERT INTO `cookbook_marche` VALUES ('1342', '2', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146154591260606.jpg', '加入3倍的水量，提前浸泡过夜', '757');
INSERT INTO `cookbook_marche` VALUES ('1343', '3', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146154592099120.jpg', '这是浸泡好的豌豆', '757');
INSERT INTO `cookbook_marche` VALUES ('1344', '4', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146154592778475.jpg', '放入电压力锅里加适量清水压20分钟，水量刚浸没豌豆就好', '757');
INSERT INTO `cookbook_marche` VALUES ('1345', '5', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146154593545147.jpg', '这是压好的豌豆，我的水放得偏少', '757');
INSERT INTO `cookbook_marche` VALUES ('1346', '6', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146154594179092.jpg', '把豌豆放入搅拌机中搅打成豌豆泥', '757');
INSERT INTO `cookbook_marche` VALUES ('1347', '1', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146154626175550.jpg', '黄豆用料理机粉碎，放入锅中炒熟，一定要用中小火，否则会糊的。', '758');
INSERT INTO `cookbook_marche` VALUES ('1348', '2', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146154699894271.jpg', '糯米放入料理机打碎成糯米粉，准备好清水。', '758');
INSERT INTO `cookbook_marche` VALUES ('1349', '3', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146154712595911.jpg', '将清水缓缓倒入糯米粉，搅拌均匀，做成一个湿润绵软的面团，或者调成糊状也行，总之水分要充足。', '758');
INSERT INTO `cookbook_marche` VALUES ('1350', '4', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146154717324190.jpg', '入蒸锅蒸30分钟。', '758');
INSERT INTO `cookbook_marche` VALUES ('1351', '5', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146154720152741.jpg', '蒸熟后取出晾凉装进保鲜袋（用保鲜膜也行）。', '758');
INSERT INTO `cookbook_marche` VALUES ('1352', '6', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146154727858580.jpg', '擀成均匀的大片。', '758');
INSERT INTO `cookbook_marche` VALUES ('1353', '7', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146154732378933.jpg', '撕掉上层的保鲜袋。', '758');
INSERT INTO `cookbook_marche` VALUES ('1354', '1', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146149581648221.jpg', '面粉，芝麻和盐混合在一起。', '759');
INSERT INTO `cookbook_marche` VALUES ('1355', '2', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146149625654861.jpg', '缓缓加入清水，用筷子搅拌成絮状。', '759');
INSERT INTO `cookbook_marche` VALUES ('1356', '3', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146149629412381.jpg', '用手揉成一个三光面团，盖上盖子静置一会儿。', '759');
INSERT INTO `cookbook_marche` VALUES ('1357', '4', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146149634836551.jpg', '将面团擀成薄薄的大片。', '759');
INSERT INTO `cookbook_marche` VALUES ('1358', '5', 'https://s1.st.meishij.net/rs/14/171/5480264/n5480264_146149636580309.jpg', '切成小块。', '759');
INSERT INTO `cookbook_marche` VALUES ('1359', '1', 'https://s1.st.meishij.net/rs/237/117/2279487/n2279487_146148082290521.jpg', '把粉倒在盆中。', '760');
INSERT INTO `cookbook_marche` VALUES ('1360', '2', 'https://s1.st.meishij.net/rs/237/117/2279487/n2279487_146148082417420.jpg', '分两次倒入水，先倒入大半。', '760');
INSERT INTO `cookbook_marche` VALUES ('1361', '3', 'https://s1.st.meishij.net/rs/237/117/2279487/n2279487_146148082616775.jpg', '用手把面粉搅拌一下，再加剩下的水揉成团。', '760');
INSERT INTO `cookbook_marche` VALUES ('1362', '4', 'https://s1.st.meishij.net/rs/237/117/2279487/n2279487_146148082864418.jpg', '面团比较软不要紧哦！', '760');
INSERT INTO `cookbook_marche` VALUES ('1363', '5', 'https://s1.st.meishij.net/rs/237/117/2279487/n2279487_146148083076684.jpg', '加入少许橄榄油。', '760');
INSERT INTO `cookbook_marche` VALUES ('1364', '6', 'https://s1.st.meishij.net/rs/237/117/2279487/n2279487_146148083237836.jpg', '用手揉入面中。', '760');
INSERT INTO `cookbook_marche` VALUES ('1365', '7', 'https://s1.st.meishij.net/rs/237/117/2279487/n2279487_146148083447544.jpg', '看下面提起来这样的。\r\n', '760');
INSERT INTO `cookbook_marche` VALUES ('1366', '8', 'https://s1.st.meishij.net/rs/237/117/2279487/n2279487_146148083659458.jpg', '拿起面团往盆里摔面，大概四五十下。（目的让面团光滑）', '760');
INSERT INTO `cookbook_marche` VALUES ('1367', '9', 'https://s1.st.meishij.net/rs/237/117/2279487/n2279487_146148083756633.jpg', '在面团上刷上油包好保鲜膜醒半小时。', '760');
INSERT INTO `cookbook_marche` VALUES ('1368', '1', 'https://s1.st.meishij.net/rs/06/24/5818506/n5818506_146065228998719.jpg', '准备主要食材', '761');
INSERT INTO `cookbook_marche` VALUES ('1369', '2', 'https://s1.st.meishij.net/rs/06/24/5818506/n5818506_146065230901452.png', '将酵母粉2克放入温水中，搅拌溶解', '761');
INSERT INTO `cookbook_marche` VALUES ('1370', '3', 'https://s1.st.meishij.net/rs/06/24/5818506/n5818506_146065232649436.png', '将搅拌好的溶液倒入面粉中，一边倒一边用筷子搅拌', '761');
INSERT INTO `cookbook_marche` VALUES ('1371', '4', 'https://s1.st.meishij.net/rs/06/24/5818506/n5818506_146065235114886.png', '搅匀成棉絮状', '761');
INSERT INTO `cookbook_marche` VALUES ('1372', '5', 'https://s1.st.meishij.net/rs/06/24/5818506/n5818506_146065238588643.png', '接着用手将其抓揉成团，再反复揉压成表面光滑的面团', '761');
INSERT INTO `cookbook_marche` VALUES ('1373', '6', 'https://s1.st.meishij.net/rs/06/24/5818506/n5818506_146065242033683.png', '将揉好的面团盖上盖子或湿纱布，放入烤箱开启发酵功能大约1小时，使面团发酵至2.5倍大', '761');
INSERT INTO `cookbook_marche` VALUES ('1374', '7', 'https://s1.st.meishij.net/rs/06/24/5818506/n5818506_146142412928531.jpg', '猪肉切片用料理机搅打成馅', '761');
INSERT INTO `cookbook_marche` VALUES ('1375', '8', 'https://s1.st.meishij.net/rs/06/24/5818506/n5818506_146065249992960.png', '搅好的猪肉馅放碗内待用', '761');
INSERT INTO `cookbook_marche` VALUES ('1376', '9', 'https://s1.st.meishij.net/rs/06/24/5818506/n5818506_146065252883264.png', '另取一个碗，将浓汤宝放入碗内用开水融化成鸡汤', '761');
INSERT INTO `cookbook_marche` VALUES ('1377', '10', 'https://s1.st.meishij.net/rs/06/24/5818506/n5818506_146065255328217.png', '将汤汁分次倒入肉馅中，一边倒一边用筷子朝一个方向搅匀上劲', '761');
INSERT INTO `cookbook_marche` VALUES ('1378', '1', 'https://s1.st.meishij.net/rs/238/58/5514738/n5514738_146115413571651.jpg', '用７０度水烫面，和成面团饬十分钟', '762');
INSERT INTO `cookbook_marche` VALUES ('1379', '2', 'https://s1.st.meishij.net/rs/238/58/5514738/n5514738_146115414074144.jpg', '韭菜切碎待用', '762');
INSERT INTO `cookbook_marche` VALUES ('1380', '3', 'https://s1.st.meishij.net/rs/238/58/5514738/n5514738_146115414447964.jpg', '鸡蛋炒熟待用', '762');
INSERT INTO `cookbook_marche` VALUES ('1381', '4', 'https://s1.st.meishij.net/rs/238/58/5514738/n5514738_146115414824260.jpg', '下韭菜调馅', '762');
INSERT INTO `cookbook_marche` VALUES ('1382', '5', 'https://s1.st.meishij.net/rs/238/58/5514738/n5514738_146115415224657.jpg', '馅调好了，待用', '762');
INSERT INTO `cookbook_marche` VALUES ('1383', '6', 'https://s1.st.meishij.net/rs/238/58/5514738/n5514738_146115415695640.jpg', '面皮同饺子皮一样大', '762');
INSERT INTO `cookbook_marche` VALUES ('1384', '1', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_146113814597799.jpg', '准备好食材：猪大肠1000克、猪肺1000克、五花肉250克、炸豆腐250克、面粉300克、清水1340克（其中和面用140克）、葱50克、姜25克、蒜20克、料酒50克、生抽15克、老抽20克、黄酱20克、酱豆腐一块、腐乳汁10克、盐17克（10克盐用于洗猪肠，和面用2克盐、其余放汤里）、花椒大料香叶小茴香适量、香菜30克、糖10克、醋适量。', '763');
INSERT INTO `cookbook_marche` VALUES ('1385', '2', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_146113816462920.jpg', '首先把猪肠放在盆里加入醋、花椒和盐抓搓清洗干净，注意翻过来也要如此洗一遍，然后摘去多余的肥油，肥油不要摘光，稍微留一些，不然不好吃，洗好以后再把肠子翻回来，光滑那面冲外。', '763');
INSERT INTO `cookbook_marche` VALUES ('1386', '3', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_146113817056050.jpg', '烧一锅水，水中加入20克料酒和葱姜。', '763');
INSERT INTO `cookbook_marche` VALUES ('1387', '4', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_146113826126279.jpg', '然后把洗好的猪肠子放入锅中煮开，开锅后煮2分钟就捞出来控干水分备用。', '763');
INSERT INTO `cookbook_marche` VALUES ('1388', '5', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_146113826790447.jpg', '再把五花肉放进去煮5分钟捞出来备用。', '763');
INSERT INTO `cookbook_marche` VALUES ('1389', '6', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_146113832334697.jpg', '最后把猪肺放进去煮10分钟，注意煮猪肺时要看着锅不停地把浮沫撇出来，煮好之后捞出来控干水分备用。', '763');
INSERT INTO `cookbook_marche` VALUES ('1390', '7', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_146113833277629.jpg', '葱切大段，姜切片，煮好的猪肺切大块，猪肠分成几截、花椒大料香叶小茴香都装在调料盒内。', '763');
INSERT INTO `cookbook_marche` VALUES ('1391', '8', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_146113834077525.jpg', '取一个大腕，放入30克料酒、15克生抽、20克老抽、一块酱豆腐和10克腐乳汁、10克白糖、5克盐、20克黄酱搅匀。', '763');
INSERT INTO `cookbook_marche` VALUES ('1392', '9', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_146113835029991.jpg', '首先把葱姜和调料盒放入锅中。', '763');
INSERT INTO `cookbook_marche` VALUES ('1393', '10', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_146113840626652.jpg', ' 然后把五花肉、猪肺和猪肠放进锅里。', '763');
INSERT INTO `cookbook_marche` VALUES ('1394', '11', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_146113841983675.jpg', '再把料汁倒进去。', '763');
INSERT INTO `cookbook_marche` VALUES ('1395', '1', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146106076649392.jpg', '糯米粉中慢慢加入水，和成面团。很粘，建议带手套', '764');
INSERT INTO `cookbook_marche` VALUES ('1396', '2', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146106077653142.jpg', '在盘子上抹油，把面团放在上面，大火蒸20分钟', '764');
INSERT INTO `cookbook_marche` VALUES ('1397', '3', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146106078820356.jpg', '另外取糯米粉用小火炒至微黄色，有黄豆面就用黄豆面', '764');
INSERT INTO `cookbook_marche` VALUES ('1398', '4', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146106080250402.jpg', '炒熟的糯米粉先铺一层在垫子上', '764');
INSERT INTO `cookbook_marche` VALUES ('1399', '5', 'https://s1.st.meishij.net/rs/246/109/4089996/n4089996_146106081404213.jpg', '蒸熟的糯米团放在上面', '764');
INSERT INTO `cookbook_marche` VALUES ('1400', '1', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_146105883650540.jpg', '准备原料，韭菜摘洗干净；', '765');
INSERT INTO `cookbook_marche` VALUES ('1401', '2', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_146105884033098.jpg', '一点点加入温水（水的温度为手感不烫手的程度），水的流量就像水管滴水的速度，一边加水一边用筷子搅拌，将没有沾到水的玉米面及时搅拌沾上水；', '765');
INSERT INTO `cookbook_marche` VALUES ('1402', '3', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_146105884242981.jpg', '判断是否搅拌好的玉米面，用手能攥成团，这样的程度就行了；', '765');
INSERT INTO `cookbook_marche` VALUES ('1403', '4', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_146105884354958.jpg', '将鸡蛋加食盐打散，倒入温热的油锅内，用筷子搅散，取出晾凉备用；', '765');
INSERT INTO `cookbook_marche` VALUES ('1404', '1', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_146071069492071.jpg', '脱皮豌豆提前一晚泡水', '766');
INSERT INTO `cookbook_marche` VALUES ('1405', '2', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_146071071409166.jpg', '泡好的豌豆洗净 拣出没有脱干净的豌豆皮', '766');
INSERT INTO `cookbook_marche` VALUES ('1406', '3', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_146071073117997.jpg', '放入电饭煲里加水没过豌豆', '766');
INSERT INTO `cookbook_marche` VALUES ('1407', '4', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_146071172466847.jpg', '煮30分钟至1小时', '766');
INSERT INTO `cookbook_marche` VALUES ('1408', '5', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_146071075237547.jpg', '煮好的豆子稍微晾一下倒入料理杯中', '766');
INSERT INTO `cookbook_marche` VALUES ('1409', '6', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_146071198785720.jpg', '搅拌成细腻的豆沙', '766');
INSERT INTO `cookbook_marche` VALUES ('1410', '1', 'https://s1.st.meishij.net/rs/40/62/5953040/n5953040_145900281283961.jpg', '五花肉少许，姜末，葱碎，六必居豆瓣酱', '767');
INSERT INTO `cookbook_marche` VALUES ('1411', '2', 'https://s1.st.meishij.net/rs/40/62/5953040/n5953040_145900281562470.jpg', '擀面也是个技术活', '767');
INSERT INTO `cookbook_marche` VALUES ('1412', '3', 'https://s1.st.meishij.net/rs/40/62/5953040/n5953040_145900281894981.jpg', '叠面', '767');
INSERT INTO `cookbook_marche` VALUES ('1413', '4', 'https://s1.st.meishij.net/rs/40/62/5953040/n5953040_145900282140718.jpg', '切面', '767');
INSERT INTO `cookbook_marche` VALUES ('1414', '5', 'https://s1.st.meishij.net/rs/40/62/5953040/n5953040_145900282562695.jpg', '切面', '767');
INSERT INTO `cookbook_marche` VALUES ('1415', '1', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145885154151142.jpg', '首先我们回顾一下材料。', '768');
INSERT INTO `cookbook_marche` VALUES ('1416', '2', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145885161733048.jpg', '将五花肉切成肉丁，在炒锅内以中小火逼出油分。', '768');
INSERT INTO `cookbook_marche` VALUES ('1417', '3', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145885166376843.jpg', '比较健康的方法就是将肉丁煸炒成这个程度。', '768');
INSERT INTO `cookbook_marche` VALUES ('1418', '4', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145885181691859.jpg', '煸至略微金黄，就将肉丁夹出，油留在锅内。', '768');
INSERT INTO `cookbook_marche` VALUES ('1419', '5', 'https://s1.st.meishij.net/rs/134/204/5801134/n5801134_145885184668767.jpg', '将甜面酱和干黄酱（有些许水勾兑开）以1:1的比例放入油锅，以小火炒制。期间可以加入姜片。', '768');
INSERT INTO `cookbook_marche` VALUES ('1420', '1', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_145873822687389.jpg', '准备好食材：柿饼250g，杏干100g，藕200g，蜂蜜50g。', '769');
INSERT INTO `cookbook_marche` VALUES ('1421', '2', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_145873822910968.jpg', '首先将柿饼的蒂摘下来。', '769');
INSERT INTO `cookbook_marche` VALUES ('1422', '3', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_145873823635655.jpg', '莲藕用戳刀将外侧切掉几条，在每两个藕洞相连的那里把藕肉去掉一条。', '769');
INSERT INTO `cookbook_marche` VALUES ('1423', '4', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_145873824084382.jpg', '然后再切片，这样切出来的片很漂亮。', '769');
INSERT INTO `cookbook_marche` VALUES ('1424', '5', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_145873825678806.jpg', '切好的藕片用冷水洗两次，然后用开水煮1分半，捞出来立刻用冷水过凉浸泡备用。', '769');
INSERT INTO `cookbook_marche` VALUES ('1425', '6', 'https://s1.st.meishij.net/rs/175/213/490925/n490925_145873827551429.jpg', '烧一锅开水将柿饼和杏干烫30秒，然后用冷水清洗两次控干水分备用。', '769');
INSERT INTO `cookbook_marche` VALUES ('1426', '1', 'https://s1.st.meishij.net/rs/203/57/5639453/n5639453_145620118957574.jpg', '将切成丝状的猪里脊肉倒入水晶碗中，随后倒入适量的香油、盐、清酒、和一点水。打入一颗蛋，再加入酱油、盐，充分拌匀，让酒和水融进肉里后，再加入淀粉。（清酒可以用米酒代替）', '770');
INSERT INTO `cookbook_marche` VALUES ('1427', '2', 'https://s1.st.meishij.net/rs/203/57/5639453/n5639453_145620119637111.jpg', '倒入适量色拉油，拌匀，这样在后续步骤中，肉才不会黏在一起。', '770');
INSERT INTO `cookbook_marche` VALUES ('1428', '3', 'https://s1.st.meishij.net/rs/203/57/5639453/n5639453_145620119748314.jpg', '将肉倒入油锅里，快速地过一遍油，里脊肉变成白色后捞出。（油需要预热，但油温不能太高）', '770');
INSERT INTO `cookbook_marche` VALUES ('1429', '4', 'https://s1.st.meishij.net/rs/203/57/5639453/n5639453_145620119922003.jpg', '将肉过滤出来（这时候的肉丝很软滑）', '770');
INSERT INTO `cookbook_marche` VALUES ('1430', '1', 'https://s1.st.meishij.net/rs/13/60/2827513/n2827513_145597151720473.jpg', '豆腐皮洗净切块放入锅中蒸10分钟。', '771');
INSERT INTO `cookbook_marche` VALUES ('1431', '2', 'https://s1.st.meishij.net/rs/13/60/2827513/n2827513_145597153852592.jpg', '里脊肉切成丝。', '771');
INSERT INTO `cookbook_marche` VALUES ('1432', '3', 'https://s1.st.meishij.net/rs/13/60/2827513/n2827513_145597156011668.jpg', '肉丝加入酱油，料酒，淀粉拌匀腌制20分钟。', '771');
INSERT INTO `cookbook_marche` VALUES ('1433', '4', 'https://s1.st.meishij.net/rs/13/60/2827513/n2827513_145597158340450.jpg', '葱切细丝。', '771');
INSERT INTO `cookbook_marche` VALUES ('1434', '5', 'https://s1.st.meishij.net/rs/13/60/2827513/n2827513_145597161481257.jpg', '黄瓜洗净切丝。', '771');
INSERT INTO `cookbook_marche` VALUES ('1435', '1', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_144635956609863.jpg', '豌豆粉中倒入250g清水', '772');
INSERT INTO `cookbook_marche` VALUES ('1436', '2', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_144635966567851.jpg', '搅拌均匀后将其过滤', '772');
INSERT INTO `cookbook_marche` VALUES ('1437', '3', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_144635958325257.jpg', '滤出的豌豆浆备用', '772');
INSERT INTO `cookbook_marche` VALUES ('1438', '4', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_144635959220642.jpg', '锅中倒入250g清水，煮开后加入白糖', '772');
INSERT INTO `cookbook_marche` VALUES ('1439', '1', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143883028107369.jpg', '将小黄鱼去内脏、鱼鳃清洗干净，放入白胡椒粉、食盐、料酒抓匀腌制30分钟\r\n\r\n', '773');
INSERT INTO `cookbook_marche` VALUES ('1440', '2', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143883029128698.jpg', '调制煎炸糊，面粉、花椒粉、淀粉混合后，一点点加入凉水，调成面糊，最后再放入5克食用油，调匀（面糊的稀稠度，用筷子挑起面糊，慢慢有面糊滴落）即可', '773');
INSERT INTO `cookbook_marche` VALUES ('1441', '3', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143883031267641.jpg', '将腌好的小黄鱼，放入面糊中，将黄鱼沾满面糊', '773');
INSERT INTO `cookbook_marche` VALUES ('1442', '1', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143875546745598.jpg', '将牛肉馅放入料理盆中，依次放入黄豆酱、鸡蛋、食盐、鸡精、生抽、料酒、花椒粉、食用油', '774');
INSERT INTO `cookbook_marche` VALUES ('1443', '2', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143875546972971.jpg', '搅拌均匀', '774');
INSERT INTO `cookbook_marche` VALUES ('1444', '3', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143875547182663.jpg', '再放入葱碎、姜末、香油，再次搅拌均匀，腌制15分钟', '774');
INSERT INTO `cookbook_marche` VALUES ('1445', '4', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143875547586667.jpg', '将面粉里一点点加入凉水，将面粉调成比较稠的面糊，醒30分钟', '774');
INSERT INTO `cookbook_marche` VALUES ('1446', '5', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143875552975933.jpg', '在案板上多放这些干面粉，将醒好的面粉糊放在案板上，擀成近似方形面片', '774');
INSERT INTO `cookbook_marche` VALUES ('1447', '6', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143875555033398.jpg', '将牛肉馅铺在面片上，留出一个角不要铺肉馅', '774');
INSERT INTO `cookbook_marche` VALUES ('1448', '7', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143875556228617.jpg', '在没铺肉馅的一角切一刀，向肉馅折起，一层一层叠上', '774');
INSERT INTO `cookbook_marche` VALUES ('1449', '1', 'https://s1.st.meishij.net/rs/173/81/3832923/n3832923_143874246200517.jpg', '用料：豌豆125克，白糖25克\r\n\r\n', '775');
INSERT INTO `cookbook_marche` VALUES ('1450', '2', 'https://s1.st.meishij.net/rs/173/81/3832923/n3832923_143874246425068.jpg', '豌豆洗净，放入炖盅，加水。电炖锅中倒少量水，放入炖盅', '775');
INSERT INTO `cookbook_marche` VALUES ('1451', '3', 'https://s1.st.meishij.net/rs/173/81/3832923/n3832923_143874246683182.jpg', '低温档隔水炖煮一夜。豌豆酥烂，用筷子搅散，静置一会\r\n\r\n', '775');
INSERT INTO `cookbook_marche` VALUES ('1452', '1', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143866861824655.jpg', '准备原料\r\n\r\n', '776');
INSERT INTO `cookbook_marche` VALUES ('1453', '2', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143866862068507.jpg', '取一容器，将北豆腐充分捏碎', '776');
INSERT INTO `cookbook_marche` VALUES ('1454', '3', 'https://s1.st.meishij.net/rs/235/148/3349735/n3349735_143866862224684.jpg', '将胡萝卜用擦板擦成丝', '776');
