INSERT INTO `cookbook_marche` VALUES ('1456', '2', 'https://s1.st.meishij.net/rs/207/244/2811207/n2811207_143864781309024.jpg', '启动程序，将黄豆打碎，成为细腻的豆浆原浆', '777');
