INSERT INTO `cookbook_marche` VALUES ('1505', '4', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143824111227654.jpg', '半小勺胡椒粉', '785');
INSERT INTO `cookbook_marche` VALUES ('1506', '5', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143824111303237.jpg', '拌匀腌渍。去和面', '785');
INSERT INTO `cookbook_marche` VALUES ('1507', '6', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143824111458562.jpg', '面粉里加入半小勺盐，用开水烫一半的面粉', '785');
INSERT INTO `cookbook_marche` VALUES ('1508', '7', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143824111453181.jpg', '再放入一点凉水把面和成柔软的面团', '785');
INSERT INTO `cookbook_marche` VALUES ('1509', '8', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143824111548559.jpg', '柔光盖好饧半小时左右', '785');
INSERT INTO `cookbook_marche` VALUES ('1510', '9', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143824111637792.jpg', '腌渍好的鸡肉用1汤勺面粉，1汤勺淀粉，加入一个鸡蛋清拌匀', '785');
INSERT INTO `cookbook_marche` VALUES ('1511', '10', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143824111686146.jpg', '炒锅热宽油，油5分热时一根根放入肉条', '785');
INSERT INTO `cookbook_marche` VALUES ('1512', '11', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143824111750275.jpg', '炸至金黄', '785');
INSERT INTO `cookbook_marche` VALUES ('1513', '1', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143806467287495.jpg', '玉米面选用超细的精磨玉米面', '786');
INSERT INTO `cookbook_marche` VALUES ('1514', '2', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143806452796002.jpg', '慢慢一点点向玉米面中倒水，边倒边搅动，使玉米面形成细小的疙瘩，要达到用手一搓就能散的程度，这不很容易做，玉米面不像白面，有疙瘩大了，用筷子轻触就会散开', '786');
INSERT INTO `cookbook_marche` VALUES ('1515', '3', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143806452965951.jpg', '韭菜洗干净', '786');
INSERT INTO `cookbook_marche` VALUES ('1516', '4', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143806453050211.jpg', '鸡蛋打散，加入半小勺盐和半汤勺酱油', '786');
INSERT INTO `cookbook_marche` VALUES ('1517', '5', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143806453321870.jpg', '搅拌均匀', '786');
INSERT INTO `cookbook_marche` VALUES ('1518', '6', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143806453489717.jpg', '炒锅热油，炒熟鸡蛋，炒的时候要直接搅散', '786');
INSERT INTO `cookbook_marche` VALUES ('1519', '1', 'https://s1.st.meishij.net/rs/101/193/3298351/n3298351_143796875624934.jpg', '选好了要的材料，我选用的这款是老北京最经典的配方，简单而健康的配搭', '787');
INSERT INTO `cookbook_marche` VALUES ('1520', '2', 'https://s1.st.meishij.net/rs/101/193/3298351/n3298351_143796877752499.jpg', '看下，有乌梅、当山、甘草、桂花', '787');
INSERT INTO `cookbook_marche` VALUES ('1521', '3', 'https://s1.st.meishij.net/rs/101/193/3298351/n3298351_143796879061054.jpg', '精选乌梅', '787');
INSERT INTO `cookbook_marche` VALUES ('1522', '4', 'https://s1.st.meishij.net/rs/101/193/3298351/n3298351_143796880974094.jpg', '特选优质甘草', '787');
INSERT INTO `cookbook_marche` VALUES ('1523', '5', 'https://s1.st.meishij.net/rs/101/193/3298351/n3298351_143796882437867.jpg', '桂花，味香杂质少', '787');
INSERT INTO `cookbook_marche` VALUES ('1524', '6', 'https://s1.st.meishij.net/rs/101/193/3298351/n3298351_143796884049818.jpg', '无籽山楂，陈皮', '787');
INSERT INTO `cookbook_marche` VALUES ('1525', '7', 'https://s1.st.meishij.net/rs/101/193/3298351/n3298351_143796887457384.jpg', '除了桂花，其它材料都放进去泡30分钟', '787');
INSERT INTO `cookbook_marche` VALUES ('1526', '8', 'https://s1.st.meishij.net/rs/101/193/3298351/n3298351_143796888802439.jpg', '记得桂花不用泡了。桂花只洗干净就行了', '787');
INSERT INTO `cookbook_marche` VALUES ('1527', '9', 'https://s1.st.meishij.net/rs/101/193/3298351/n3298351_143796891296111.jpg', '锅装满水，然后把泡洗好的材料，乌梅、陈皮、甘草、山楂', '787');
INSERT INTO `cookbook_marche` VALUES ('1528', '1', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_143797078583267.jpg', '1斤的豆角洗净备用，焖面用四季豆比较好，不要买那种白色的白不老，那种扁豆比较适合炖肉吃，不适合焖面的', '788');
INSERT INTO `cookbook_marche` VALUES ('1529', '2', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_143797083718601.jpg', '五花肉适量切薄片，加入料酒和盐，搅拌均匀备用', '788');
INSERT INTO `cookbook_marche` VALUES ('1530', '3', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_143797087108241.jpg', '适量葱姜末备用', '788');
INSERT INTO `cookbook_marche` VALUES ('1531', '4', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_143797123206928.jpg', '蒜瓣切蒜末，开锅适合放', '788');
INSERT INTO `cookbook_marche` VALUES ('1532', '5', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_143797133288920.jpg', '坐热锅子，放入适量油，加入葱姜末爆香', '788');
INSERT INTO `cookbook_marche` VALUES ('1533', '6', 'https://s1.st.meishij.net/rs/162/177/3544412/n3544412_143797148027180.jpg', '加入五花肉片和适量料酒翻炒', '788');
INSERT INTO `cookbook_marche` VALUES ('1534', '1', 'https://s1.st.meishij.net/rs/128/78/2269628/n2269628_143794685271010.jpg', '西葫芦洗净打成丝或切成丝，再加盐拌匀放置10分钟。（我就喜欢切丝的，口感丰富）', '789');
INSERT INTO `cookbook_marche` VALUES ('1535', '2', 'https://s1.st.meishij.net/rs/128/78/2269628/n2269628_143794685393036.jpg', '一会儿葫芦丝就被盐出了水，然后打入鸡蛋拌匀。（盐出的水一定不能倒掉哦）', '789');
INSERT INTO `cookbook_marche` VALUES ('1536', '3', 'https://s1.st.meishij.net/rs/128/78/2269628/n2269628_143794685583266.jpg', '加入香葱碎、淋入香油拌匀', '789');
INSERT INTO `cookbook_marche` VALUES ('1537', '1', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789732134692.jpg', '首先 先来和面。\r\n把面粉铺在案板上，中间用刮板挖出一个圆窝，也就是砌个粉墙', '790');
INSERT INTO `cookbook_marche` VALUES ('1538', '2', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789732286074.jpg', '温水倒入粉墙中', '790');
INSERT INTO `cookbook_marche` VALUES ('1539', '3', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789732207792.jpg', '粉墙内壁上的面粉慢慢混合到水里面，变成粘性较大的面糊', '790');
INSERT INTO `cookbook_marche` VALUES ('1540', '4', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789732567628.jpg', '在用刮板把所有面粉混合到面糊里面', '790');
INSERT INTO `cookbook_marche` VALUES ('1541', '5', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789732642280.jpg', '用手和成面团，要多揉一会儿，揉透，变得光滑有弹性。揉好抹上植物油，盖好，松弛20分钟', '790');
INSERT INTO `cookbook_marche` VALUES ('1542', '6', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789732450982.jpg', '猪肉用刀剁碎，加入剁碎的姜末', '790');
INSERT INTO `cookbook_marche` VALUES ('1543', '7', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789732560813.jpg', '在倒入生抽、老抽、胡椒粉、麻油，和盐', '790');
INSERT INTO `cookbook_marche` VALUES ('1544', '8', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789732740905.jpg', '搅拌均匀，加入高汤', '790');
INSERT INTO `cookbook_marche` VALUES ('1545', '9', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789732903428.jpg', '再把高汤或者水分三次倒进肉馅搅拌，每一次都要搅拌均匀，让肉馅充分吸收后再加，拌好备用', '790');
INSERT INTO `cookbook_marche` VALUES ('1546', '10', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789732936795.jpg', '火烧案板不要放生粉，抹上一油，饧好的面团搓成长条', '790');
INSERT INTO `cookbook_marche` VALUES ('1547', '11', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789732096290.jpg', '分成8个均匀地面剂子', '790');
INSERT INTO `cookbook_marche` VALUES ('1548', '1', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789463243705.jpg', '温水融化酵母', '791');
INSERT INTO `cookbook_marche` VALUES ('1549', '2', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789463242655.jpg', '融好的酵母水倒进面粉，搅拌成絮状', '791');
INSERT INTO `cookbook_marche` VALUES ('1550', '3', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789511498401.jpg', '在把面粉和成光滑的面团，盖严，饧发约1小时。这里面团要和的柔软，我的面粉较干，250g大约用了200g的温水，看面粉的干湿度调节，面要和的柔软一些，吃起来才宣软', '791');
INSERT INTO `cookbook_marche` VALUES ('1551', '4', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789463376979.jpg', '现在室温较高，1小时后面就发的很好', '791');
INSERT INTO `cookbook_marche` VALUES ('1552', '5', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789463403644.jpg', '猪肉约300g，剁成泥，加入剁碎的葱姜，同时加入蚝油，酱油，胡椒粉，盐，油', '791');
INSERT INTO `cookbook_marche` VALUES ('1553', '6', 'https://s1.st.meishij.net/rs/51/205/2301301/n2301301_143789463159548.jpg', '在搅拌均匀成馅，肉馅较干可以少量加点水，但要一个方向搅动，使水吃进肉馅里', '791');
INSERT INTO `cookbook_marche` VALUES ('1554', '1', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143739730885999.jpg', '买回现成猪肉泥加入料酒', '792');
INSERT INTO `cookbook_marche` VALUES ('1555', '2', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143739732896605.jpg', '加入姜汁', '792');
INSERT INTO `cookbook_marche` VALUES ('1556', '3', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143739735483584.jpg', '加点盐和砂糖', '792');
INSERT INTO `cookbook_marche` VALUES ('1557', '4', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143739739055821.jpg', '加点生抽', '792');
INSERT INTO `cookbook_marche` VALUES ('1558', '5', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143739741290731.jpg', '加点香油', '792');
INSERT INTO `cookbook_marche` VALUES ('1559', '6', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143739743427479.jpg', '加点家乐鸡粉', '792');
INSERT INTO `cookbook_marche` VALUES ('1560', '7', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143739744924157.jpg', '向一个方向搅拌上劲', '792');
INSERT INTO `cookbook_marche` VALUES ('1561', '8', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143739746417924.jpg', '面粉里加入用温水化开的酵母水', '792');
INSERT INTO `cookbook_marche` VALUES ('1562', '9', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_143739773193353.jpg', '再加入适量清水和成面团', '792');
INSERT INTO `cookbook_marche` VALUES ('1563', '1', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143722116283894.jpg', '准备豌豆粉和细砂糖', '793');
INSERT INTO `cookbook_marche` VALUES ('1564', '2', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143722134305383.jpg', '容器里放入100g豌豆粉和细砂糖，加入250g清水，搅拌均匀，搅成细细的面糊', '793');
INSERT INTO `cookbook_marche` VALUES ('1565', '3', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143722135366076.jpg', '面糊过筛，滤出的面疙瘩用勺子碾碎，可以继续用；过滤后的面糊非常细滑', '793');
INSERT INTO `cookbook_marche` VALUES ('1566', '4', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143722136593688.jpg', '小锅里放入余下的250g水，小火烧至锅底开始冒泡，倒入面糊，边到边搅', '793');
INSERT INTO `cookbook_marche` VALUES ('1567', '1', 'https://s1.st.meishij.net/rs/183/61/827933/n827933_143720829726277.jpg', '面粉加水调成面疙瘩饧10分钟', '794');
INSERT INTO `cookbook_marche` VALUES ('1568', '2', 'https://s1.st.meishij.net/rs/183/61/827933/n827933_143720830585220.jpg', '香菇切丁', '794');
INSERT INTO `cookbook_marche` VALUES ('1569', '3', 'https://s1.st.meishij.net/rs/183/61/827933/n827933_143720831197674.jpg', '西红柿切块、干紫菜洗净备用', '794');
INSERT INTO `cookbook_marche` VALUES ('1570', '4', 'https://s1.st.meishij.net/rs/183/61/827933/n827933_143720831952438.jpg', '水开焯烫香菇丁然后捞出控水', '794');
INSERT INTO `cookbook_marche` VALUES ('1571', '5', 'https://s1.st.meishij.net/rs/183/61/827933/n827933_143720832738730.jpg', '炒锅倒少许油下西红柿翻炒至出汁', '794');
INSERT INTO `cookbook_marche` VALUES ('1572', '1', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718474139974.jpg', '老北京奶酪卷做法的步骤 先把自制的米酒过滤，只取米酒液。配方中的米酒量指的是过滤后的酒液的量', '795');
INSERT INTO `cookbook_marche` VALUES ('1573', '2', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718474382773.jpg', '买的牛初乳，看看表面就可以看出，黄腾腾的一层哦。就是厨房拍的效果不好，没能真正展现出来', '795');
INSERT INTO `cookbook_marche` VALUES ('1574', '3', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718474805174.jpg', '初乳用火煮开，加入米酒', '795');
INSERT INTO `cookbook_marche` VALUES ('1575', '4', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718474992261.jpg', '继续煮1分钟左右，奶酪就凝结成块了', '795');
INSERT INTO `cookbook_marche` VALUES ('1576', '5', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718474925674.jpg', '扒开看的更清楚，奶清和奶酪很明显的分离了', '795');
INSERT INTO `cookbook_marche` VALUES ('1577', '6', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718475045819.jpg', '用网筛把上面凝结的奶酪捞到屉布上', '795');
INSERT INTO `cookbook_marche` VALUES ('1578', '1', 'https://s1.st.meishij.net/rs/105/225/1306355/n1306355_143713068542953.jpg', '材料集合', '796');
INSERT INTO `cookbook_marche` VALUES ('1579', '2', 'https://s1.st.meishij.net/rs/105/225/1306355/n1306355_143713094369819.jpg', '豌豆(去皮)洗净，加水浸泡4小时以上。豌豆泡发后倒掉多余的水，使水面和豌豆持平', '796');
INSERT INTO `cookbook_marche` VALUES ('1580', '3', 'https://s1.st.meishij.net/rs/105/225/1306355/n1306355_143713160855893.jpg', '高压锅加水，底下加支架，把豌豆放入', '796');
INSERT INTO `cookbook_marche` VALUES ('1581', '4', 'https://s1.st.meishij.net/rs/105/225/1306355/n1306355_143713185326732.jpg', '盖子加阀，大火上汽后，改中火，从上汽算起，15分钟关火', '796');
INSERT INTO `cookbook_marche` VALUES ('1582', '5', 'https://s1.st.meishij.net/rs/105/225/1306355/n1306355_143713204618702.jpg', '泄气后取出，看，豌豆已经酥烂了，水也没有了', '796');
INSERT INTO `cookbook_marche` VALUES ('1583', '6', 'https://s1.st.meishij.net/rs/105/225/1306355/n1306355_143713235913546.jpg', '加糖搅拌，喜欢甜的可多加些', '796');
INSERT INTO `cookbook_marche` VALUES ('1584', '1', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143712891329488.jpg', '这是自制的米酒，酒味很重', '797');
INSERT INTO `cookbook_marche` VALUES ('1585', '2', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143712893065061.jpg', '过滤出500g，滤掉米粒', '797');
INSERT INTO `cookbook_marche` VALUES ('1586', '3', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143712894233157.jpg', '一升全脂牛奶放入奶锅里，小火加热', '797');
INSERT INTO `cookbook_marche` VALUES ('1587', '4', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143712895319182.jpg', '加热至微微冒泡的时候，倒入米酒', '797');
INSERT INTO `cookbook_marche` VALUES ('1588', '5', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143712896467127.jpg', '继续加热至冒泡，牛奶已变得像豆腐渣一样，关火', '797');
INSERT INTO `cookbook_marche` VALUES ('1589', '6', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143712898084392.jpg', '把牛奶倒入过滤袋中', '797');
INSERT INTO `cookbook_marche` VALUES ('1590', '7', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143712899184799.jpg', '滤出的奶清，可以继续做它用', '797');
INSERT INTO `cookbook_marche` VALUES ('1591', '1', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_143712876729588.jpg', '面粉中加入酵母，现在是夏天，气温高，所以我就偷懒了，直接把酵母拌到面粉里，如果气温低的情况下，建议把酵母用温水化开再用', '798');
INSERT INTO `cookbook_marche` VALUES ('1592', '2', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_143712891211190.jpg', '慢慢地加入清水，用筷子搅拌成湿润的面团。这时不必下手', '798');
INSERT INTO `cookbook_marche` VALUES ('1593', '3', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_143712923479653.jpg', '盖上保鲜膜，室温发酵至面团两倍大呈现蜂窝状。夏天的话这个过程大概需要一个小时', '798');
INSERT INTO `cookbook_marche` VALUES ('1594', '4', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_143712926644476.jpg', '在等待面团发酵过程中来调肉馅:葱、姜都要切碎，和各种调料一起加入肉馅', '798');
INSERT INTO `cookbook_marche` VALUES ('1595', '5', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_143712956793093.jpg', '用筷子顺着同一个方向搅拌均匀', '798');
INSERT INTO `cookbook_marche` VALUES ('1596', '6', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_143712957830755.jpg', '把发酵好的面团放在案板上，撒上些手粉，把面团揉光揉匀，这也是一个排气的过程', '798');
INSERT INTO `cookbook_marche` VALUES ('1597', '1', 'https://s1.st.meishij.net/rs/163/225/2431413/n2431413_150650481293538.jpg', '首先将羊肉和羊骨放在水中泡上2小时，泡出血水，中间换几次水。\r\n', '1025');
INSERT INTO `cookbook_marche` VALUES ('1598', '2', 'https://s1.st.meishij.net/rs/163/225/2431413/n2431413_150650481680485.jpg', '将泡出血水的肉和骨放入清水中煮开2-3分钟，煮出血污，然后将水倒掉，肉和骨清洗干净。\r\n', '1025');
INSERT INTO `cookbook_marche` VALUES ('1599', '3', 'https://s1.st.meishij.net/rs/163/225/2431413/n2431413_150650481932386.jpg', '再将肉和骨放回锅里换上新水，约3升。\r\n', '1025');
INSERT INTO `cookbook_marche` VALUES ('1600', '4', 'https://s1.st.meishij.net/rs/163/225/2431413/n2431413_150650482311383.jpg', '用大火烧开，撇去浮沫，然后将备好的香料放入，盖严锅盖。\r\n', '1025');
INSERT INTO `cookbook_marche` VALUES ('1601', '5', 'https://s1.st.meishij.net/rs/163/225/2431413/n2431413_150650482893323.jpg', '我用的香料是大料1个、花椒3克、桂皮4克、葱80克、姜20克，如果有草果、小茴香、白芷、良姜、砂仁、丁香、山奈用上些就更好了，有人说最重要的是小茴香。', '1025');
INSERT INTO `cookbook_marche` VALUES ('1602', '6', 'https://s1.st.meishij.net/rs/163/225/2431413/n2431413_150650483467663.jpg', '待到上气，改小火，保持一直上气的状态煮30分钟。注：如果是普通锅约2小时。\r\n', '1025');
INSERT INTO `cookbook_marche` VALUES ('1603', '7', 'https://s1.st.meishij.net/rs/163/225/2431413/n2431413_150650483957007.jpg', '解压后，如果羊肉可达到用筷子轻轻穿过，就将肉捞出备用。\r\n', '1025');
INSERT INTO `cookbook_marche` VALUES ('1604', '8', 'https://s1.st.meishij.net/rs/163/225/2431413/n2431413_150650484478406.jpg', '骨头仍留在锅里，盖严锅盖，大火煮上气，改小火，保持一直上气的状态煮30分钟，这样骨头就全煮酥了，羊肉汤也就煮好了。\r\n', '1025');
INSERT INTO `cookbook_marche` VALUES ('1605', '9', 'https://s1.st.meishij.net/rs/163/225/2431413/n2431413_150650676866343.jpg', '骨头煮好后我们开始泡馍\r\n泡好的黄花菜，摘去硬头，一撕为二。', '1025');
INSERT INTO `cookbook_marche` VALUES ('1606', '1', 'https://s1.st.meishij.net/rs/195/26/506695/n506695_148905114461691.jpg', '400克面粉和4克盐。', '1026');
INSERT INTO `cookbook_marche` VALUES ('1607', '2', 'https://s1.st.meishij.net/rs/195/26/506695/n506695_148905114736889.jpg', '揉成光滑的面团。', '1026');
INSERT INTO `cookbook_marche` VALUES ('1608', '3', 'https://s1.st.meishij.net/rs/195/26/506695/n506695_148905115050417.jpg', '盖上保鲜膜饧发一个小时。', '1026');
INSERT INTO `cookbook_marche` VALUES ('1609', '4', 'https://s1.st.meishij.net/rs/195/26/506695/n506695_148905115358649.jpg', '把饧好的面搓成长条。', '1026');
INSERT INTO `cookbook_marche` VALUES ('1610', '5', 'https://s1.st.meishij.net/rs/195/26/506695/n506695_148905115611554.jpg', '平分成12个小剂子。', '1026');
INSERT INTO `cookbook_marche` VALUES ('1611', '6', 'https://s1.st.meishij.net/rs/195/26/506695/n506695_148905115952665.jpg', '按扁剂子，抹上一层油。', '1026');
INSERT INTO `cookbook_marche` VALUES ('1612', '7', 'https://s1.st.meishij.net/rs/195/26/506695/n506695_148905116148071.jpg', '盘子里也抹上一层薄薄的油，盖上保鲜膜，再次饧发两个小时。（最少也要半个小时）', '1026');
INSERT INTO `cookbook_marche` VALUES ('1613', '8', 'https://s1.st.meishij.net/rs/195/26/506695/n506695_148905116455513.jpg', '准备好葱末蒜末辣椒末，生抽和醋调在一起。', '1026');
INSERT INTO `cookbook_marche` VALUES ('1614', '9', 'https://s1.st.meishij.net/rs/195/26/506695/n506695_148905116845591.jpg', '准备煮面。', '1026');
INSERT INTO `cookbook_marche` VALUES ('1615', '10', 'https://s1.st.meishij.net/rs/195/26/506695/n506695_148905117367318.jpg', '把面片用手按平。', '1026');
INSERT INTO `cookbook_marche` VALUES ('1616', '1', 'https://s1.st.meishij.net/rs/141/19/8504891/n8504891_148301697218910.jpg', '猪肉切成中块，葱切段，蒜切成片', '1027');
INSERT INTO `cookbook_marche` VALUES ('1617', '2', 'https://s1.st.meishij.net/rs/141/19/8504891/n8504891_148301697310061.jpg', '准备好的猪肉冷水下锅', '1027');
INSERT INTO `cookbook_marche` VALUES ('1618', '3', 'https://s1.st.meishij.net/rs/141/19/8504891/n8504891_148301697585212.jpg', '煮至血沫浮出', '1027');
INSERT INTO `cookbook_marche` VALUES ('1619', '4', 'https://s1.st.meishij.net/rs/141/19/8504891/n8504891_148301697629425.jpg', '捞出用温水洗净血沫，放入砂锅中', '1027');
INSERT INTO `cookbook_marche` VALUES ('1620', '5', 'https://s1.st.meishij.net/rs/141/19/8504891/n8504891_148301697776567.jpg', '加入所有的辅料，加入适量的温水', '1027');
INSERT INTO `cookbook_marche` VALUES ('1621', '6', 'https://s1.st.meishij.net/rs/141/19/8504891/n8504891_148301697824929.jpg', '大火煮开，转至小火炖煮两个小时', '1027');
INSERT INTO `cookbook_marche` VALUES ('1622', '7', 'https://s1.st.meishij.net/rs/141/19/8504891/n8504891_148301697976861.jpg', '将面粉放入面包机，后将4克的酵母放入温水中搅拌均匀倒入面包机内，按下和面功能键，揉至面团光滑，将面团用保鲜膜盖住发酵一个小时', '1027');
INSERT INTO `cookbook_marche` VALUES ('1623', '8', 'https://s1.st.meishij.net/rs/141/19/8504891/n8504891_148301698192635.jpg', '将面团取出切成均匀的小段，擀成椭圆形，然后对折，从一头慢慢卷起，如图所示，全部卷完用保鲜膜盖住让它继续醒发十分钟', '1027');
INSERT INTO `cookbook_marche` VALUES ('1624', '1', 'https://s1.st.meishij.net/rs/236/182/6608236/n6608236_148301418027192.jpg', '羊肉洗净冷水下锅煮', '1028');
INSERT INTO `cookbook_marche` VALUES ('1625', '2', 'https://s1.st.meishij.net/rs/236/182/6608236/n6608236_148301418424410.jpg', '高压锅内，放姜，橘皮', '1028');
INSERT INTO `cookbook_marche` VALUES ('1626', '3', 'https://s1.st.meishij.net/rs/236/182/6608236/n6608236_148301419000512.jpg', '粉条提前泡软备用', '1028');
INSERT INTO `cookbook_marche` VALUES ('1627', '4', 'https://s1.st.meishij.net/rs/236/182/6608236/n6608236_148301420249419.jpg', '香菜洗好备用', '1028');
INSERT INTO `cookbook_marche` VALUES ('1628', '5', 'https://s1.st.meishij.net/rs/236/182/6608236/n6608236_148301420579988.jpg', '高压锅煮好的羊肉', '1028');
INSERT INTO `cookbook_marche` VALUES ('1629', '6', 'https://s1.st.meishij.net/rs/236/182/6608236/n6608236_148301421317415.jpg', '羊肉捞出来切片', '1028');
INSERT INTO `cookbook_marche` VALUES ('1630', '7', 'https://s1.st.meishij.net/rs/236/182/6608236/n6608236_148301422094987.jpg', '切好待会用', '1028');
INSERT INTO `cookbook_marche` VALUES ('1631', '8', 'https://s1.st.meishij.net/rs/236/182/6608236/n6608236_148301422488007.jpg', '白汤倒入炒锅里，放入粉条', '1028');
INSERT INTO `cookbook_marche` VALUES ('1632', '1', 'https://s1.st.meishij.net/rs/223/138/9034723/n9034723_148282920670721.jpg', '碗内装好面粉，准备半碗水，边用筷子搅面粉边加水，水一次性少加，每次加几滴', '1029');
INSERT INTO `cookbook_marche` VALUES ('1633', '2', 'https://s1.st.meishij.net/rs/223/138/9034723/n9034723_148282925341113.jpg', '直到搅拌成如图的状态就可以不用加水了，开始和面', '1029');
INSERT INTO `cookbook_marche` VALUES ('1634', '3', 'https://s1.st.meishij.net/rs/223/138/9034723/n9034723_148282928935474.jpg', '揉成一个光滑的面团，盖上保鲜膜醒30分钟', '1029');
INSERT INTO `cookbook_marche` VALUES ('1635', '4', 'https://s1.st.meishij.net/rs/223/138/9034723/n9034723_148282932650366.jpg', '将面团分成下图大小的小面团，揉成圆柱状，刷满食用油，继续盖上保鲜膜醒20分钟', '1029');
INSERT INTO `cookbook_marche` VALUES ('1636', '1', 'https://s1.st.meishij.net/rs/221/158/6102221/n6102221_148048168587894.jpg', '扣肉上锅蒸好', '1030');
INSERT INTO `cookbook_marche` VALUES ('1637', '1', 'https://s1.st.meishij.net/rs/76/191/5485326/n5485326_147711917712616.jpg', '准备配菜', '1031');
INSERT INTO `cookbook_marche` VALUES ('1638', '2', 'https://s1.st.meishij.net/rs/76/191/5485326/n5485326_147711918009825.jpg', '将配菜都切丁，木耳切丝，小白菜和芹菜切段，豆芽掐根', '1031');
INSERT INTO `cookbook_marche` VALUES ('1639', '3', 'https://s1.st.meishij.net/rs/76/191/5485326/n5485326_147711918294426.jpg', '锅底加入油，先翻炒西红柿，再倒入所有配菜翻炒', '1031');
INSERT INTO `cookbook_marche` VALUES ('1640', '4', 'https://s1.st.meishij.net/rs/76/191/5485326/n5485326_147711918424930.jpg', '加入一勺高汤，这里我加入的是排骨汤，或者加入水。', '1031');
INSERT INTO `cookbook_marche` VALUES ('1641', '1', 'https://s1.st.meishij.net/rs/36/159/6539786/n6539786_147693812228466.jpg', '陕西人和面讲究三光_1面光 2手光 3盆光_，楼主正在提升中，所以和面先用筷子，加水差不多的时候用手揉', '1032');
INSERT INTO `cookbook_marche` VALUES ('1642', '2', 'https://s1.st.meishij.net/rs/36/159/6539786/n6539786_147693812793066.jpg', '放入盆中，醒15-20分钟。这个步骤也是为了给配菜的准备留时间', '1032');
INSERT INTO `cookbook_marche` VALUES ('1643', '3', 'https://s1.st.meishij.net/rs/36/159/6539786/n6539786_147693813093843.jpg', '土豆洗干净去皮切块准备，可以先炒半熟然，也可以最后直接煮', '1032');
INSERT INTO `cookbook_marche` VALUES ('1644', '4', 'https://s1.st.meishij.net/rs/36/159/6539786/n6539786_147693813446737.jpg', '豆角洗干净，切丁备用。也可以先炒半熟', '1032');
INSERT INTO `cookbook_marche` VALUES ('1645', '5', 'https://s1.st.meishij.net/rs/36/159/6539786/n6539786_147693813900349.jpg', '鸡蛋搅拌备用，依个人口味，可以再准备两个西红柿切丁备用，出来的烩麻食会看着会更好看', '1032');
INSERT INTO `cookbook_marche` VALUES ('1646', '6', 'https://s1.st.meishij.net/rs/36/159/6539786/n6539786_147693814260142.jpg', '把面团拿出来，可以分三份，先把第一份擀成面饼，然后切条，宽度一厘米左右', '1032');
INSERT INTO `cookbook_marche` VALUES ('1647', '1', 'https://s1.st.meishij.net/rs/12/85/1896262/n1896262_147366813182893.jpg', '一定要肥瘦适当的带皮五花肉或肋条肉切成薄片，四分肥六分瘦，或者三七分，总之不能太瘦那样烧出来太柴。', '1033');
INSERT INTO `cookbook_marche` VALUES ('1648', '2', 'https://s1.st.meishij.net/rs/12/85/1896262/n1896262_147366813861286.jpg', '将菜油倒入锅中六分热时，将肉片倒入煸炒至变色，火力大小如图所示。', '1033');
INSERT INTO `cookbook_marche` VALUES ('1649', '3', 'https://s1.st.meishij.net/rs/12/85/1896262/n1896262_147366814633607.jpg', '在肉片炒至发白出油依次加入料酒，白糖，五香粉，姜粉炒匀盖上锅盖焖制，这四个步骤均间隔不超过五分钟。', '1033');
INSERT INTO `cookbook_marche` VALUES ('1650', '1', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146398937323193.jpg', '所有材料。', '1034');
INSERT INTO `cookbook_marche` VALUES ('1651', '2', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146398951059656.jpg', '茼蒿洗净切碎。', '1034');
INSERT INTO `cookbook_marche` VALUES ('1652', '3', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146398951158535.jpg', '分次加入面粉搅拌均匀。', '1034');
INSERT INTO `cookbook_marche` VALUES ('1653', '4', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146398991615869.jpg', '最后一点点加入水搅拌，搅拌到能成团即可。', '1034');
INSERT INTO `cookbook_marche` VALUES ('1654', '5', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146399001451784.jpg', '微波炉蒸宝所带的篦子上刷油，将拌好的菜团整形后放入。', '1034');
INSERT INTO `cookbook_marche` VALUES ('1655', '6', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146399009798463.jpg', '放入蒸宝中。', '1034');
INSERT INTO `cookbook_marche` VALUES ('1656', '1', 'https://s1.st.meishij.net/rs/176/16/3879176/n3879176_146371444020294.jpg', '中筋面粉500克，酵母7克，温水270克，盐3克。 将面粉加酵母泡打粉盐混合均匀后加入温水，揉制成表皮光滑的面团后醒发约1小时（表面盖湿布或保鲜膜），醒发至面团一倍大左右', '1035');
INSERT INTO `cookbook_marche` VALUES ('1657', '2', 'https://s1.st.meishij.net/rs/176/16/3879176/n3879176_146371454757168.jpg', '揉成长条，分成大小均匀的小份', '1035');
INSERT INTO `cookbook_marche` VALUES ('1658', '3', 'https://s1.st.meishij.net/rs/176/16/3879176/n3879176_146371458780111.jpg', '分别揉圆', '1035');
INSERT INTO `cookbook_marche` VALUES ('1659', '4', 'https://s1.st.meishij.net/rs/176/16/3879176/n3879176_146371464870954.jpg', '表面刷少许油松弛15分钟', '1035');
INSERT INTO `cookbook_marche` VALUES ('1660', '5', 'https://s1.st.meishij.net/rs/176/16/3879176/n3879176_146371471823472.jpg', '将松弛好的面团搓成长条', '1035');
INSERT INTO `cookbook_marche` VALUES ('1661', '6', 'https://s1.st.meishij.net/rs/176/16/3879176/n3879176_146371476622289.jpg', '用擀面杖擀开，将面片对折', '1035');
INSERT INTO `cookbook_marche` VALUES ('1662', '7', 'https://s1.st.meishij.net/rs/176/16/3879176/n3879176_146371481095549.jpg', '从一侧卷起', '1035');
INSERT INTO `cookbook_marche` VALUES ('1663', '8', 'https://s1.st.meishij.net/rs/176/16/3879176/n3879176_146371485645362.jpg', '将收尾压在下面制成饼坯', '1035');
INSERT INTO `cookbook_marche` VALUES ('1664', '9', 'https://s1.st.meishij.net/rs/176/16/3879176/n3879176_146371491060322.jpg', '将做好的饼坯松弛10分钟，将饼坯擀成圆形薄片', '1035');
INSERT INTO `cookbook_marche` VALUES ('1665', '2', 'https://s1.st.meishij.net/rs/200/159/2789950/n2789950_146370412058901.jpg', '土豆用蔬菜处理器刮成细丝，放入水里泡掉一些淀粉，沥干水分', '1036');
INSERT INTO `cookbook_marche` VALUES ('1666', '3', 'https://s1.st.meishij.net/rs/200/159/2789950/n2789950_146370413599750.jpg', '把面粉倒到土豆丝上面', '1036');
INSERT INTO `cookbook_marche` VALUES ('1667', '4', 'https://s1.st.meishij.net/rs/200/159/2789950/n2789950_146370415087542.jpg', '用手抓匀，使每根土豆丝都能裹上面粉', '1036');
INSERT INTO `cookbook_marche` VALUES ('1668', '5', 'https://s1.st.meishij.net/rs/200/159/2789950/n2789950_146370416379189.jpg', '上锅蒸制13分钟左右', '1036');
INSERT INTO `cookbook_marche` VALUES ('1669', '1', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_146254039438637.jpg', '备好食材：把子肉、白吉馍和青椒', '1037');
INSERT INTO `cookbook_marche` VALUES ('1670', '2', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_146254041150736.jpg', '将把子肉和青椒分别切碎', '1037');
INSERT INTO `cookbook_marche` VALUES ('1671', '1', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_146243644636132.jpg', '面粉里放入酵母、糖和少许食用碱面，搅拌均匀', '1038');
INSERT INTO `cookbook_marche` VALUES ('1672', '2', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_146243644853375.jpg', '倒入温水和成光滑面团后，密封好放置在温暖处醒发40分钟到一小时', '1038');
INSERT INTO `cookbook_marche` VALUES ('1673', '3', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_146243645716353.jpg', '将醒发好的面团排气后分成大小均匀的小团', '1038');
INSERT INTO `cookbook_marche` VALUES ('1674', '4', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_146243646768603.jpg', '取一面团擀成椭圆形', '1038');
INSERT INTO `cookbook_marche` VALUES ('1675', '5', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_146243647117584.jpg', '从一端卷起', '1038');
INSERT INTO `cookbook_marche` VALUES ('1676', '1', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146155568651469.jpg', '槐花最好选择花骨朵状态的，不要选已经开花的。花骨朵的槐花口感好滋味足而且容易清洗。我是买的，只能尽量选择没有开的槐花。如果自己可以去采摘，那就方便了。劝君采花莫折枝哦', '1039');
INSERT INTO `cookbook_marche` VALUES ('1677', '2', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146155569922666.jpg', '玉米面和面粉以1：2的比例混合均匀备用。只用其中一样也可以，但两者混合一起使用口感更丰富营养也更好', '1039');
INSERT INTO `cookbook_marche` VALUES ('1678', '3', 'https://s1.st.meishij.net/rs/58/07/2939308/n2939308_146155570922447.jpg', '把槐花洗净沥水，撒上玉米面和面粉的混合粉。可以分批逐次地添加，这样更利于操作', '1039');
INSERT INTO `cookbook_marche` VALUES ('1679', '1', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146142598937091.jpg', '馄饨所需材料。', '1040');
INSERT INTO `cookbook_marche` VALUES ('1680', '2', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146142604140910.jpg', '白萝卜刨丝，撒入少量盐腌制，然后挤去水分，剁碎。', '1040');
INSERT INTO `cookbook_marche` VALUES ('1681', '3', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146142610417027.jpg', '拌入所有十三香粉和盐，搅拌均匀备用。', '1040');
INSERT INTO `cookbook_marche` VALUES ('1682', '4', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146142615007845.jpg', '面粉中一点点加入温水，搅拌成絮状，揉成面团，静置半小时，擀开。', '1040');
INSERT INTO `cookbook_marche` VALUES ('1683', '5', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146142618043717.jpg', '先切4厘米宽的面条，然后将面条展开，叠起来排放整齐，再切4厘米宽，即成4厘米的正方块面片。', '1040');
INSERT INTO `cookbook_marche` VALUES ('1684', '6', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146142628911875.jpg', ' 取一块面片，中间放上腌制好的白萝卜。', '1040');
INSERT INTO `cookbook_marche` VALUES ('1685', '7', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146142639194021.jpg', '将面片从中间对折，然后再将上半部分折下来，以左中指为环，将下半部分的两边按压在左中指上，捏紧。', '1040');
INSERT INTO `cookbook_marche` VALUES ('1686', '8', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146142645286919.jpg', '这就是捏好的馄饨！\r\n晚上坐餐桌前捏的馄饨，灯光原因，颜色不同。', '1040');
INSERT INTO `cookbook_marche` VALUES ('1687', '9', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146142647585665.jpg', ' 捏好的馄饨整齐的摆放在刷过油的篦子上，蒸锅水烧开，将篦子上捏好的馄饨上锅蒸35分钟。', '1040');
INSERT INTO `cookbook_marche` VALUES ('1688', '1', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146070330396310.jpg', '所需材料，大米用清水浸泡数小时，至可以碾碎，沥水，用擀面杖碾碎备用。', '1041');
INSERT INTO `cookbook_marche` VALUES ('1689', '2', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146070332311527.jpg', '郫县豆瓣酱用150g温水化开，过滤，留汁备用。', '1041');
INSERT INTO `cookbook_marche` VALUES ('1690', '3', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146070333739285.jpg', '猪肉先切片，然后稍微剁碎，加入姜末拌匀，调入白酒，然后将豆瓣酱汁倒入肉中。', '1041');
INSERT INTO `cookbook_marche` VALUES ('1691', '1', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146070113580204.jpg', '需要的材料。', '1042');
INSERT INTO `cookbook_marche` VALUES ('1692', '2', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146070139143463.jpg', '酵母放入35度左右温度的水中融化， 将酵母水一点点的倒进面粉中，边倒水边搅拌面粉，将面粉活成较为光滑的面团，然后放进盆子里，盖上保鲜袋放温暖的地方发酵.', '1042');
INSERT INTO `cookbook_marche` VALUES ('1693', '3', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146070142307443.jpg', '大约一个半小时，面团发成两倍大.', '1042');
INSERT INTO `cookbook_marche` VALUES ('1694', '1', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146073543616197.jpg', '所需材料。', '1043');
INSERT INTO `cookbook_marche` VALUES ('1695', '2', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146073545401204.jpg', '红薯去皮后切着成小段备用。', '1043');
INSERT INTO `cookbook_marche` VALUES ('1696', '3', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146073550740242.jpg', '炒锅烧油，油热后，下入红薯块先中小火慢炸，炸几分钟后开中火上色，炸至金黄色即可盛出，控油，备用。', '1043');
INSERT INTO `cookbook_marche` VALUES ('1697', '1', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146069881292864.jpg', '羊肉放清水中浸泡一晚，将羊肉上的羊油取下来切小块备用', '1044');
INSERT INTO `cookbook_marche` VALUES ('1698', '2', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146069885387769.jpg', '泡好的羊肉放入砂锅中。', '1044');
INSERT INTO `cookbook_marche` VALUES ('1699', '3', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146069893525486.jpg', '放入小茴香和花椒大火烧开，撇去表层浮沫，然后小火炖煮。', '1044');
INSERT INTO `cookbook_marche` VALUES ('1700', '4', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_146069902895896.jpg', '炒锅中放入几滴食用油，然后将取下来的羊油倒入开始小火炸羊油炸至羊油块中的油出尽。', '1044');
INSERT INTO `cookbook_marche` VALUES ('1701', '1', 'https://s1.st.meishij.net/rs/10/174/5293510/n5293510_145775642414005.jpg', '蒜苗，嫩豆腐，西红柿去皮，香菇切成丁。四人份面', '1045');
INSERT INTO `cookbook_marche` VALUES ('1702', '2', 'https://s1.st.meishij.net/rs/10/174/5293510/n5293510_145775642705112.jpg', '热油锅，先倒入香菇，煸炒片刻，倒入西红柿，待西红柿出汁，依次倒入切好的豆腐，蒜苗，倒入适量生抽翻炒，待蒜苗熟后关火，备用.', '1045');
INSERT INTO `cookbook_marche` VALUES ('1703', '3', 'https://s1.st.meishij.net/rs/10/174/5293510/n5293510_145775643019389.jpg', '待西红柿出汁', '1045');
INSERT INTO `cookbook_marche` VALUES ('1704', '4', 'https://s1.st.meishij.net/rs/10/174/5293510/n5293510_145775643330931.jpg', '放入蒜苗', '1045');
INSERT INTO `cookbook_marche` VALUES ('1705', '1', 'https://s1.st.meishij.net/rs/110/142/4723110/n4723110_145646596553872.jpg', '面粉500克加适量清水和2克的盐活成糊糊状，醒制时间久点就行，我是头晚把面糊做好的，放冰箱冷藏了一晚，做好的凉皮很劲道', '1046');
INSERT INTO `cookbook_marche` VALUES ('1706', '2', 'https://s1.st.meishij.net/rs/110/142/4723110/n4723110_145646599972033.jpg', '把面皮锣洗净擦干水，抹入玉米油，舀入两勺糊糊，转动锣让面糊均匀铺满锣', '1046');
INSERT INTO `cookbook_marche` VALUES ('1707', '3', 'https://s1.st.meishij.net/rs/110/142/4723110/n4723110_145646601363404.jpg', '开水锅内放入面皮锣盖锅盖，2分钟面皮起了好多泡泡就熟了', '1046');
INSERT INTO `cookbook_marche` VALUES ('1708', '4', 'https://s1.st.meishij.net/rs/110/142/4723110/n4723110_145646602724630.jpg', '把面皮锣放入一盆凉水中，让它快速凉下来', '1046');
INSERT INTO `cookbook_marche` VALUES ('1709', '1', 'https://s1.st.meishij.net/rs/131/18/5254631/n5254631_145222164320075.jpg', '肉馅步骤：\r\n将五花肉冷水下锅，煮开后去掉血水，用凉水冲干净', '1047');
INSERT INTO `cookbook_marche` VALUES ('1710', '1.5', 'https://s1.st.meishij.net/rs/131/18/5254631/n5254631_145222173496434.jpg', '然后重新下锅，把配料也都放下去，大火煮开后转小火炖煮2小时至肉酥烂（这个等待的时候用来做馍）', '1047');
INSERT INTO `cookbook_marche` VALUES ('1711', '2', 'https://s1.st.meishij.net/rs/131/18/5254631/n5254631_145222181646929.jpg', '趁热将肉切碎，浇上肉汁和葱粒备用\r\n', '1047');
INSERT INTO `cookbook_marche` VALUES ('1712', '1', 'https://s1.st.meishij.net/rs/23/237/621773/n621773_38812.jpg', '五花肉用清水冲洗干净，然后用刀切成3~4段，皮不要切断\r\n', '1048');
INSERT INTO `cookbook_marche` VALUES ('1713', '2', 'https://s1.st.meishij.net/rs/23/237/621773/n621773_67321.jpg', '将五花肉平放在高压锅中皮朝下把盐、老抽、料酒、大葱、清水等材料全部放入高压锅中，压15分钟左右，卤好的肉建议在卤汁中多放一段时间，肉香味更浓', '1048');
INSERT INTO `cookbook_marche` VALUES ('1714', '3', 'https://s1.st.meishij.net/rs/23/237/621773/n621773_29577.jpg', '卤肉准备好后，开始准备饼，将面粉、酵母、泡打粉装和面盆中，用筷子一边搅拌，一边加水，待看不到干面粉时，用手将面粉揉成光滑面团，盖上保鲜膜饧15分钟左右', '1048');
INSERT INTO `cookbook_marche` VALUES ('1715', '4', 'https://s1.st.meishij.net/rs/23/237/621773/n621773_54880.jpg', '面团饧好后，分成6等份', '1048');
INSERT INTO `cookbook_marche` VALUES ('1716', '5', 'https://s1.st.meishij.net/rs/23/237/621773/n621773_66683.jpg', '取其中一个面团擀成椭圆形长条', '1048');
INSERT INTO `cookbook_marche` VALUES ('1717', '1', 'https://s1.st.meishij.net/rs/110/142/4723110/n4723110_144411459684040.jpg', '羊肉泡水中洗静', '1049');
INSERT INTO `cookbook_marche` VALUES ('1718', '2', 'https://s1.st.meishij.net/rs/110/142/4723110/n4723110_144411461551565.jpg', '放锅中开始煮2个小时，放生姜快，料酒少许慢炖', '1049');
INSERT INTO `cookbook_marche` VALUES ('1719', '3', 'https://s1.st.meishij.net/rs/110/142/4723110/n4723110_144411463109011.jpg', '备好羊肉泡馍的小菜。辣椒酱糖蒜香菜末是必不可少', '1049');
INSERT INTO `cookbook_marche` VALUES ('1720', '4', 'https://s1.st.meishij.net/rs/110/142/4723110/n4723110_144411560958547.jpg', '饼烙好后，一分为二，二分为四，掰“蝇头”状大小。烙饼在“虎背菊花心”坨坨馍的菜鋪里有详细说明，亲可以参考', '1049');
INSERT INTO `cookbook_marche` VALUES ('1721', '1', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_143761514611203.jpg', '所需材料', '1050');
INSERT INTO `cookbook_marche` VALUES ('1722', '2', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_143761515699500.jpg', '除土豆外所有的菜切碎备用', '1050');
INSERT INTO `cookbook_marche` VALUES ('1723', '3', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_143761516868909.jpg', '土豆用擦子刨成条状备用。（不能冲水噢）', '1050');
INSERT INTO `cookbook_marche` VALUES ('1724', '4', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_143761517833425.jpg', ' 刨好的土豆条放入盆子中，撒入少量干面粉，搅拌均匀，然后再撒入盐和五香粉，搅拌均匀', '1050');
INSERT INTO `cookbook_marche` VALUES ('1725', '1', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143746978155292.jpg', '小麦粉加少许盐和成面团静置15分钟。面团要稍微硬些', '1051');
INSERT INTO `cookbook_marche` VALUES ('1726', '2', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143746978259059.jpg', '五花肉洗干净，切成块，加盐、料酒搅拌均匀腌制10分钟', '1051');
INSERT INTO `cookbook_marche` VALUES ('1727', '3', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143746978419571.jpg', '土豆、胡萝卜、黄瓜洗干净切成丁', '1051');
INSERT INTO `cookbook_marche` VALUES ('1728', '4', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143746978519745.jpg', '饧好的面团擀成片\r\n', '1051');
INSERT INTO `cookbook_marche` VALUES ('1729', '5', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143746978723953.jpg', '切成稍宽的条备用', '1051');
INSERT INTO `cookbook_marche` VALUES ('1730', '6', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143746979209199.jpg', '锅中加少许油，五花肉入锅煸炒后盛出备用', '1051');
INSERT INTO `cookbook_marche` VALUES ('1731', '7', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143746979443052.jpg', '再起油锅，土豆入锅过油', '1051');
INSERT INTO `cookbook_marche` VALUES ('1732', '8', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143746979845087.jpg', '胡萝卜入锅一同过油后盛出备用', '1051');
INSERT INTO `cookbook_marche` VALUES ('1733', '1', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143738143620944.jpg', '洋芋去皮洗干净。香菜洗干净', '1052');
INSERT INTO `cookbook_marche` VALUES ('1734', '2', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143738143753030.jpg', '洋芋擦碎', '1052');
INSERT INTO `cookbook_marche` VALUES ('1735', '3', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143738143872672.jpg', '擦碎的洋芋中加盐搅拌均匀', '1052');
INSERT INTO `cookbook_marche` VALUES ('1736', '4', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143738144083468.jpg', '\r\n加胡椒粉搅拌均匀', '1052');
INSERT INTO `cookbook_marche` VALUES ('1737', '5', 'https://s1.st.meishij.net/rs/121/15/3441371/n3441371_143738144149793.jpg', '加孜然搅拌均匀', '1052');
INSERT INTO `cookbook_marche` VALUES ('1738', '1', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143721576362964.jpg', '将面粉和2g盐放入盆里，倒水和面', '1053');
INSERT INTO `cookbook_marche` VALUES ('1739', '2', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143721579153303.jpg', '和成面团后盖上保鲜膜饧20分钟再揉，这样反复三次', '1053');
INSERT INTO `cookbook_marche` VALUES ('1740', '3', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143721583744023.jpg', '把揉好的面团分割成2份，盖上保鲜膜饧30分钟', '1053');
INSERT INTO `cookbook_marche` VALUES ('1741', '4', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143721587800683.jpg', '准备好蒜末、葱花及少许香菜', '1053');
INSERT INTO `cookbook_marche` VALUES ('1742', '5', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143721593475587.jpg', '把饧好的面团放在面板上，撒上面粉防粘', '1053');
INSERT INTO `cookbook_marche` VALUES ('1743', '6', 'https://s1.st.meishij.net/rs/147/198/4174647/n4174647_143721598927280.jpg', '把面团干成大的面片后（不要太薄），在面片的两面撒上面粉', '1053');
INSERT INTO `cookbook_marche` VALUES ('1744', '1', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143722879329526.jpg', '面粉、水、酵母和成面团，放置一边发酵', '1054');
INSERT INTO `cookbook_marche` VALUES ('1745', '2', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143722880478403.jpg', '不需要发的太大，比平时做馒头发酵的时间短一半；面团使劲揉光滑，搓成长条', '1054');
INSERT INTO `cookbook_marche` VALUES ('1746', '3', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143722881646371.jpg', '分切成10个剂子', '1054');
INSERT INTO `cookbook_marche` VALUES ('1747', '4', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143722882973593.jpg', '取一个剂子压扁擀长，从上往下卷起来', '1054');
INSERT INTO `cookbook_marche` VALUES ('1748', '5', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143722885187094.jpg', '卷好的面团', '1054');
INSERT INTO `cookbook_marche` VALUES ('1749', '6', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143722888376048.jpg', '全部卷好后，醒发10-15分钟', '1054');
INSERT INTO `cookbook_marche` VALUES ('1750', '7', 'https://s1.st.meishij.net/rs/139/60/2827639/n2827639_143722889826247.jpg', '醒发后的面团压扁，擀成圆片，中间凹，四周稍高；放入已烧热的平底锅中，大火烙', '1054');
INSERT INTO `cookbook_marche` VALUES ('1751', '1', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143709338198891.jpg', '面粉分次加水、1克盐揉匀，静置30分钟', '1055');
INSERT INTO `cookbook_marche` VALUES ('1752', '2', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143709338219122.jpg', '面团再次揉光后切成小剂子', '1055');
INSERT INTO `cookbook_marche` VALUES ('1753', '3', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143711496417763.jpg', '搓成长条后抹油，盖保鲜膜醒1小时', '1055');
INSERT INTO `cookbook_marche` VALUES ('1754', '4', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143711496499010.jpg', '醒好的面擀开，中间压一下', '1055');
INSERT INTO `cookbook_marche` VALUES ('1755', '5', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143709338354626.jpg', '双手慢慢将面片拉长', '1055');
INSERT INTO `cookbook_marche` VALUES ('1756', '1', 'https://s1.st.meishij.net/rs/26/98/3774526/n3774526_143710916260932.jpg', '豆芽和青菜洗干净', '1056');
INSERT INTO `cookbook_marche` VALUES ('1757', '2', 'https://s1.st.meishij.net/rs/26/98/3774526/n3774526_143710919522057.jpg', '小辣椒和葱花准备好', '1056');
INSERT INTO `cookbook_marche` VALUES ('1758', '3', 'https://s1.st.meishij.net/rs/26/98/3774526/n3774526_143710925393332.jpg', '水烧开后，放入面条，青菜和豆芽，软硬程度自己调整', '1056');
INSERT INTO `cookbook_marche` VALUES ('1759', '1', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_143705611057899.jpg', '材料', '1057');
INSERT INTO `cookbook_marche` VALUES ('1760', '2', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_143705612483023.jpg', '这个就是马齿苋', '1057');
INSERT INTO `cookbook_marche` VALUES ('1761', '3', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_143705613921527.jpg', ' 洗净切长两三厘米的段', '1057');
INSERT INTO `cookbook_marche` VALUES ('1762', '4', 'https://s1.st.meishij.net/rs/245/200/4487745/n4487745_143705615573835.jpg', ' 先撒入少量面粉，搅拌匀，然后撒入适量盐拌匀', '1057');
INSERT INTO `cookbook_marche` VALUES ('1763', '1', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143660951918687.jpg', '面粉加酵母、泡打粉、碱，和好，醒发半小时', '1058');
INSERT INTO `cookbook_marche` VALUES ('1764', '2', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143660952000359.jpg', '每100g分成小剂，搓成长条，擀开，从一头卷起，再压成圆饼，用擀面杖擀成中间薄的小碗状，上电饼铛', '1058');
INSERT INTO `cookbook_marche` VALUES ('1765', '3', 'https://s1.st.meishij.net/rs/24/169/4479774/n4479774_143660952130759.jpg', '翻面，注意是干烙，不要加油', '1058');
INSERT INTO `cookbook_marche` VALUES ('1766', '1', 'https://s1.st.meishij.net/rs/158/87/4021908/n4021908_143582183552885.jpg', '备好澄粉和面粉', '1059');
INSERT INTO `cookbook_marche` VALUES ('1767', '1.5', 'https://s1.st.meishij.net/rs/158/87/4021908/n4021908_143582183736285.jpg', '混合倒入料理盆中', '1059');
INSERT INTO `cookbook_marche` VALUES ('1768', '2.5', 'https://s1.st.meishij.net/rs/158/87/4021908/n4021908_143582184464625.jpg', '并筷子先搅拌均匀', '1059');
INSERT INTO `cookbook_marche` VALUES ('1769', '3', 'https://s1.st.meishij.net/rs/158/87/4021908/n4021908_143582184775386.jpg', '再慢慢的加入水，搅拌至无颗粒', '1059');
INSERT INTO `cookbook_marche` VALUES ('1770', '1', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_87520.jpg', '准备200克面粉', '1060');
INSERT INTO `cookbook_marche` VALUES ('1771', '2', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_41950.jpg', '面粉里加入适量清水拌合成棉絮桩', '1060');
INSERT INTO `cookbook_marche` VALUES ('1772', '3', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_46366.jpg', '把面团揉成面团，要软硬适度，太软太硬都不方便后面的操作，静止20分钟', '1060');
INSERT INTO `cookbook_marche` VALUES ('1773', '4', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_78846.jpg', '静止好的面团加水开始洗面，揉压面团', '1060');
INSERT INTO `cookbook_marche` VALUES ('1774', '5', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_97117.jpg', '反复的揉洗，洗到水变白白地', '1060');
INSERT INTO `cookbook_marche` VALUES ('1775', '6', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_87773.jpg', '洗出的面粉水倒入另一个盆中，继续加水洗面团', '1060');
INSERT INTO `cookbook_marche` VALUES ('1776', '7', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_19504.jpg', '洗面团的目的就将淀粉充分洗出来，最后只剩面筋', '1060');
INSERT INTO `cookbook_marche` VALUES ('1777', '8', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_23468.jpg', '准备配菜，黄瓜、胡萝卜、细香葱、蒜', '1060');
INSERT INTO `cookbook_marche` VALUES ('1778', '9', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_09345.jpg', '胡萝卜和黄瓜切成细丝待用', '1060');
INSERT INTO `cookbook_marche` VALUES ('1779', '10', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_77679.jpg', '细香葱和蒜切成末', '1060');
INSERT INTO `cookbook_marche` VALUES ('1780', '1', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_22091.jpg', '盆里放入面粉300克', '1061');
INSERT INTO `cookbook_marche` VALUES ('1781', '2', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_24295.jpg', '小碗里加适量的盐用清水化开，然后慢慢加入面粉中', '1061');
INSERT INTO `cookbook_marche` VALUES ('1782', '3', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_19881.jpg', '把面粉搅成棉絮状', '1061');
INSERT INTO `cookbook_marche` VALUES ('1783', '4', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_15737.jpg', '在揉至光滑盖上保鲜膜静置30分钟', '1061');
INSERT INTO `cookbook_marche` VALUES ('1784', '5', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_83369.jpg', '然后切成差不多30克左右的小面团揉成扁成条', '1061');
INSERT INTO `cookbook_marche` VALUES ('1785', '6', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_61571.jpg', '每个面团在揉成条状后抹油，再次静置20分钟', '1061');
INSERT INTO `cookbook_marche` VALUES ('1786', '1', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_99996.jpg', '准备制作素臊子的食材：胡萝卜1/2根、蒜薹100克、干黄花20克、干木耳20克、炸豆腐80克，黄花、木耳、胡萝卜、豆腐这些都是陕西臊子常见的食材，还可以根据季节选择时令蔬菜，例如豆角、蒜薹、土豆等等，每种食材的量可随喜好搭配', '1062');
INSERT INTO `cookbook_marche` VALUES ('1787', '2', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_79016.jpg', '胡萝卜切成丁，炸豆腐切丁，蒜薹切丁，黄花木耳泡发后切碎', '1062');
INSERT INTO `cookbook_marche` VALUES ('1788', '3', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_41230.jpg', '热锅倒入25克油爆香葱、姜末', '1062');
INSERT INTO `cookbook_marche` VALUES ('1789', '4', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_78525.jpg', '先倒入胡萝卜翻炒', '1062');
INSERT INTO `cookbook_marche` VALUES ('1790', '5', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_52220.jpg', '再依次倒入黄花、木耳、炸豆腐、蒜薹翻炒2分钟左右，如果锅太干可以加入小半碗水', '1062');
INSERT INTO `cookbook_marche` VALUES ('1791', '6', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_11514.jpg', '加入1/2小勺五香粉', '1062');
INSERT INTO `cookbook_marche` VALUES ('1792', '7', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_90877.jpg', '加入1小勺盐即可，盛出备用', '1062');
INSERT INTO `cookbook_marche` VALUES ('1793', '1', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_62227.jpg', '准备原料，酱辣子多以莲藕和胡萝卜为原料，也可以单选莲藕，莲藕口感脆脆的，个人更偏爱莲藕', '1063');
INSERT INTO `cookbook_marche` VALUES ('1794', '2', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_33330.jpg', '葱姜各20克，辣椒粉2大勺，黄豆酱2大勺，芝麻1大勺，杏仁1大勺，辣椒粉是陕西当地的秦椒辣面，杏仁选的是山杏仁不是美国大杏仁', '1063');
INSERT INTO `cookbook_marche` VALUES ('1795', '3', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_42782.jpg', '莲藕切成薄片', '1063');
INSERT INTO `cookbook_marche` VALUES ('1796', '4', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_53711.jpg', '再切细丝\r\n', '1063');
INSERT INTO `cookbook_marche` VALUES ('1797', '5', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_22426.jpg', '斩切成细末，胡萝卜也切同样大小的细末，尽量细碎一些，不要太大', '1063');
INSERT INTO `cookbook_marche` VALUES ('1798', '6', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_21211.jpg', '锅热好倒入菜籽油，油大约200克，尽量选择菜籽油，为了便于保存这个油的量要多一些，太少的话酱辣子会很干涩', '1063');
INSERT INTO `cookbook_marche` VALUES ('1799', '7', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_01377.jpg', '油温5成热时，炒香葱、姜末，下入黄豆酱煸炒出香味大约30秒左右即可，正宗应该选用当地产的黄豆酱，没有用其他黄豆酱或者干黄酱代替味道很不错', '1063');
INSERT INTO `cookbook_marche` VALUES ('1800', '1', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_63973.jpg', '面里加一点盐，凉水和面', '1064');
INSERT INTO `cookbook_marche` VALUES ('1801', '2', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_70569.jpg', '揉光揉圆后醒制30分钟', '1064');
INSERT INTO `cookbook_marche` VALUES ('1802', '3', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_60583.jpg', '擀开成1毫米左右薄片，擀面是个熟练活儿，多做就可以', '1064');
INSERT INTO `cookbook_marche` VALUES ('1803', '4', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_36117.jpg', '卷起一切两半后切宽片', '1064');
INSERT INTO `cookbook_marche` VALUES ('1804', '1', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_24868.jpg', '准备300克高筋面粉，做6张10寸披萨盘的量', '1065');
INSERT INTO `cookbook_marche` VALUES ('1805', '2', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_77816.jpg', '面粉里加入清水适量拌合成棉絮', '1065');
INSERT INTO `cookbook_marche` VALUES ('1806', '2.5', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_71823.jpg', '把面团揉成面团，要软硬适度，太软太硬都不方便后面的操作，静止20分钟', '1065');
INSERT INTO `cookbook_marche` VALUES ('1807', '3.5', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_72492.jpg', '静止好的面团加水开始洗面，揉压面团', '1065');
INSERT INTO `cookbook_marche` VALUES ('1808', '4.5', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_82709.jpg', '反复的揉洗，洗到水变白', '1065');
INSERT INTO `cookbook_marche` VALUES ('1809', '5.5', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_46942.jpg', '洗出的面粉水倒入另一个盆中，继续加水洗面团，洗好的面粉水需要很长时间的沉淀，大致1小时', '1065');
INSERT INTO `cookbook_marche` VALUES ('1810', '6.5', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_29302.jpg', '洗面团的目的就将淀粉充分洗出来，最后只剩面筋', '1065');
INSERT INTO `cookbook_marche` VALUES ('1811', '7.5', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_29291.jpg', '胡萝卜和黄瓜切成细丝待用', '1065');
INSERT INTO `cookbook_marche` VALUES ('1812', '8.5', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_50378.jpg', '香葱、大蒜切成末', '1065');
INSERT INTO `cookbook_marche` VALUES ('1813', '9.5', 'https://s1.st.meishij.net/rs/208/36/1259208/n1259208_80856.jpg', '将表面清澈的水倒干净，留下底部白色的粉浆水，剩下面粉水的状态', '1065');
INSERT INTO `cookbook_marche` VALUES ('1814', '1', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_55329.jpg', '把少许干花椒叶和小茴香用小火炒香，并捣碎（花椒叶是好吃的关键，如果实在没有就不加了，可不敢加花椒哦）', '1066');
INSERT INTO `cookbook_marche` VALUES ('1815', '2', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_01771.jpg', '盆中加入面粉和玉米面粉（注意是玉米面粉，不是淀粉，如果没有就全用中筋面粉），加入盐和泡打粉拌匀', '1066');
INSERT INTO `cookbook_marche` VALUES ('1816', '3', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_22744.jpg', '打入一个中等大小鸡蛋，加入20克菜籽油（陕西正宗的常用菜籽油，没有可以用其他植物油代替）\r\n', '1066');
INSERT INTO `cookbook_marche` VALUES ('1817', '4', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_41886.jpg', '30克左右的水溶化四分之一小勺酵母，慢慢加入面粉中，用手搅拌和面，成较为硬的面团', '1066');
INSERT INTO `cookbook_marche` VALUES ('1818', '5', 'https://s1.st.meishij.net/rs/17/110/2090017/n2090017_80377.jpg', '盖上保鲜膜醒发30分钟，再次揉匀揉光后擀成8毫米左右的长方形厚片', '1066');
INSERT INTO `cookbook_marche` VALUES ('1819', '1', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_88459.jpeg', '五花肉用小刀切成2厘米宽的正方体（小刀比较好切，最好在解冻到一半的时候切是最容易切的）', '1073');
INSERT INTO `cookbook_marche` VALUES ('1820', '2', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_88885.jpeg', '做红烧肉需要的料先备齐：大葱切片、大蒜去皮、干辣椒剪段、大料和香叶洗净', '1073');
INSERT INTO `cookbook_marche` VALUES ('1821', '3', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_42352.jpeg', '热锅烧油（油少许，五花肉已经有很多油了），5成热时加入五花肉小火翻炒变色', '1073');
INSERT INTO `cookbook_marche` VALUES ('1822', '4', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_23440.jpg', '加入香料一块小火煸炒，炒出香味', '1073');
INSERT INTO `cookbook_marche` VALUES ('1823', '5', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_06027.jpg', '下调料：生抽2勺、老抽2勺、料酒2勺，开大火烧开，加水没过五花肉', '1073');
INSERT INTO `cookbook_marche` VALUES ('1824', '6', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_55444.jpg', '大火烧5分钟后关成中小火煮一个小时后改成小火一直煮到剩一点汤汁就可以盛出（记得去掉大葱蒜大料那些料），放凉备用', '1073');
INSERT INTO `cookbook_marche` VALUES ('1825', '6.5', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_41727.jpg', '由于制作红烧肉比较花时间，中间这段煮的过程可以利用起来和面。\r\n这次用的面粉是自发面粉，里边已经添加了酵母粉的，所以不用另外加酵母，把适量面粉倒入盆里，加一勺盐搅拌均匀', '1073');
INSERT INTO `cookbook_marche` VALUES ('1826', '7.5', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_14331.jpg', '加入适量温水（30-40度），用筷子沿顺时针方向搅拌成形', '1073');
INSERT INTO `cookbook_marche` VALUES ('1827', '8.5', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_51594.jpg', '搓揉成光滑偏软的面团', '1073');
INSERT INTO `cookbook_marche` VALUES ('1828', '9.5', 'https://s1.st.meishij.net/rs/33/140/1722533/n1722533_50309.jpg', '盖上湿的屉布，放在温暖的地方醒30分钟以上', '1073');
INSERT INTO `cookbook_marche` VALUES ('1829', '1', 'https://s1.st.meishij.net/rs/117/242/1123117/n1123117_42195.jpg', '面粉内加入少许盐，再加入适量水和成干湿适中的面团', '1077');
INSERT INTO `cookbook_marche` VALUES ('1830', '2', 'https://s1.st.meishij.net/rs/117/242/1123117/n1123117_88910.jpg', '面团先盖上保鲜膜，再盖上湿布静置30分钟', '1077');
INSERT INTO `cookbook_marche` VALUES ('1831', '3', 'https://s1.st.meishij.net/rs/117/242/1123117/n1123117_62170.jpg', '盆内放入水，把面团放入像洗衣服一样反复搓洗', '1077');
INSERT INTO `cookbook_marche` VALUES ('1832', '4', 'https://s1.st.meishij.net/rs/117/242/1123117/n1123117_21082.jpg', '当水变的很白时把水过滤到大点的容器中，然后再加入水接着洗白后滤出', '1077');
INSERT INTO `cookbook_marche` VALUES ('1833', '5', 'https://s1.st.meishij.net/rs/117/242/1123117/n1123117_04780.jpg', '直到洗的水变清，只剩下发黄的面筋即可', '1077');
INSERT INTO `cookbook_marche` VALUES ('1834', '6', 'https://s1.st.meishij.net/rs/117/242/1123117/n1123117_90113.jpg', '过滤出来的水静置4至5小时', '1077');
INSERT INTO `cookbook_marche` VALUES ('1835', '7', 'https://s1.st.meishij.net/rs/117/242/1123117/n1123117_56208.jpg', '面筋用流水冲洗一下', '1077');
INSERT INTO `cookbook_marche` VALUES ('1836', '8', 'https://s1.st.meishij.net/rs/117/242/1123117/n1123117_13975.jpg', '往面筋里加入1克酵母，揉匀后盖好发酵至1倍大', '1077');
