INSERT INTO `cookbook_marche` VALUES ('1940', '1', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_146279366525441.jpg', '新鲜槐花。', '1436');
INSERT INTO `cookbook_marche` VALUES ('1941', '2', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_146279366637507.jpg', '槐花加少许盐，浸泡29分钟后洗净，沥干水分。', '1436');
INSERT INTO `cookbook_marche` VALUES ('1942', '3', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_146279366621818.jpg', '槐花里加适量面粉，拌匀至盆底无干粉。', '1436');
INSERT INTO `cookbook_marche` VALUES ('1943', '4', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_146279366719156.jpg', '放入蒸锅，开火。', '1436');
INSERT INTO `cookbook_marche` VALUES ('1944', '5', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_146279366781678.jpg', '蒸15分钟后用筷子翻一翻。', '1436');
INSERT INTO `cookbook_marche` VALUES ('1945', '6', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_146279384439000.jpg', '蒸好的槐花不烂。', '1436');
INSERT INTO `cookbook_marche` VALUES ('1946', '1', 'https://s1.st.meishij.net/rs/247/67/5579497/n5579497_146078616799734.jpg', '材料：刀削面、猪肉丝、胡萝卜丝、黑木耳丝、葱花、姜、蒜末。肉丝用料酒、水淀粉拌匀。', '1437');
INSERT INTO `cookbook_marche` VALUES ('1947', '2', 'https://s1.st.meishij.net/rs/247/67/5579497/n5579497_146078617185064.jpg', '刀削面，放入沸水煮8分钟后捞起，冲冷水至凉后捞起沥干。', '1437');
INSERT INTO `cookbook_marche` VALUES ('1948', '1', 'https://s1.st.meishij.net/rs/85/181/2732835/n2732835_146017875763692.jpg', '土豆去皮洗干净（只洗这一次）切约2cm丝，加适量盐和花椒粉拌匀！', '1438');
INSERT INTO `cookbook_marche` VALUES ('1949', '2', 'https://s1.st.meishij.net/rs/85/181/2732835/n2732835_146017875922577.jpg', '把面粉少量多次加入土豆里，直至土豆丝都均匀地裹上面粉衣！（面粉的量要自己看着调整）', '1438');
INSERT INTO `cookbook_marche` VALUES ('1950', '3', 'https://s1.st.meishij.net/rs/85/181/2732835/n2732835_146017876187093.jpg', '蒸锅烧开水，把上面拌好的土豆先铺一层盖好蒸，约八分熟再撒一层，到全部完成', '1438');
INSERT INTO `cookbook_marche` VALUES ('1951', '4', 'https://s1.st.meishij.net/rs/85/181/2732835/n2732835_146017876338157.jpg', '趁蒸的时候准备下辅料', '1438');
INSERT INTO `cookbook_marche` VALUES ('1952', '1', 'https://s1.st.meishij.net/rs/205/129/4157455/n4157455_145239115655058.jpg', '先准备食材，卷卷可做凉菜也可以做热菜，所以肉选择瘦肉多肥肉少，500克，先把肉冷冻在冰箱里，一伙好切丝，土豆比肉的量多一些700克，葱选葱白部分，葱的量是肉量的一半，250克，姜8克，十三香5克，盐20克，花椒6克，3克是准备料水，3克做花椒油的，淀粉50克，面粉100克', '1439');
INSERT INTO `cookbook_marche` VALUES ('1953', '2', 'https://s1.st.meishij.net/rs/205/129/4157455/n4157455_145239196642472.jpg', '先把土豆上锅蒸大概20分钟，用筷子试一试土豆能不能戳穿，不行的话，再蒸一蒸，蒸好的土豆剥皮，装入保鲜袋，用擀面杖压成土豆泥，蒸时多蒸几个，在拌馅料时感觉软了，还可以添加做调整', '1439');
INSERT INTO `cookbook_marche` VALUES ('1954', '3', 'https://s1.st.meishij.net/rs/205/129/4157455/n4157455_145239215779196.jpg', '取花椒3克，沸水15克，泡料水，或水稍多些煮一下', '1439');
INSERT INTO `cookbook_marche` VALUES ('1955', '4', 'https://s1.st.meishij.net/rs/205/129/4157455/n4157455_145239237925799.jpg', '冰箱取出肉切丝', '1439');
INSERT INTO `cookbook_marche` VALUES ('1956', '5', 'https://s1.st.meishij.net/rs/205/129/4157455/n4157455_145239269270426.jpg', '切葱末剁姜泥', '1439');
INSERT INTO `cookbook_marche` VALUES ('1957', '6', 'https://s1.st.meishij.net/rs/205/129/4157455/n4157455_145239312193081.jpg', '热锅，10克多凉油放3克花椒，花椒变色，关火漏网勺取出花椒，花椒油泼在馅料上', '1439');
INSERT INTO `cookbook_marche` VALUES ('1958', '7', 'https://s1.st.meishij.net/rs/205/129/4157455/n4157455_145239339773130.jpg', '把肉丝、土豆泥、葱、姜、盐、十三香、面粉、淀粉、料水去掉花椒，放入大盆中，热花椒油泼在上面，戴薄膜手套拌匀', '1439');
INSERT INTO `cookbook_marche` VALUES ('1959', '8', 'https://s1.st.meishij.net/rs/205/129/4157455/n4157455_145239379111061.jpg', '拌匀是稍稠的感觉，要是拌稀了，可以稍添点土豆泥调整', '1439');
INSERT INTO `cookbook_marche` VALUES ('1960', '9', 'https://s1.st.meishij.net/rs/205/129/4157455/n4157455_145239414384183.jpg', '把馅料准备好后，开始做蛋皮，取3克淀粉加10克多水，和蛋液加在一起拌均匀，7到8个鸡蛋差不多就够了', '1439');
INSERT INTO `cookbook_marche` VALUES ('1961', '1', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_145077699882963.jpg', '原料。', '1440');
INSERT INTO `cookbook_marche` VALUES ('1962', '2', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_145077699997248.jpg', '花生油加热。', '1440');
