INSERT INTO `cookbook_marche` VALUES ('2097', '2', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146107299825352.jpg', '馄饨皮里面放一坨红豆沙', '1697');
INSERT INTO `cookbook_marche` VALUES ('2098', '3', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146107300749678.jpg', '四周抹上水', '1697');
INSERT INTO `cookbook_marche` VALUES ('2099', '4', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146107301795210.jpg', '对角折起来，压紧', '1697');
INSERT INTO `cookbook_marche` VALUES ('2100', '5', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146107302928354.jpg', '全部做好', '1697');
INSERT INTO `cookbook_marche` VALUES ('2101', '1', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146104911731760.jpg', '新鲜的茼蒿采摘回来后，细心挑捡去里面的杂质，去掉比较老的茎秆叶片后用清水洗干净。', '1698');
INSERT INTO `cookbook_marche` VALUES ('2102', '2', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146104912371234.jpg', '将沥干水的茼蒿切碎,越碎越好.。', '1698');
INSERT INTO `cookbook_marche` VALUES ('2103', '3', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146104912924305.jpg', '切碎的茼蒿放入自来水中，用手使劲搓揉，水变绿了再换水揉，直到水变清了为止。经过多次搓揉，茼蒿的味基本上都去除干净了，就可以进行下一步的操作。', '1698');
INSERT INTO `cookbook_marche` VALUES ('2104', '4', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146104913571759.jpg', '把茼蒿捞出来，用力将水拧干，放入大盆里，把糯米粉和白糖也倒进去，边揉边加水，揉成较硬的团。（面团一定要硬，水慢慢加入，揉至没有干粉，能捏成团的状态就可以了。）像揉面团一样使劲搓揉，揉得越好，做出来的粑粑口感越劲道。如果不喜欢吃出明显的茼蒿茎叶，也可以将茼蒿加少量水放入料理机中打碎，再和糯米粉、白糖混合成团，放在一边醒十分钟左右。', '1698');
INSERT INTO `cookbook_marche` VALUES ('2105', '5', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146104914171843.jpg', '醒面团的时候我们就可以处理馅料了。花生米炒香去皮（或者放入烤箱中烤香）放入保鲜袋，用擀面杖擀成碎颗粒状，花生碎加入炒香的黑芝麻、糖，再放进少许软化的黄油，用手搓匀。如果没有黄油，用猪油或者植物油代替，馅料里面的坚果可以随意按照家人的喜好调节哦。加了油脂的馅料，不但可以让各种坚果粘连在一起，更能让口感变得细腻油滑。如果不喜欢干果的粗颗粒口感的，同样可以将馅料里面的各种干果用料理机打碎再操作哦！', '1698');
INSERT INTO `cookbook_marche` VALUES ('2106', '1', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146097034299142.jpg', '火龙果皮榨汁，将糯米粉分成三份，分别加入糖粉、火龙果皮汁、抹茶粉，揉成三种颜色的粉团。', '1699');
INSERT INTO `cookbook_marche` VALUES ('2107', '2', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146097034659582.jpg', '将白色的糯米粉取出大约三分之一，蒸熟', '1699');
INSERT INTO `cookbook_marche` VALUES ('2108', '3', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146097035336881.jpg', '将蒸熟的糯米团分别加入三个颜色的粉团中，揉均匀', '1699');
INSERT INTO `cookbook_marche` VALUES ('2109', '1', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146096159342473.jpg', '鱿鱼去皮处理干净，沥干水备用\r\n', '1700');
INSERT INTO `cookbook_marche` VALUES ('2110', '2', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146096161280291.jpg', '糯米饭蒸熟\r\n', '1700');
INSERT INTO `cookbook_marche` VALUES ('2111', '3', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146096163423127.jpg', '葱花切好，肉末切好。所有的食材备用\r\n', '1700');
INSERT INTO `cookbook_marche` VALUES ('2112', '4', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146096164352360.jpg', '油锅烧热，炒香肉末\r\n', '1700');
INSERT INTO `cookbook_marche` VALUES ('2113', '5', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146096165104522.jpg', '把糯米饭倒进去翻炒片刻，放盐\r\n', '1700');
INSERT INTO `cookbook_marche` VALUES ('2114', '6', 'https://s1.st.meishij.net/rs/208/102/1025708/n1025708_146096166918722.jpg', '放入辣椒粉\r\n', '1700');
INSERT INTO `cookbook_marche` VALUES ('2115', '1', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_143851839814435.jpg', '小龙虾', '1701');
INSERT INTO `cookbook_marche` VALUES ('2116', '2', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_143851839905577.jpg', '先把小龙虾尾翅中间一片拉出抽出虾肠', '1701');
INSERT INTO `cookbook_marche` VALUES ('2117', '3', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_143851840034129.jpg', '再反复清洗至水清撤不浑浊后再加入清水能浸没龙虾加入盐静养15分钟后洗净捞起', '1701');
INSERT INTO `cookbook_marche` VALUES ('2118', '4', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_143851840130677.jpg', '准备所有调料（小葱葱白和葱叶分开切）', '1701');
INSERT INTO `cookbook_marche` VALUES ('2119', '5', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_143851840289475.jpg', '热锅倒入一勺油，温油倒入花椒（就是图片上的这种勺子）', '1701');
INSERT INTO `cookbook_marche` VALUES ('2120', '6', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_143851840386749.jpg', '炸出香味后捞起不用', '1701');
INSERT INTO `cookbook_marche` VALUES ('2121', '1', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143737680519397.jpg', '买回来的豆腐用清水浸泡十来二十分钟左右捞起沥干水分', '1702');
INSERT INTO `cookbook_marche` VALUES ('2122', '2', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143737680714576.jpg', '把豆腐切成大小合适的块，摆放在蒸屉里，再盖上纱布，放温暖的地方让它发霉', '1702');
INSERT INTO `cookbook_marche` VALUES ('2123', '3', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143737680875151.jpg', '约一星期，豆腐表面变成黄色，而且有一层粘液这样就好了，假如是黑色的就是不行了', '1702');
INSERT INTO `cookbook_marche` VALUES ('2124', '4', 'https://s1.st.meishij.net/rs/82/49/4324832/n4324832_143737680905416.jpg', '自晒的干辣椒用料理机打成末（不能吃太辣的可以像我这样自己买不辣的红椒晒干，再加进一两个辣的一起打成末）', '1702');
INSERT INTO `cookbook_marche` VALUES ('2125', '1', 'https://s1.st.meishij.net/rs/41/143/2723291/n2723291_143728387288271.jpg', '准备食材，糯米粉、红糖', '1703');
INSERT INTO `cookbook_marche` VALUES ('2126', '2', 'https://s1.st.meishij.net/rs/41/143/2723291/n2723291_143728392872175.jpg', '糯米粉加入温水揉成面团', '1703');
INSERT INTO `cookbook_marche` VALUES ('2127', '3', 'https://s1.st.meishij.net/rs/41/143/2723291/n2723291_143728389616592.jpg', '分成几个小份', '1703');
INSERT INTO `cookbook_marche` VALUES ('2128', '4', 'https://s1.st.meishij.net/rs/41/143/2723291/n2723291_143728399915091.jpg', '用手心搓圆压扁', '1703');
INSERT INTO `cookbook_marche` VALUES ('2129', '5', 'https://s1.st.meishij.net/rs/41/143/2723291/n2723291_143728404859997.jpg', '红糖加入热水化开', '1703');
INSERT INTO `cookbook_marche` VALUES ('2130', '1', 'https://s1.st.meishij.net/rs/62/67/3891812/n3891812_143720119511112.jpg', '和糯米面：取大概300克左右的糯米面，加入适量的温水和面，将面和好后，醒大概2小时左右', '1704');
INSERT INTO `cookbook_marche` VALUES ('2131', '2', 'https://s1.st.meishij.net/rs/62/67/3891812/n3891812_143720122217925.jpg', '准备炒芝麻：先将锅烧热，然后关火，取大约20g左右的生白芝麻到入加热好的锅中', '1704');
INSERT INTO `cookbook_marche` VALUES ('2132', '3', 'https://s1.st.meishij.net/rs/62/67/3891812/n3891812_143720123834895.jpg', '炒芝麻：生白芝麻到入加热好的锅中反复翻炒1分钟后，然后再打开火，打到最小档，一直用小火反复炒，直到芝麻变成谈黄色，就立即将炒好的芝麻盛出', '1704');
INSERT INTO `cookbook_marche` VALUES ('2133', '4', 'https://s1.st.meishij.net/rs/62/67/3891812/n3891812_143720149429577.jpg', '准备剂子：将醒好的糯米面，分成大约5g左右的小剂子，然后将糯米面搓圆按成圆饼状，做出糯米圆子备用', '1704');
INSERT INTO `cookbook_marche` VALUES ('2134', '5', 'https://s1.st.meishij.net/rs/62/67/3891812/n3891812_143720174090946.jpg', '热油：热锅中倒入约400ml左右的调和食用油，油温大约达到5成热时，就可以准备炒粑粑了', '1704');
INSERT INTO `cookbook_marche` VALUES ('2135', '1', 'https://s1.st.meishij.net/rs/62/147/599312/n599312_40562.jpg', '准备糯米粉', '1705');
INSERT INTO `cookbook_marche` VALUES ('2136', '2', 'https://s1.st.meishij.net/rs/62/147/599312/n599312_65184.jpg', '取小碗，放入红糖和冰糖', '1705');
INSERT INTO `cookbook_marche` VALUES ('2137', '3', 'https://s1.st.meishij.net/rs/62/147/599312/n599312_71236.jpg', '糯米粉种一边加水一边用筷子搅拌均匀，搅拌至干稀适中的程度，揉成面团', '1705');
INSERT INTO `cookbook_marche` VALUES ('2138', '4', 'https://s1.st.meishij.net/rs/62/147/599312/n599312_50043.jpg', '再加入白糖、蜂蜜和适量开水搅拌至糖粒完全溶化', '1705');
INSERT INTO `cookbook_marche` VALUES ('2139', '5', 'https://s1.st.meishij.net/rs/62/147/599312/n599312_59447.jpg', '将揉好的糯米粉团分割成约60克1个的小剂子', '1705');
INSERT INTO `cookbook_marche` VALUES ('2140', '6', 'https://s1.st.meishij.net/rs/62/147/599312/n599312_72988.jpg', '逐一搓圆拍扁', '1705');
INSERT INTO `cookbook_marche` VALUES ('2141', '7', 'https://s1.st.meishij.net/rs/62/147/599312/n599312_29732.jpg', '煎锅倒入70克油，中火加热，至油温四成热时转小火', '1705');
INSERT INTO `cookbook_marche` VALUES ('2142', '1', 'https://s1.st.meishij.net/rs/150/171/1480400/n1480400_27942.jpg', '黄鸭叫洗净、控水，豆腐切块，葱切段，姜切片备用。', '1707');
INSERT INTO `cookbook_marche` VALUES ('2143', '2', 'https://s1.st.meishij.net/rs/150/171/1480400/n1480400_31780.jpg', '炒锅内放入适量植物油，烧七成热后将黄鸭叫放入锅内，两面煎一两分钟。', '1707');
INSERT INTO `cookbook_marche` VALUES ('2144', '3', 'https://s1.st.meishij.net/rs/150/171/1480400/n1480400_42902.jpg', '锅内加水，盖过黄鸭叫，放入葱姜。', '1707');
INSERT INTO `cookbook_marche` VALUES ('2145', '1', 'https://s1.st.meishij.net/rs/22/225/5306272/n5306272_148826970712603.jpg', '材料备好洗净，我买到海带丝是盐腌制的要泡一下水，豇豆角洗净切段，肉切片，西红柿切快', '1787');
INSERT INTO `cookbook_marche` VALUES ('2146', '2', 'https://s1.st.meishij.net/rs/22/225/5306272/n5306272_148826971439205.jpg', '准备好葱姜蒜和卤料包（这里我用的卤料包是现成的）', '1787');
INSERT INTO `cookbook_marche` VALUES ('2147', '3', 'https://s1.st.meishij.net/rs/22/225/5306272/n5306272_148826972163254.jpg', '锅里放适量油', '1787');
INSERT INTO `cookbook_marche` VALUES ('2148', '4', 'https://s1.st.meishij.net/rs/22/225/5306272/n5306272_148826972860149.jpg', '油热后加入葱姜蒜和和肉翻炒', '1787');
INSERT INTO `cookbook_marche` VALUES ('2149', '5', 'https://s1.st.meishij.net/rs/22/225/5306272/n5306272_148826973777423.jpg', '加入酱油，豆瓣酱和老抽翻炒', '1787');
INSERT INTO `cookbook_marche` VALUES ('2150', '6', 'https://s1.st.meishij.net/rs/22/225/5306272/n5306272_148826980066725.jpg', '加入水，盐和卤料包，山楂也放进去', '1787');
INSERT INTO `cookbook_marche` VALUES ('2151', '1', 'https://s1.st.meishij.net/rs/236/94/8898736/n8898736_148281059997889.jpg', '芝麻叶用清水泡2个小时', '1788');
INSERT INTO `cookbook_marche` VALUES ('2152', '2', 'https://s1.st.meishij.net/rs/236/94/8898736/n8898736_148281060422102.jpg', '葱花用一勺盐，一勺鸡精，一勺色拉油拌匀备用', '1788');
INSERT INTO `cookbook_marche` VALUES ('2153', '3', 'https://s1.st.meishij.net/rs/236/94/8898736/n8898736_148281060994781.jpg', '自己压的面条，可多洒一点面粉在上面，吃起来粘粘的比较好吃', '1788');
INSERT INTO `cookbook_marche` VALUES ('2154', '1', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_146935062594649.jpg', ' 准备好的原料：猪肉，葱姜，豆角，面条等。', '1789');
INSERT INTO `cookbook_marche` VALUES ('2155', '2', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_146935066442469.jpg', ' 将新鲜的湿面条拽成小段，抖散。蒸锅水烧开后，放入铺了笼布的蒸架，把面条均匀的放在蒸架上，盖上锅盖蒸8分钟。', '1789');
INSERT INTO `cookbook_marche` VALUES ('2156', '3', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_146935063825200.jpg', '趁热抖散。', '1789');
INSERT INTO `cookbook_marche` VALUES ('2157', '4', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_146935065298077.jpg', '蒸面条的同时用另一灶炒菜。把肥肉倒入炒锅内，翻炒煸出油。', '1789');
INSERT INTO `cookbook_marche` VALUES ('2158', '5', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_146935067278251.jpg', '放入红辣椒段、姜煸出味，放入葱段翻下。', '1789');
INSERT INTO `cookbook_marche` VALUES ('2159', '6', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_146935068541505.jpg', '放入豆角翻炒。', '1789');
INSERT INTO `cookbook_marche` VALUES ('2160', '7', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_146935204020325.jpg', '翻炒至豆角变色后加入适量开水（水量也是感觉面条可以吸收多少放，一般一斤面条一斤水左右），中小火炖会。', '1789');
INSERT INTO `cookbook_marche` VALUES ('2161', '8', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_146935141816834.jpg', '放老抽、盐、十三香调味（咸度可以尝下汤汁，感觉汤汁稍微有点咸就可以了。）', '1789');
INSERT INTO `cookbook_marche` VALUES ('2162', '1', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_147619833630296.jpg', '粉条提前泡发控水备用，韭菜、葱、姜、藿香、十香菜洗净控水备用。', '1790');
INSERT INTO `cookbook_marche` VALUES ('2163', '2', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_147619834087020.jpg', '把菜全部切碎放盆里。', '1790');
INSERT INTO `cookbook_marche` VALUES ('2164', '3', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_147619834536409.jpg', '倒入香油拌匀(锁水)，再放盐、十三香拌匀调味备用。', '1790');
INSERT INTO `cookbook_marche` VALUES ('2165', '4', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_147619835174559.jpg', ' 面粉倒盆里，放一点点盐，边倒水边用筷子搅成无干粉的面絮，用手和成三光面团，盖盖醒半小时左右，把面团揉光滑。(面粉：水=100：55左右，耳垂软硬度。)（我准备的面团多，只用一小部分就够了。）', '1790');
INSERT INTO `cookbook_marche` VALUES ('2166', '5', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_147619836170161.jpg', '取一部分面团搓长长条，拽成小剂，分别揉圆备用。(一般一个面剂50克左右就可以，我这个是40克，擀的很薄。)', '1790');
INSERT INTO `cookbook_marche` VALUES ('2167', '6', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_147619836468183.jpg', ' 先取1个擀皮，其他的用湿布盖着防干皮。用小擀面杖擀成大小合适的圆形。', '1790');
INSERT INTO `cookbook_marche` VALUES ('2168', '7', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_147619836850520.jpg', '面皮放锅拍上。', '1790');
INSERT INTO `cookbook_marche` VALUES ('2169', '8', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_147619837290046.jpg', '取适量调好的馅，用筷子扒平。', '1790');
INSERT INTO `cookbook_marche` VALUES ('2170', '9', 'https://s1.st.meishij.net/rs/04/195/5236254/n5236254_147619837851148.jpg', ' 取一个鸡蛋磕个小口，均匀甩在菜上。(也可把鸡蛋磕碗里用筷子搅匀再均匀倒菜上。这样的蛋液少油清淡更营养。)(还可把蛋液炒熟冷凉切碎拌馅中，那样香些，但我喜欢少油清淡的。)', '1790');
INSERT INTO `cookbook_marche` VALUES ('2171', '1', 'https://s1.st.meishij.net/rs/104/40/6947604/n6947604_147489121723710.jpg', '首先把面粉放入盆中，加入适量的水，揉成面团，盖上保鲜膜，醒20分钟。把面团放在案板上，用擀面杖擀成薄厚均匀的面饼。把擀好的面饼折叠好，折叠一层放一次面粉，防止面粘连在一起。用刀切成均匀面条。然后把面条用手抖弄开。撒上一些面粉，防止面条粘在一起。', '1791');
INSERT INTO `cookbook_marche` VALUES ('2172', '2', 'https://s1.st.meishij.net/rs/104/40/6947604/n6947604_147489124134122.jpg', '把长豆角洗干净，切成均匀的段，放在盘中。猪廋肉放在冰箱的冷冻层中冻半个小时后（半冻状态的肉比较好切），拿出切成丝儿，装入盘中待用。葱、姜切好放在小碟子中待用。锅中放入适量的花生油，起火，油温度五成热的时候，加入一勺花椒粉，切好的肉丝放入锅中翻炒。再把切好的葱、姜放入锅中翻炒。直到肉丝炒至变色为止。', '1791');
INSERT INTO `cookbook_marche` VALUES ('2173', '1', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_146103928051567.jpg', '槐花洗净 放在筛子里控一下水份 20-30分钟就可以了', '1792');
INSERT INTO `cookbook_marche` VALUES ('2174', '2', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_146103928524470.jpg', '控好水份的槐花倒入大一点的盆子里放入少许植物油抓匀防粘  放入面粉和玉米淀粉', '1792');
INSERT INTO `cookbook_marche` VALUES ('2175', '3', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_146103928733660.jpg', '轻轻抓匀 蒸屉铺屉布，均匀铺入拌好面粉的槐花 盖盖大火烧开 ，转中火蒸6分钟', '1792');
INSERT INTO `cookbook_marche` VALUES ('2176', '4', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_146103929175182.jpg', '蒸槐花的时候把蒜泥做好', '1792');
INSERT INTO `cookbook_marche` VALUES ('2177', '1', 'https://s1.st.meishij.net/rs/169/156/2789169/n2789169_145758661164734.jpg', '水加入面粉里', '1793');
INSERT INTO `cookbook_marche` VALUES ('2178', '2', 'https://s1.st.meishij.net/rs/169/156/2789169/n2789169_145758663367993.jpg', '用筷子搅成絮状', '1793');
INSERT INTO `cookbook_marche` VALUES ('2179', '3', 'https://s1.st.meishij.net/rs/169/156/2789169/n2789169_145758666760369.jpg', '整理成面团，达到三光状态，盖是纱布醒发一会，不醒发也是可以的，我们烙这个饼，没有那么多讲究，就是冷水和面，活好就可以烙', '1793');
INSERT INTO `cookbook_marche` VALUES ('2180', '4', 'https://s1.st.meishij.net/rs/169/156/2789169/n2789169_145758678204313.jpg', '我直接用了腌制的牛排，比较省事，不需要再用别的调料了，切成条状', '1793');
INSERT INTO `cookbook_marche` VALUES ('2181', '5', 'https://s1.st.meishij.net/rs/169/156/2789169/n2789169_145758682449434.jpg', '炒锅放油', '1793');
INSERT INTO `cookbook_marche` VALUES ('2182', '6', 'https://s1.st.meishij.net/rs/169/156/2789169/n2789169_145758677681455.jpg', '放入牛肉条炒熟，变色后再略微炒几秒，不要炒老了，不然口感会硬', '1793');
INSERT INTO `cookbook_marche` VALUES ('2183', '7', 'https://s1.st.meishij.net/rs/169/156/2789169/n2789169_145758679734007.jpg', '香菇切片，韭菜切段，葱姜切碎，黄豆芽洗净', '1793');
INSERT INTO `cookbook_marche` VALUES ('2184', '8', 'https://s1.st.meishij.net/rs/169/156/2789169/n2789169_145758701558971.jpg', '炒锅放油，爆香香葱', '1793');
INSERT INTO `cookbook_marche` VALUES ('2185', '9', 'https://s1.st.meishij.net/rs/169/156/2789169/n2789169_145758693804088.jpg', '加入黄豆芽和香菇，翻炒一会加入蚝油调味', '1793');
INSERT INTO `cookbook_marche` VALUES ('2186', '10', 'https://s1.st.meishij.net/rs/169/156/2789169/n2789169_145758688898265.jpg', '放入韭菜', '1793');
INSERT INTO `cookbook_marche` VALUES ('2187', '11', 'https://s1.st.meishij.net/rs/169/156/2789169/n2789169_145758703274488.jpg', '再略微炒几下就好了', '1793');
INSERT INTO `cookbook_marche` VALUES ('2188', '1', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_143721097622855.jpg', '面粉放入盆中，缓慢加入开水，用筷子搅拌', '1794');
INSERT INTO `cookbook_marche` VALUES ('2189', '2', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_143721097969636.jpg', '稍微晾一下，用手和成软一些的面团', '1794');
INSERT INTO `cookbook_marche` VALUES ('2190', '3', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_143721098186989.jpg', '盖保鲜膜醒一会儿，再次揉匀', '1794');
INSERT INTO `cookbook_marche` VALUES ('2191', '4', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_143721098494732.jpg', '分成4份，整成这样的椭圆形', '1794');
INSERT INTO `cookbook_marche` VALUES ('2192', '5', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_143721098698851.jpg', '案板上抹一些油防粘，取一份面团，把中间捏细', '1794');
INSERT INTO `cookbook_marche` VALUES ('2193', '6', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_143721099060614.jpg', '然后按扁，两面都抹上油', '1794');
INSERT INTO `cookbook_marche` VALUES ('2194', '7', 'https://s1.st.meishij.net/rs/52/75/2268802/n2268802_143721103592020.jpg', '合在一起，擀成薄一些的圆饼', '1794');
INSERT INTO `cookbook_marche` VALUES ('2195', '1', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718404830683.jpg', '核桃仁炒熟', '1795');
INSERT INTO `cookbook_marche` VALUES ('2196', '1.5', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718405148968.jpg', '芝麻炒熟，我用了黑白两种芝麻', '1795');
INSERT INTO `cookbook_marche` VALUES ('2197', '2.5', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718411635233.jpg', '花生米芝麻核桃仁全部炒熟晾凉', '1795');
INSERT INTO `cookbook_marche` VALUES ('2198', '3', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718412088658.jpg', '用料理机的绞肉杯把花生米和核桃仁打成颗粒状，芝麻则用研磨杯打碎，也可以全部用绞肉杯打成颗粒状', '1795');
INSERT INTO `cookbook_marche` VALUES ('2199', '4', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718417449842.jpg', '面粉用屉布包上蒸25分钟，然后晾凉擀碎过筛备用', '1795');
INSERT INTO `cookbook_marche` VALUES ('2200', '4.5', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_143718418089192.jpg', '锅中倒入少量的橄榄油', '1795');
INSERT INTO `cookbook_marche` VALUES ('2201', '1', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143711717514642.jpg', '面粉加200克水、2克盐、1克食用碱揉成光滑的面团', '1796');
INSERT INTO `cookbook_marche` VALUES ('2202', '2', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143711717599747.jpg', '醒15分钟后再次揉面，分成小块后擀开', '1796');
INSERT INTO `cookbook_marche` VALUES ('2203', '3', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143711717653262.jpg', '抹油，醒2小时', '1796');
INSERT INTO `cookbook_marche` VALUES ('2204', '4', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143711717749355.jpg', '羊肉切块', '1796');
INSERT INTO `cookbook_marche` VALUES ('2205', '5', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143711717719523.jpg', '羊肉焯水，水里加料酒去腥', '1796');
INSERT INTO `cookbook_marche` VALUES ('2206', '6', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143711717880643.jpg', '焯出羊肉的血沫', '1796');
INSERT INTO `cookbook_marche` VALUES ('2207', '7', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143711718324013.jpg', '压力锅里放入姜、葱、花椒、枸杞、热水，放入羊肉煮熟', '1796');
INSERT INTO `cookbook_marche` VALUES ('2208', '8', 'https://s1.st.meishij.net/rs/23/159/2414773/n2414773_143711718483279.jpg', '羊肉炖好后，取适量炖羊肉的汤，加少许生抽、盐，煮开', '1796');
INSERT INTO `cookbook_marche` VALUES ('2209', '1', 'https://s1.st.meishij.net/rs/67/36/4071567/n4071567_143711001335498.jpg', '准备好相关食材', '1797');
INSERT INTO `cookbook_marche` VALUES ('2210', '2', 'https://s1.st.meishij.net/rs/67/36/4071567/n4071567_143711001638865.jpg', '将韭菜叶与根茎分开', '1797');
INSERT INTO `cookbook_marche` VALUES ('2211', '3', 'https://s1.st.meishij.net/rs/67/36/4071567/n4071567_143711001937768.jpg', '韭菜叶切碎，根茎一切两段', '1797');
INSERT INTO `cookbook_marche` VALUES ('2212', '4', 'https://s1.st.meishij.net/rs/67/36/4071567/n4071567_143711002117158.jpg', '小盆中加入：面粉、韭菜碎、鸡蛋和少许食盐', '1797');
INSERT INTO `cookbook_marche` VALUES ('2213', '5', 'https://s1.st.meishij.net/rs/67/36/4071567/n4071567_143711002448197.jpg', '用筷子搅拌均匀', '1797');
INSERT INTO `cookbook_marche` VALUES ('2214', '6', 'https://s1.st.meishij.net/rs/67/36/4071567/n4071567_143711002791277.jpg', '然后用手和面，此时面团较硬，饧面片刻', '1797');
INSERT INTO `cookbook_marche` VALUES ('2215', '7', 'https://s1.st.meishij.net/rs/67/36/4071567/n4071567_143711003034572.jpg', '韭菜根茎：加入少许食盐、香油和五香粉，拌均待用', '1797');
INSERT INTO `cookbook_marche` VALUES ('2216', '1', 'https://s1.st.meishij.net/rs/246/42/3260746/n3260746_142563379399194.jpg', '准备好所需食材', '1798');
INSERT INTO `cookbook_marche` VALUES ('2217', '2', 'https://s1.st.meishij.net/rs/246/42/3260746/n3260746_142563377900964.jpg', '将红薯粉条放到凉水中浸泡至变软', '1798');
INSERT INTO `cookbook_marche` VALUES ('2218', '3', 'https://s1.st.meishij.net/rs/246/42/3260746/n3260746_142563382175949.jpg', '红薯淀粉加入水（或高汤）调成淀粉汁', '1798');
INSERT INTO `cookbook_marche` VALUES ('2219', '4', 'https://s1.st.meishij.net/rs/246/42/3260746/n3260746_142563385957514.jpg', '姜蒜切末，大葱切成小薄片', '1798');
INSERT INTO `cookbook_marche` VALUES ('2220', '5', 'https://s1.st.meishij.net/rs/246/42/3260746/n3260746_142563385139910.jpg', '锅里加水煮沸，放入泡好的粉条煮5分钟左右（粉条煮软就可以了，不需要煮太烂。）', '1798');
INSERT INTO `cookbook_marche` VALUES ('2221', '1', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_76947.jpg', '有时普通面粉和出来的面不够筋道，那就不能只加盐，要加一点食用碱，一般500克左右的面粉加1克的就可以了，食用碱要先用温水化开', '1799');
INSERT INTO `cookbook_marche` VALUES ('2222', '1.5', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_39082.jpg', '把面粉、食盐、水同时放入面包机', '1799');
INSERT INTO `cookbook_marche` VALUES ('2223', '2', 'https://s1.st.meishij.net/rs/114/34/1258614/n1258614_18174.jpg', '用一个和面程序和成光滑的面团，手工揉面也是可以的，揉到表面光滑后饧30分钟就可以了', '1799');
INSERT INTO `cookbook_marche` VALUES ('2224', '1', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_148974903512133.jpg', '备好全部食材\r\n\r\n\r\n', '1870');
INSERT INTO `cookbook_marche` VALUES ('2225', '2', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_148974903695449.jpg', '春笋去老根和壳洗净后切成滚刀块', '1870');
INSERT INTO `cookbook_marche` VALUES ('2226', '3', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_148974903815026.jpg', '先把咸肉，金华火腿和小排骨冷水下锅，焯水后捞起冲洗干净', '1870');
INSERT INTO `cookbook_marche` VALUES ('2227', '4', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_148974903913768.jpg', '准备生姜片和料酒', '1870');
INSERT INTO `cookbook_marche` VALUES ('2228', '5', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_148974904169784.jpg', '汤锅内加入热水，再把咸肉和猪小排骨，金华火腿片，生姜片加入', '1870');
INSERT INTO `cookbook_marche` VALUES ('2229', '6', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_148974904365092.jpg', '倒入料酒', '1870');
INSERT INTO `cookbook_marche` VALUES ('2230', '7', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_148974904419893.jpg', '盖上大火烧', '1870');
INSERT INTO `cookbook_marche` VALUES ('2231', '1', 'https://s1.st.meishij.net/rs/69/207/2801819/n2801819_148231649211209.jpg', '紫糯米提前用清水浸泡2小时。这样米更粘糯。', '1871');
INSERT INTO `cookbook_marche` VALUES ('2232', '2', 'https://s1.st.meishij.net/rs/69/207/2801819/n2801819_148231657474811.jpg', '浸泡好的紫糯米投洗干净，倒入比米量略少一些的清水，煮糯米饭水要比平时煮饭的水量少一些，这样米才会颗粒分明，有劲道感。装入美的AH煲内。', '1871');
INSERT INTO `cookbook_marche` VALUES ('2233', '3', 'https://s1.st.meishij.net/rs/69/207/2801819/n2801819_148231662007831.jpg', '按下快煮键，此时你就不用管了，坐等。', '1871');
INSERT INTO `cookbook_marche` VALUES ('2234', '4', 'https://s1.st.meishij.net/rs/69/207/2801819/n2801819_148231670109658.jpg', '全程只需20分钟，程序结束会有提示音。打开看一下糯米饭即粘糯且粒粒分明，而且用麦饭石内胆的AH煲煮的饭香气四溢。', '1871');
INSERT INTO `cookbook_marche` VALUES ('2235', '5', 'https://s1.st.meishij.net/rs/69/207/2801819/n2801819_148231670337595.jpg', '糯米饭煮好，底部铺上卷帘和保鲜膜，撒上少许熟白芝麻，吧煮好的糯米饭铺成长方形，压压实。', '1871');
INSERT INTO `cookbook_marche` VALUES ('2236', '6', 'https://s1.st.meishij.net/rs/69/207/2801819/n2801819_148231674326419.jpg', '撒上一层肉松。', '1871');
INSERT INTO `cookbook_marche` VALUES ('2237', '1', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146171525017515.jpg', '春笋焯水', '1872');
INSERT INTO `cookbook_marche` VALUES ('2238', '2', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146171522188768.jpg', '把春笋切成小丁', '1872');
INSERT INTO `cookbook_marche` VALUES ('2239', '3', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146171522624563.jpg', '浓汤宝也切成小丁，如果有皮冻最好，我家没有就用了浓汤宝', '1872');
INSERT INTO `cookbook_marche` VALUES ('2240', '4', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146171523233637.jpg', '取一大盆放入猪肉泥和笋丁', '1872');
INSERT INTO `cookbook_marche` VALUES ('2241', '5', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146171523554005.jpg', '把浓汤宝丁也放入', '1872');
INSERT INTO `cookbook_marche` VALUES ('2242', '6', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146171524026500.jpg', '加入料酒、姜汁、盐、砂糖、生抽和少许清水', '1872');
INSERT INTO `cookbook_marche` VALUES ('2243', '7', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146171524365490.jpg', '搅拌均匀备用', '1872');
INSERT INTO `cookbook_marche` VALUES ('2244', '8', 'https://s1.st.meishij.net/rs/238/152/2913238/n2913238_146171525165276.jpg', '盆中加入中粉和适量清水', '1872');
INSERT INTO `cookbook_marche` VALUES ('2245', '1', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_146150457413509.jpg', '准备猪肉末和春笋', '1873');
INSERT INTO `cookbook_marche` VALUES ('2246', '2', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_146150457565196.jpg', '把笋去壳，去老根后洗净切开', '1873');
INSERT INTO `cookbook_marche` VALUES ('2247', '3', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_146150457617698.jpg', '冷水下锅加入2克盐煮开后捞起', '1873');
INSERT INTO `cookbook_marche` VALUES ('2248', '4', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_146150457887139.jpg', '剁成细末', '1873');
INSERT INTO `cookbook_marche` VALUES ('2249', '5', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_146150458005742.jpg', '猪肉里面加入鸡蛋，黄酒，盐，蚝油，鲜酱油按顺时针方向搅拌有粘性后再加入少许清水再一次搅拌有粘性', '1873');
INSERT INTO `cookbook_marche` VALUES ('2250', '6', 'https://s1.st.meishij.net/rs/115/102/588115/n588115_146150458184364.jpg', '把笋加入搅拌均匀', '1873');
